    <script>
        // Initialize Three.js scene and Plotly charts
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({
            canvas: document.getElementById('globe-container'),
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);
        camera.position.z = 5;
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        const earthGeometry = new THREE.SphereGeometry(1, 60, 60);
        const earthMaterial = new THREE.MeshPhongMaterial({
            color: 0x3399ff,
            specular: 0xffffff,
            shininess: 100
        });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        scene.add(earth);
        const light = new THREE.DirectionalLight(0xffffff, 1);
        light.position.set(0, 0, 5);
        scene.add(light);
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        const canvas = document.createElement('canvas');
        canvas.width = 256;
        canvas.height = 256;
        const ctx = canvas.getContext('2d');
        ctx.fillStyle = 'blue';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = 'green';
        ctx.beginPath();
        ctx.arc(canvas.width / 2, canvas.height / 2, canvas.width / 2, 0, Math.PI * 2);
        ctx.fill();
        const texture = new THREE.CanvasTexture(canvas);
        earth.material.map = texture;
        const selectedLatitudeLine = new THREE.Line(
            new THREE.CircleGeometry(1, 32).getPoints(),
            new THREE.LineDashedMaterial({
                color: 0x0000ff,
                pattern: [10, 10]
            })
        );
        selectedLatitudeLine.position.y = 0;
        scene.add(selectedLatitudeLine);
        const surfaceNormalVector = new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 1, 0x000000);
        scene.add(surfaceNormalVector);
        const sunVector = new THREE.ArrowHelper(new THREE.Vector3(0, 0, -1), new THREE.Vector3(0, 0, 0), 1, 0xff0000);
        scene.add(sunVector);
        const polarAxis = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 1, 0x000000);
        scene.add(polarAxis);
        const plotInstant = document.getElementById('plot-instant');
        const plotCumulative = document.getElementById('plot-cumulative');
        const plotLegend = document.getElementById('plot-legend');
        let season = 0;
        let latitude = 51.4789;
        let time = 12;
        function update() {
            const δ = 23.5 * Math.cos(2 * Math.PI * season);
            const δ_rad = δ * Math.PI / 180;
            const HRA = 15 * (time - 12);
            const HRA_rad = HRA * Math.PI / 180;
            const solarDeclination = new THREE.Vector3(0, Math.sin(δ_rad), -Math.cos(δ_rad));
            light.position.copy(solarDeclination);
            selectedLatitudeLine.position.y = latitude;
            surfaceNormalVector.setDirection(new THREE.Vector3(0, 1, 0));
            sunVector.setDirection(solarDeclination);
            polarAxis.setDirection(new THREE.Vector3(0, 0, 1));
            const energyEquator = [];
            const energyLatitude = [];
            const energyNorthPole = [];
            for (let hour = 0; hour < 24; hour++) {
                const energy = 1361 * Math.max(0, Math.sin(latitude * Math.PI / 180) * Math.sin(δ_rad) + Math.cos(latitude * Math.PI / 180) * Math.cos(δ_rad) * Math.cos(HRA_rad));
                energyEquator.push(energy);
                energyLatitude.push(energy);
                energyNorthPole.push(energy);
            }
            const data = {
                x: Array.from({ length: 24 }, (_, i) => i),
                y: [energyEquator, energyLatitude, energyNorthPole]
            };
            Plotly.newPlot(plotInstant, [{
                x: data.x,
                y: data.y[0],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'red'
                }
            }, {
                x: data.x,
                y: data.y[1],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'black'
                }
            }, {
                x: data.x,
                y: data.y[2],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'blue'
                }
            }, {
                x: [time],
                y: [energyLatitude[time]],
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: 'black'
                }
            }], {
                title: 'energy',
                xaxis: {
                    title: 'time (hr)'
                },
                yaxis: {
                    title: 'watts/m^2',
                    range: [0, 1400]
                }
            });
            const cumulativeEnergyEquator = [];
            const cumulativeEnergyLatitude = [];
            const cumulativeEnergyNorthPole = [];
            for (let hour = 0; hour < 24; hour++) {
                let cumulativeEnergy = 0;
                for (let i = 0; i < hour; i++) {
                    cumulativeEnergy += 1361 * Math.max(0, Math.sin(latitude * Math.PI / 180) * Math.sin(δ_rad) + Math.cos(latitude * Math.PI / 180) * Math.cos(δ_rad) * Math.cos((15 * (i - 12)) * Math.PI / 180));
                }
                cumulativeEnergyEquator.push(cumulativeEnergy);
                cumulativeEnergyLatitude.push(cumulativeEnergy);
                cumulativeEnergyNorthPole.push(cumulativeEnergy);
            }
            const cumulativeData = {
                x: Array.from({ length: 24 }, (_, i) => i),
                y: [cumulativeEnergyEquator, cumulativeEnergyLatitude, cumulativeEnergyNorthPole]
            };
            Plotly.newPlot(plotCumulative, [{
                x: cumulativeData.x,
                y: cumulativeData.y[0],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'red'
                }
            }, {
                x: cumulativeData.x,
                y: cumulativeData.y[1],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'black'
                }
            }, {
                x: cumulativeData.x,
                y: cumulativeData.y[2],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'blue'
                }
            }], {
                title: 'energy',
                xaxis: {
                    title: 'time (hr)'
                },
                yaxis: {
                    title: 'watts/m^2',
                    range: [0, 14000]
                }
            });
            const legendItems = [];
            for (let i = 0; i < 3; i++) {
                const item = document.createElement('div');
                item.classList.add('legend-item');
                const span = document.createElement('span');
                span.style.backgroundColor = i === 0 ? 'red' : i === 1 ? 'black' : 'blue';
                item.appendChild(span);
                const text = document.createElement('span');
                text.textContent = i === 0 ? 'Equator' : i === 1 ? 'Latitude' : 'North Pole';
                item.appendChild(text);
                plotLegend.appendChild(item);
            }
            document.getElementById('value-season').textContent = season.toFixed(3);
            document.getElementById('value-latitude').textContent = latitude.toFixed(2);
            document.getElementById('value-time').textContent = time.toFixed(2);
            renderer.render(scene, camera);
        }
        update();
        const sliders = document.querySelectorAll('.slider-group input[type="range"]');
        sliders.forEach((slider) => {
            slider.addEventListener('input', () => {
                season = parseFloat(document.getElementById('slider-season').value);
                latitude = parseFloat(document.getElementById('slider-latitude').value);
                time = parseFloat(document.getElementById('slider-time').value);
                update();
            });
        });
    </script>
</body>
