            width: 50%;
            margin-right: 10px;
        }

        #input-area {
            width: 30%;
            margin-right: 10px;
        }

        #canvas-container {
            border: 1px solid #ccc;
            padding: 10px;
            width: 80%;
            height: 600px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <span>Area:</span>
            <input type="range" id="slider-area" value="13" min="1" max="101" step="1">
            <input type="number" id="input-area" value="13" min="1" max="101" step="1">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let congruentData = {
          5: { a: {n: 3, d: 2}, b: {n: 20, d: 3}, c: {n: 41, d: 6} },
          6: { a: {n: 3, d: 1}, b: {n: 4, d: 1}, c: {n: 5, d: 1} },
          7: { a: {n: 35, d: 12}, b: {n: 24, d: 5}, c: {n: 337, d: 60} },
          13: { a: {n: 780, d: 323}, b: {n: 323, d: 30}, c: {n: 106921, d: 9690} },
          79: { a: {n: 233126551, d: 167973000}, b: {n: 335946000, d: 2950969}, c: {n: 56434050774922081, d: 495683115837000} }
        };

        let currentArea = 13;

        function drawFraction(fractionObject, x, y, rotation, fontSize) {
          let numStr = fractionObject.n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
          let denomStr = fractionObject.d.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");

          push();
          translate(x, y);
          rotate(rotation);
          textSize(fontSize);
          textAlign(CENTER, CENTER);
          fill(147, 90, 43); // Brown
          text(numStr, 0, 0);
          text(denomStr, 0, -fontSize - 5);
          pop();

          let lineLength = max(textWidth(numStr), textWidth(denomStr));
          line(0, 0, 0, -fontSize - 5);
        }

        function setup() {
          createCanvas(800, 600);
          background(220);
          frameRate(30);
        }

        function draw() {
          background(220);
          let area = document.getElementById("slider-area").value || currentArea;

          if (area in congruentData) {
            let data = congruentData[area];
            let a = data.a;
            let b = data.b;
            let c = data.c;
            let scale = min((width - 20) / a.n, (height - 20) / b.n);
            let x = width / 2;
            let y = height / 2;
            let aX = x - (a.n * scale) / 2;
            let aY = y;
            let bX = x;
            let bY = y + (b.n * scale) / 2;
            let cX = x + (c.n * scale) / 2;
            let cY = y + (c.n * scale) / 2;

            // Draw the triangle
            noStroke();
            fill(255, 242, 229); // Light beige
            beginShape();
            vertex(aX, aY);
            vertex(bX, bY);
            vertex(cX, cY);
            endShape(CLOSE);

            // Draw the labels
            stroke(0);
            strokeWeight(2);
            line(aX, aY, bX, bY);
            drawFraction(a, aX, aY, 0, 18);
            drawFraction(b, bX, bY, 0, 18);
            let angle = atan2(cY - bY, cX - bX);
            push();
            translate(bX, bY);
            rotate(angle);
            drawFraction(c, 0, 0, angle, 18);
            pop();
          } else {
            // Draw the "Not Congruent" message
            fill(255, 0, 0);
            textSize(20);
            textAlign(CENTER, CENTER);
            text(area + " is not a congruent number", width / 2, height / 2);
          }
        }

        function mousePressed() {
          currentArea = document.getElementById("input-area").value || currentArea;
          draw();
        }

        function windowResized() {
          resizeCanvas(windowWidth, windowHeight);
          draw();
        }

        setup();
        draw();
    </script>
</body>
</html>
