            </div>
            <div class="problem-column">
                <p>dual problem: maximize volume Q given costs C</p>
                <div class="control-item">
                    <label for="slider-C">C</label>
                    <input type="range" id="slider-C" min="1" max="10" step="0.1" value="4.0">
                    <span id="label-C">4</span>
                </div>
                <button id="btn-C-from-primal">C from primal</button>
            </div>
        </div>

        <!-- 3. Visualization Area -->
        <div id="visualization-area">
            <div id="plot-primal" class="plot-container"></div>
            <div id="plot-dual" class="plot-container"></div>
        </div>

        <!-- 4. Output Display -->
        <div id="output-area">
            <div id="output-primal" class="output-box"></div>
            <div id="output-dual" class="output-box"></div>
        </div>

        <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
        <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
        <script src="https://cdn.jsdelivr.net/npm/mathjs@9.5.1/dist/math.min.js"></script>

        <script>
            let alpha = document.getElementById('slider-alpha');
            let beta = document.getElementById('slider-beta');
            let w = document.getElementById('slider-w');
            let r = document.getElementById('slider-r');
            let Q = document.getElementById('slider-Q');
            let C = document.getElementById('slider-C');
            let btnReset = document.getElementById('btn-reset');
            let btnQFromDual = document.getElementById('btn-Q-from-dual');
            let btnCFromPrimal = document.getElementById('btn-C-from-primal');
            let labelAlpha = document.getElementById('label-alpha');
            let labelBeta = document.getElementById('label-beta');
            let labelW = document.getElementById('label-w');
            let labelR = document.getElementById('label-r');
            let labelQ = document.getElementById('label-Q');
            let labelC = document.getElementById('label-C');
            let outputPrimal = document.getElementById('output-primal');
            let outputDual = document.getElementById('output-dual');

            let camera, scene, renderer, controls;

            function update() {
                alpha.value = parseFloat(alpha.value);
                beta.value = parseFloat(beta.value);
                w.value = parseFloat(w.value);
                r.value = parseFloat(r.value);
                Q.value = parseFloat(Q.value);
                C.value = parseFloat(C.value);

                let x_p, y_p, C_out, x_d, y_d, Q_out;

                // Primal Problem Solver
                x_p = Math.pow(Q.value / (Math.pow(w.value * beta.value, 1 / (alpha.value + beta.value))), 1 / alpha.value);
                y_p = x_p * Math.pow(w.value * beta.value / (r.value * alpha.value), 1 / alpha.value);
                C_out = w.value * x_p + r.value * y_p;

                // Dual Problem Solver
                x_d = (C.value * alpha.value) / (w.value * (alpha.value + beta.value));
                y_d = (C.value * beta.value) / (r.value * (alpha.value + beta.value));
                Q_out = Math.pow(x_d, alpha.value) * Math.pow(y_d, beta.value);

                outputPrimal.textContent = `x = ${x_p.toFixed(2)}, y = ${y_p.toFixed(2)}, C = ${C_out.toFixed(2)}`;
                outputDual.textContent = `x = ${x_d.toFixed(2)}, y = ${y_d.toFixed(2)}, Q = ${Q_out.toFixed(2)}`;

                // Update plot
                let plotPrimal = document.getElementById('plot-primal');
                let plotDual = document.getElementById('plot-dual');

                // Primal Plot
                let geometry = new THREE.BufferGeometry();
                let vertices = new Float32Array([
                    -1, 0, 0,
                    1, 0, 0,
                    0, 0, 0
                ]);
                geometry.setAttribute('position', new THREE.BufferAttribute(vertices, 3));
                let material = new THREE.MeshBasicMaterial({ color: 0xffa07a });
                let mesh = new THREE.Mesh(geometry, material);
                plotPrimal.innerHTML = '';
                plotPrimal.appendChild(mesh);

                // Dual Plot
                let geometryDual = new THREE.ParametricGeometry((u, v) => {
                    let x = Math.pow(u, alpha.value) * Math.pow(v, beta.value);
                    let y = 0;
                    let z = 0;
                    return new THREE.Vector3(x, y, z);
                }, 100, 100);
                let materialDual = new THREE.MeshBasicMaterial({ color: 0xffa07a });
                let meshDual = new THREE.Mesh(geometryDual, materialDual);
                plotDual.innerHTML = '';
                plotDual.appendChild(meshDual);
            }

            alpha.addEventListener('input', update);
            beta.addEventListener('input', update);
            w.addEventListener('input', update);
            r.addEventListener('input', update);
            Q.addEventListener('input', update);
            C.addEventListener('input', update);
            btnReset.addEventListener('click', () => {
                alpha.value = 0.5;
                beta.value = 0.5;
                w.value = 1.0;
                r.value = 1.0;
                Q.value = 3.0;
                C.value = 4.0;
                update();
            });
            btnQFromDual.addEventListener('click', () => {
                Q.value = parseFloat(outputDual.textContent.split(', ')[2]);
                update();
            });
            btnCFromPrimal.addEventListener('click', () => {
                C.value = parseFloat(outputPrimal.textContent.split(', ')[2]);
                update();
            });

            update();

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            scene = new THREE.Scene();
            renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('plot-primal'),
                antialias: true
            });
            controls = new THREE.OrbitControls(camera, renderer.domElement);

            camera.position.z = 5;
            controls.update();

            function animate() {
                requestAnimationFrame(animate);
                controls.update();
                renderer.render(scene, camera);
            }
            animate();
        </script>
    </div>
</body>
</html>
