</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <!-- Flash Pressure Slider -->
            <div class="control-row">
                <label for="slider-pressure">flash pressure</label>
                <input type="range" id="slider-pressure" value="180" min="100" max="1000" step="1">
                <span id="pressure-value">180 kPa</span>
            </div>
            <!-- Heat Load Slider -->
            <div class="control-row">
                <label for="slider-heat">heat load</label>
                <input type="range" id="slider-heat" value="0" min="0" max="10000" step="100">
                <span id="heat-value">0 kJ/hr</span>
            </div>
        </div>

        <!-- Section 2: Visualization Area -->
        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>
    </div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <!-- JavaScript code -->
    <script>
        // Global variables to store calculation results
        let T_flash = 0;
        let V_frac = 0;
        let x = [0, 0, 0, 0];
        let y = [0, 0, 0, 0];
        let Q_hr = 0;

        // Constants for thermodynamic properties
        const F = 10; // Feed flow in kmol/hr
        const T_F = 323.2; // Feed temperature in K
        const P_F = 1000; // Feed pressure in kPa
        const z = [0.25, 0.25, 0.25, 0.25]; // Feed mole fractions
        const Cp_L = [132, 161, 225, 255]; // Liquid heat capacities in kJ/kmol·K
        const ΔH_vap = [22400, 25800, 31800, 34400]; // Vapor enthalpies of vaporization in kJ/kmol

        // Antoine constants for saturation pressures
        const A = [6.80165, 6.86177, 6.90237, 6.92383];
        const B = [1005.157, 1184.249, 1358.59, 1431.149];
        const C = [-27.159, -35.223, -51.303, -59.501];

        // Function to calculate saturation pressure using Antoine's equation
        function P_sat(i, T) {
            return 10 ** (A[i] - B[i] / (T + C[i]));
        }

        // Function to calculate K-values (vapor-liquid equilibrium ratios)
        function K(i, T) {
            return P_sat(i, T) / 100;
        }

        // Function to calculate Rachford-Rice equation
        function RachfordRice(V, T) {
            let sum = 0;
            for (let i = 0; i < 4; i++) {
                sum += z[i] * (K(i, T) - 1) / (1 + V * (K(i, T) - 1));
            }
            return sum;
        }

        // Function to calculate energy balance
        function energyBalance(V, T) {
            let sum = 0;
            for (let i = 0; i < 4; i++) {
                sum += z[i] * Cp_L[i] * (T_F - T);
            }
            return Q_hr / F + sum - V * sum;
        }

        // Function to solve system of equations using bisection method
        function solveSystem() {
            let T_min = 273.15;
            let T_max = 500;
            let tol = 0.0001;
            let iter = 0;
            while (Math.abs(T_max - T_min) > tol && iter < 100) {
                let T_mid = (T_min + T_max) / 2;
                let V_mid = 0;
                let RR_mid = RachfordRice(V_mid, T_mid);
                let EB_mid = energyBalance(V_mid, T_mid);
                if (RR_mid * EB_mid < 0) {
                    T_min = T_mid;
                } else {
                    T_max = T_mid;
                }
                iter++;
            }
            if (iter < 100) {
                T_flash = (T_min + T_max) / 2;
                V_frac = 0;
                for (let i = 0; i < 4; i++) {
                    V_frac += z[i] * (K(i, T_flash) - 1) / (1 + V_frac * (K(i, T_flash) - 1));
                }
                for (let i = 0; i < 4; i++) {
                    x[i] = z[i] * (1 + V_frac * (K(i, T_flash) - 1)) / (1 + V_frac * (K(i, T_flash) - 1));
                    y[i] = x[i] * K(i, T_flash) / (1 + V_frac * (K(i, T_flash) - 1));
                }
            }
        }

        // Function to calculate flash distillation
        function calculateFlash(P_flash, Q_hr) {
            Q_hr = parseInt(document.getElementById('slider-heat').value);
            P_flash = parseInt(document.getElementById('slider-pressure').value);
            solveSystem();
            return { T_flash, V_frac, x, y };
        }

        // p5.js setup function
        function setup() {
            createCanvas(800, 500);
            background(240);
            noStroke();
            textSize(16);
            textAlign(CENTER, CENTER);
            textFont('Arial');
            let result = calculateFlash(180, 0);
            T_flash = result.T_flash;
            V_frac = result.V_frac;
            x = result.x;
            y = result.y;
            draw();
        }

        // p5.js draw function
        function draw() {
            background(240);
            // Draw Flash Vessel
            fill(240, 176, 250);
            noStroke();
            rect(350, 200, 100, 100);
            fill(0);
            textSize(24);
            textAlign(CENTER, CENTER);
            text('Flash', 400, 250);

            // Draw Feed Stream
            fill(136, 205, 50);
            noStroke();
            beginShape();
            vertex(100, 250);
            vertex(350, 250);
            endShape();
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('Feed', 175, 275);

            // Draw Vapor Stream
            fill(148, 112, 219);
            noStroke();
            beginShape();
            vertex(400, 200);
            vertex(400, 100);
            vertex(550, 100);
            endShape();
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('Vapor', 475, 125);
            text('Mole Fraction:', 475, 150);
            for (let i = 0; i < 4; i++) {
                text(y[i].toFixed(2), 475, 175 + i * 20);
            }

            // Draw Liquid Stream
            fill(255, 165, 0);
            noStroke();
            beginShape();
            vertex(400, 300);
            vertex(400, 400);
            vertex(550, 400);
            endShape();
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('Liquid', 475, 325);
            text('Mole Fraction:', 475, 350);
            for (let i = 0; i < 4; i++) {
                text(x[i].toFixed(2), 475, 375 + i * 20);
            }

            // Draw Heat Input
            noFill();
            stroke(255, 0, 0);
            strokeWeight(2);
            beginShape();
            vertex(400, 250);
            vertex(400, 150);
            endShape();
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('Q = ' + Q_hr, 400, 175);

            // Draw Process Info
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('Vapor Fraction:', 475, 425);
            text(V_frac.toFixed(2), 475, 450);
            text('Flash Temperature:', 475, 475);
            text(T_flash.toFixed(2) + ' K', 475, 500);
            text('Flash Pressure:', 475, 525);
            text(P_flash + ' kPa', 475, 550);
        }

        // Add event listeners to sliders
        document.getElementById('slider-pressure').addEventListener('input', function() {
            let P_flash = parseInt(this.value);
