    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
        }
        #controls-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        .slider-container {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }
        .slider-container label {
            margin-right: 10px;
        }
        #plot-div {
            width: 800px;
            height: 600px;
            margin: 40px auto;
        }
    </style>
</head>
<body>
    <h1>Best Effort Global Warming Trajectories</h1>
    <p>This demo visualizes the output of a climate model based on user-adjustable parameters.</p>
    <div id="controls-container">
        <div class="slider-container">
            <label for="slider-residence-time">Atmospheric CO2 residence time (years)</label>
            <input type="range" id="slider-residence-time" min="50" max="500" step="1" value="200">
            <span id="value-residence-time">200</span>
        </div>
        <div class="slider-container">
            <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
            <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25">
            <span id="value-transition-pace">25</span>
        </div>
        <div class="slider-container">
            <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
            <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4">
            <span id="value-wedges">4.00</span>
        </div>
    </div>
    <div id="plot-div"></div>
    <script>
        // Constants
        const C0 = 850; // Initial atmospheric carbon in GtC at t=0
        const E0 = 8.5; // Initial annual emission rate in GtC/yr at t=0
        const C_eq = 586; // Pre-industrial equilibrium atmospheric carbon in GtC
        const E_floor = 1.5; // Floor for emission rate in GtC/yr
        const rise_rate = 0.02; // GtC/yr^2, rate of emission increase during transition
        const time_step = 0.5; // years, for numerical integration
        const max_time = 300; // years

        // Initialize plot
        const trace1 = {
            x: Array.from({ length: Math.ceil(max_time / time_step) }, (_, i) => i * time_step),
            y: Array(max_time / time_step + 1).fill(0),
            mode: 'lines',
            line: { color: 'blue', width: 2 }
        };
        const trace2 = {
            x: Array.from({ length: Math.ceil(max_time / time_step) }, (_, i) => i * time_step),
            y: Array(max_time / time_step + 1).fill(0),
            mode: 'lines',
            line: { color: 'purple', width: 2 }
        };
        const trace3 = {
            x: [0, max_time],
            y: [1200, 1200],
            mode: 'lines',
            line: { color: 'black', width: 2, dash: 'dash' }
        };

        const layout = {
            title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
            xaxis: { title: 'years', range: [0, max_time], zeroline: true },
            yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
            showlegend: false,
            margin: { l: 60, r: 20, b: 40, t: 80 },
            paper_bgcolor: '#f9f9f9',
            plot_bgcolor: 'white',
            grid: { color: '#ddd' }
        };

        Plotly.newPlot('plot-div', [trace1, trace2, trace3], layout);

        // Event listeners for sliders
        const sliders = document.querySelectorAll('input[type="range"]');
        sliders.forEach(slider => {
            slider.addEventListener('input', () => {
                const value = slider.value;
                const valueElement = document.getElementById(`value-${slider.id.split('-')[1]}`);
                valueElement.textContent = value;

                if (slider.id === 'slider-wedges') {
                    valueElement.textContent = parseFloat(value).toFixed(2);
                }

                updatePlot();
            });
        });

        // Update plot based on slider values
        function updatePlot() {
            const residenceTime = parseFloat(document.getElementById('slider-residence-time').value);
            const transitionPace = parseFloat(document.getElementById('slider-transition-pace').value);
            const wedges = parseFloat(document.getElementById('slider-wedges').value);

            const timeValues = Array.from({ length: Math.ceil(max_time / time_step) }, (_, i) => i * time_step);
            const emissions = Array(max_time / time_step + 1).fill(0);
            const carbon = Array(max_time / time_step + 1).fill(0);

            // Calculate peak emission rate
            const peakEmissionRate = E0 + rise_rate * transitionPace;

            // Numerical integration for atmospheric carbon and emissions
            for (let t = 0; t <= max_time; t += time_step) {
                if (t < transitionPace) {
                    const emissionRate = E0 + rise_rate * t;
                    emissions[Math.floor(t / time_step)] = emissionRate;
                } else {
                    const reductionRate = wedges / 50;
                    const linearEmissionRate = peakEmissionRate - reductionRate * (t - transitionPace);
                    const finalEmissionRate = Math.max(E_floor, linearEmissionRate);
                    emissions[Math.floor(t / time_step)] = finalEmissionRate;
                }

                if (t === 0) {
                    carbon[Math.floor(t / time_step)] = C0;
                } else {
                    const dCarbon = (emissions[Math.floor(t / time_step)] - (carbon[Math.floor((t - time_step) / time_step)] - C_eq) / residenceTime) * time_step;
                    carbon[Math.floor(t / time_step)] = carbon[Math.floor((t - time_step) / time_step)] + dCarbon;
                }
            }

            // Update plot data
            trace1.y = emissions;
            trace2.y = emissions.map(e => e * 100);
            trace3.x = [0, max_time];

            Plotly.react('plot-div', [trace1, trace2, trace3], layout);
        }

        // Initialize plot with default slider values
        updatePlot();
    </script>
</body>
</html>
