    <script>
        let isPlaying = false;
        let theta0 = 0.5236;
        let v0 = 0;
        let length = 2;
        let theta = 0;
        let theta_dot = 0;
        let x = 0;
        let x_dot = 0;
        let g = 9.8;
        let M = 1;
        let m = 1;
        let dt = 0.01;
        let scale = 50;
        let canvas;

        function setup() {
            canvas = createCanvas(500, 500);
            canvas.parent('visualization');
            frameRate(30);
        }

        function draw() {
            background(220);
            if (isPlaying) {
                update();
                drawPivot();
                drawBob();
                drawConnector();
            }
        }

        function update() {
            let theta_ddot = (-m*length*Math.sin(theta)*Math.cos(theta)*theta_dot**2 - g*(M+m)*Math.sin(theta)) / (length*(M + m*Math.sin(theta)**2));
            theta_dot += theta_ddot * dt;
            theta += theta_dot * dt;
            x_dot = v0 - (m*length*Math.cos(theta)*theta_dot) / (M+m);
            x += x_dot * dt;
        }

        function drawPivot() {
            fill('red');
            noStroke();
            rect(width/2 + x * scale, height/2, 20, 20);
        }

        function drawBob() {
            fill('orange');
            stroke('black');
            strokeWeight(2);
            ellipse(width/2 + (length * scale) * Math.sin(theta) + x * scale, height/2 + (length * scale) * Math.cos(theta), 20, 20);
        }

        function drawConnector() {
            stroke('black');
            strokeWeight(2);
            line(width/2 + x * scale, height/2, width/2 + (length * scale) * Math.sin(theta) + x * scale, height/2 + (length * scale) * Math.cos(theta));
        }

        function reset() {
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            theta0 = document.getElementById('slider-theta0').value;
            v0 = document.getElementById('slider-v0').value;
            length = document.getElementById('slider-length').value;
            theta = 0;
            theta_dot = 0;
            x = 0;
            x_dot = 0;
            draw();
        }

        function playPause() {
            isPlaying = !isPlaying;
            if (isPlaying) {
                document.getElementById('btn-play-pause').textContent = '||';
            } else {
                document.getElementById('btn-play-pause').textContent = '▶';
            }
        }

        document.getElementById('slider-theta0').addEventListener('input', function() {
            let value = this.value;
            let fraction = Math.round(value / Math.PI * 12) / 12;
            let sign = value >= 0 ? '+' : '-';
            document.getElementById('value-theta0').textContent = sign + 'π/' + fraction;
            reset();
        });

        document.getElementById('slider-v0').addEventListener('input', function() {
            document.getElementById('value-v0').textContent = this.value;
            reset();
        });

        document.getElementById('slider-length').addEventListener('input', function() {
            document.getElementById('value-length').textContent = this.value;
            reset();
        });

        document.getElementById('btn-play-pause').addEventListener('click', playPause);
        document.getElementById('btn-reset').addEventListener('click', reset);
    </script>
</body>
</html>
