    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/DDSLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/PNGLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/TextureLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/JSONLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/OBJLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/MTLLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/DDSLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/loaders/PNGLoader.js"></script>
    <script>
        let scene, camera, renderer, controls, sphereGeometry, mesh, boundingBox;
        let strike = 180, dip = 60, slipAngle = 90, slipAmount = 0.5;
        let checkboxBox = document.getElementById('checkbox-box');
        let sliderStrike = document.getElementById('slider-strike');
        let sliderDip = document.getElementById('slider-dip');
        let sliderSlipAngle = document.getElementById('slider-slip-angle');
        let sliderSlipAmount = document.getElementById('slider-slip-amount');
        let valueStrike = document.getElementById('value-strike');
        let valueDip = document.getElementById('value-dip');
        let valueSlipAngle = document.getElementById('value-slip-angle');
        let valueSlipAmount = document.getElementById('value-slip-amount');
        let btnReset = document.getElementById('btn-reset');

        sliderStrike.addEventListener('input', updateUI);
        sliderDip.addEventListener('input', updateUI);
        sliderSlipAngle.addEventListener('input', updateUI);
        sliderSlipAmount.addEventListener('input', updateUI);
        checkboxBox.addEventListener('change', updateUI);
        btnReset.addEventListener('click', resetUI);

        function updateUI() {
            strike = parseFloat(sliderStrike.value);
            dip = parseFloat(sliderDip.value);
            slipAngle = parseFloat(sliderSlipAngle.value);
            slipAmount = parseFloat(sliderSlipAmount.value);
            valueStrike.value = strike;
            valueDip.value = dip;
            valueSlipAngle.value = slipAngle;
            valueSlipAmount.value = slipAmount;
            updateScene();
        }

        function resetUI() {
            strike = 180;
            dip = 60;
            slipAngle = 90;
            slipAmount = 0.5;
            sliderStrike.value = strike;
            sliderDip.value = dip;
            sliderSlipAngle.value = slipAngle;
            sliderSlipAmount.value = slipAmount;
            valueStrike.value = strike;
            valueDip.value = dip;
            valueSlipAngle.value = slipAngle;
            valueSlipAmount.value = slipAmount;
            updateScene();
        }

        function updateScene() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('webgl-canvas'),
                antialias: true
            });
            controls = new THREE.OrbitControls(camera, renderer.domElement);

            let geometry = new THREE.SphereGeometry(1, 64, 32);
            let material = new THREE.MeshStandardMaterial({
                vertexColors: true
            });
            mesh = new THREE.Mesh(geometry, material);
            scene.add(mesh);

            let P = getAxis(strike, dip, slipAngle);
            let T = getAxis(strike, dip, slipAngle + 90);
            let U = getAxis(strike, dip, slipAngle + 180);

            let deformationScale = 0.1 * slipAmount;
            let vertices = geometry.vertices;
            for (let i = 0; i < vertices.length; i++) {
                let v = vertices[i];
                let deformation = (Math.pow(v.dot(P), 2) - Math.pow(v.dot(T), 2)) * v;
                mesh.geometry.vertices[i] = v.add(deformationScale * slipAmount * deformation);
            }
            mesh.geometry.verticesNeedUpdate = true;

            let boundingBox = new THREE.BoxHelper(mesh, 0x00ff00);
            scene.add(boundingBox);

            let compassRose = createCompassRose();
            scene.add(compassRose);

            let axesLabels = createAxesLabels(P, T, U);
            scene.add(axesLabels);

            camera.position.z = 5;
            controls.update();
            renderer.setSize(window.innerWidth, window.innerHeight);
            renderer.render(scene, camera);
        }

        function createCompassRose() {
            let geometry = new THREE.BufferGeometry();
            let positions = [];
            let colors = [];
            let indices = [];

            for (let i = 0; i < 360; i += 10) {
                let x = Math.cos(i * Math.PI / 180) * 0.5;
                let z = Math.sin(i * Math.PI / 180) * 0.5;
                positions.push(x, 0, z);
                colors.push(1, 0, 0);
            }

            for (let i = 0; i < 360; i += 10) {
                let x = Math.cos(i * Math.PI / 180) * 0.5;
                let z = Math.sin(i * Math.PI / 180) * 0.5;
                positions.push(x, 0.2, z);
                colors.push(0, 0, 1);
            }

            geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            let material = new THREE.LineDashedMaterial({
                color: 0xffffff,
                dashSize: 0.1,
                gapSize: 0.1
            });

            let line = new THREE.Line(geometry, material);
            line.rotation.x = -Math.PI / 2;
            line.position.z = 0.5;
            return line;
        }

        function createAxesLabels(P, T, U) {
            let geometry = new THREE.TextGeometry('P', {
                font: 'Arial',
                size: 0.2,
                height: 0.1,
                curveSegments: 12
            });
            let material = new THREE.MeshBasicMaterial({
                color: 0xffffff
            });
            let labelP = new THREE.Mesh(geometry, material);
            labelP.position.copy(P);
            labelP.rotation.x = -Math.PI / 2;
            labelP.rotation.z = Math.PI / 4;

            geometry = new THREE.TextGeometry('T', {
                font: 'Arial',
                size: 0.2,
                height: 0.1,
                curveSegments: 12
            });
            material = new THREE.MeshBasicMaterial({
                color: 0xffffff
            });
            let labelT = new THREE.Mesh(geometry, material);
            labelT.position.copy(T);
            labelT.rotation.x = -Math.PI / 2;
            labelT.rotation.z = Math.PI / 4;

            geometry = new THREE.TextGeometry('U', {
                font: 'Arial',
                size: 0.2,
                height: 0.1,
                curveSegments: 12
            });
            material = new THREE.MeshBasicMaterial({
                color: 0xffffff
            });
            let labelU = new THREE.Mesh(geometry, material);
            labelU.position.copy(U);
            labelU.rotation.x = -Math.PI / 2;
            labelU.rotation.z = Math.PI / 4;

            let group = new THREE.Group();
            group.add(labelP);
            group.add(labelT);
            group.add(labelU);
            return group;
        }

        function getAxis(strike, dip, slipAngle) {
            let n = new THREE.Vector3();
            n.x = Math.cos(strike * Math.PI / 180);
            n.z = Math.sin(strike * Math.PI / 180);
            n.y = Math.sin(dip * Math.PI / 180);
            n = n.normalize();

            let u = new THREE.Vector3();
            u.x = Math.cos(slipAngle * Math.PI / 180 + strike * Math.PI / 180);
            u.z = Math.sin(slipAngle * Math.PI / 180 + strike * Math.PI / 180);
            u.y = Math.sin(dip * Math.PI / 180);
            u = u.normalize();

            let P = new THREE.Vector3();
            P.addVectors(n, u);
            P = P.normalize();

            let T = new THREE.Vector3();
            T.addVectors(n, u.multiplyScalar(-1));
            T = T.normalize();

            return P;
        }

        updateScene();
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
