<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Quantum Simulation Demo</title>
    <style>
        #app-container {
            width: 800px;
            height: 600px;
            border: 1px solid #ccc;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            margin: 40px auto;
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        #control-panel {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            background-color: #f0f0f0;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        .control-group {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }

        .control-group label {
            margin-right: 10px;
        }

        #canvas-container {
            width: 100%;
            height: 400px;
            margin-top: 20px;
        }

        #probability-display {
            font-size: 24px;
            font-weight: bold;
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">Database Size</label>
                <input type="range" id="slider-db-size" min="8" max="100" value="10" step="1">
                <div class="value-display">+ <span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">Number of Iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" value="2" step="1">
                <div class="value-display">+ <span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let N = 10;
        let k = 2;

        function setup() {
            createCanvas(800, 400);
            frameRate(30);
            background(220);
            noStroke();
        }

        function draw() {
            background(220);
            let dbSize = parseInt(document.getElementById('slider-db-size').value);
            let iterations = parseInt(document.getElementById('slider-iterations').value);
            N = dbSize;
            k = iterations;

            // Draw original amplitudes
            let amplitudeWidth = width / N;
            let amplitudeHeight = height / 4;
            let barHeight = amplitudeHeight * 40;
            let mean = 1 / Math.sqrt(N);
            let initialAmplitudes = new Array(N).fill(1 / Math.sqrt(N));
            for (let i = 0; i < N; i++) {
                let x = i * amplitudeWidth + amplitudeWidth / 2;
                let y = height / 2 + barHeight * initialAmplitudes[i];
                fill(128);
                if (i === 6) fill(255, 165, 0); // Orange for target state
                rect(x, y, amplitudeWidth, barHeight);
            }
            fill(0);
            text("Original Amplitudes", width / 2, 20);

            // Draw after first inversion
            let invertedAmplitudes = initialAmplitudes.slice();
            invertedAmplitudes[6] *= -1;
            for (let i = 0; i < N; i++) {
                let x = i * amplitudeWidth + amplitudeWidth / 2;
                let y = height / 2 + barHeight * invertedAmplitudes[i];
                fill(128);
                if (i === 6) fill(255, 165, 0); // Orange for target state
                rect(x, y, amplitudeWidth, barHeight);
            }
            text("After First Inversion", width / 2, 60);

            // Draw after first diffusion
            let diffusedAmplitudes = invertedAmplitudes.slice();
            let diffusedMean = mean;
            for (let i = 0; i < N; i++) {
                diffusedAmplitudes[i] = 2 * diffusedMean - diffusedAmplitudes[i];
            }
            for (let i = 0; i < N; i++) {
                let x = i * amplitudeWidth + amplitudeWidth / 2;
                let y = height / 2 + barHeight * diffusedAmplitudes[i];
                fill(128);
                if (i === 6) fill(255, 165, 0); // Orange for target state
                rect(x, y, amplitudeWidth, barHeight);
            }
            text("After First Diffusion", width / 2, 100);

            // Draw after k iterations
            let finalAmplitudes = initialAmplitudes.slice();
            for (let i = 0; i < k; i++) {
                let diffusedMean = mean;
                for (let j = 0; j < N; j++) {
                    finalAmplitudes[j] = 2 * diffusedMean - finalAmplitudes[j];
                }
            }
            for (let i = 0; i < N; i++) {
                let x = i * amplitudeWidth + amplitudeWidth / 2;
                let y = height / 2 + barHeight * finalAmplitudes[i];
                fill(128);
                if (i === 6) fill(255, 165, 0); // Orange for target state
                rect(x, y, amplitudeWidth, barHeight);
            }
            text("After " + k + " Iterations", width / 2, 140);

            // Draw parentheses around initial amplitudes
            let start = amplitudeWidth * 3;
            let end = start + amplitudeWidth;
            let radius = 10;
            fill(0);
            ellipse(start, height / 2 + barHeight / 2, radius, radius);
            ellipse(end, height / 2 + barHeight / 2, radius, radius);

            // Calculate and display success probability
            let probability = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2);
            let probabilityText = "Success Probability: " + probability.toFixed(1) + "%";
            fill(0);
            text(probabilityText, 20, height - 20);
        }

        function mousePressed() {
            // Update display values
            document.getElementById('display-db-size').textContent = document.getElementById('slider-db-size').value;
            document.getElementById('display-iterations').textContent = document.getElementById('slider-iterations').value;
        }

        // Update display values on slider change
        document.getElementById('slider-db-size').addEventListener('input', function() {
            document.getElementById('display-db-size').textContent = this.value;
        });
        document.getElementById('slider-iterations').addEventListener('input', function() {
            document.getElementById('display-iterations').textContent = this.value;
        });

        setup();
    </script>
</body>
</html>
