    <script>
        let sliderVolatility = document.getElementById('slider-volatility');
        let sliderDistillate = document.getElementById('slider-distillate');
        let sliderBottom = document.getElementById('slider-bottom');
        let sliderFeedComp = document.getElementById('slider-feed-comp');
        let sliderFeedQuality = document.getElementById('slider-feed-quality');
        let btnVolatilityPlus = document.getElementById('btn-volatility-plus');
        let btnDistillatePlus = document.getElementById('btn-distillate-plus');
        let btnBottomPlus = document.getElementById('btn-bottom-plus');
        let btnFeedCompPlus = document.getElementById('btn-feed-comp-plus');
        let btnFeedQualityPlus = document.getElementById('btn-feed-quality-plus');
        let stagesCountDisplay = document.getElementById('stages-count-display');
        let canvasContainer = document.getElementById('canvas-container');

        let volatility = 2.5;
        let distillate = 0.95;
        let bottom = 0.05;
        let feedComp = 0.50;
        let feedQuality = 1.0;
        let stageCount = 8;

        sliderVolatility.addEventListener('input', updateDiagram);
        sliderDistillate.addEventListener('input', updateDiagram);
        sliderBottom.addEventListener('input', updateDiagram);
        sliderFeedComp.addEventListener('input', updateDiagram);
        sliderFeedQuality.addEventListener('input', updateDiagram);
        btnVolatilityPlus.addEventListener('click', incrementSliderValue.bind(null, sliderVolatility));
        btnDistillatePlus.addEventListener('click', incrementSliderValue.bind(null, sliderDistillate));
        btnBottomPlus.addEventListener('click', incrementSliderValue.bind(null, sliderBottom));
        btnFeedCompPlus.addEventListener('click', incrementSliderValue.bind(null, sliderFeedComp));
        btnFeedQualityPlus.addEventListener('click', incrementSliderValue.bind(null, sliderFeedQuality));

        function incrementSliderValue(slider) {
            let step = slider.step;
            let currentValue = parseFloat(slider.value);
            let maxValue = parseFloat(slider.max);
            let minValue = parseFloat(slider.min);
            let newValue = currentValue + step;
            if (newValue > maxValue) {
                newValue = maxValue;
            }
            if (newValue < minValue) {
                newValue = minValue;
            }
            slider.value = newValue;
            updateDiagram();
        }

        function updateDiagram() {
            volatility = parseFloat(sliderVolatility.value);
            distillate = parseFloat(sliderDistillate.value);
            bottom = parseFloat(sliderBottom.value);
            feedComp = parseFloat(sliderFeedComp.value);
            feedQuality = parseFloat(sliderFeedQuality.value);

            if (feedComp < bottom) {
                sliderFeedComp.value = parseFloat(bottom) + parseFloat(sliderFeedComp.step);
            }
            if (feedComp > distillate) {
                sliderFeedComp.value = parseFloat(distillate) - parseFloat(sliderFeedComp.step);
            }
            if (feedComp < distillate) {
                sliderFeedComp.max = distillate;
                sliderFeedComp.min = bottom;
            } else {
                sliderFeedComp.max = 1.0;
                sliderFeedComp.min = 0.0;
            }

            let p5Canvas = createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            p5Canvas.parent(canvasContainer);
            noLoop();
            drawDiagram();
        }

        function drawDiagram() {
            background(220);
            stroke(128);
            line(0, height, width, 0);
            line(0, 0, width, height);
            stroke(255, 0, 0);
            beginShape();
            for (let x = 0; x <= 1; x += 0.01) {
                let y = (volatility * x) / (1 + (volatility - 1) * x);
                vertex(x * width, y * height);
            }
            endShape();
            stroke(0, 128, 0);
            line(0, 0, width, height);
            stroke(0, 255, 255);
            let xD = distillate * width;
            let xB = bottom * width;
            let xF = feedComp * width;
            let yD = distillate * height;
            let yB = bottom * height;
            let yF = feedComp * height;
            stroke(255, 0, 255);
            let m = feedQuality / (feedQuality - 1);
            let xInt = (yF - (m * xF)) / (m - 1);
            let yInt = m * xInt;
            stroke(0, 0, 255);
            line(xD, yD, xInt * width, yInt * height);
            stroke(0, 0, 0);
            let stageCount = 0;
            let x = distillate;
            let y = distillate;
            while (x >= bottom) {
                let newX = x + (volatility * (x - y)) / (1 + (volatility - 1) * x);
                line(x * width, y * height, newX * width, y * height);
                x = newX;
                y = (volatility * x) / (1 + (volatility - 1) * x);
                stageCount++;
            }
            fill(0);
            textSize(24);
            textAlign(CENTER, CENTER);
            text(stageCount, width / 2, height - 10);
        }
    </script>
</body>
</html>
