<body>
    <div id="demo-container">
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month" min="0" max="11" value="(new Date()).getMonth()" step="1">

            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>

        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        let currentMonth = (new Date()).getMonth();
        let perspective = '2D';

        document.getElementById('slider-month').addEventListener('input', () => {
            currentMonth = parseInt(document.getElementById('slider-month').value);
            updateVisualization();
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            perspective = '2D';
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            initP5();
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            perspective = '3D';
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            initThree();
        });

        function updateVisualization() {
            if (perspective === '2D') {
                initP5();
            } else if (perspective === '3D') {
                initThree();
            }
        }

        function initP5() {
            let canvas = document.getElementById('main-canvas');
            let ctx = canvas.getContext('2d');
            let width = canvas.width;
            let height = canvas.height;

            let tilt = 23.5 * Math.cos(2 * Math.PI * currentMonth / 12);

            ctx.clearRect(0, 0, width, height);
            ctx.fillStyle = '#4169E1';
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 50, 0, 2 * Math.PI);
            ctx.fill();

            ctx.fillStyle = '#228B22';
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 20, 0, 2 * Math.PI);
            ctx.fill();

            ctx.fillStyle = '#FFA500';
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 150, 0, 2 * Math.PI);
            ctx.stroke();

            ctx.fillStyle = '#00FFFF';
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 100, 0, 2 * Math.PI);
            ctx.stroke();

            ctx.fillStyle = '#808080';
            for (let i = 0; i < 5; i++) {
                ctx.beginPath();
                ctx.arc(width / 2, height / 2, 20 + i * 20, 0, 2 * Math.PI);
                ctx.stroke();
            }

            ctx.fillStyle = '#006400';
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 20, 0, 2 * Math.PI);
            ctx.fill();

            ctx.fillStyle = '#32CD32';
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 30, 0, 2 * Math.PI);
            ctx.fill();

            ctx.fillStyle = '#F0E68C';
            ctx.beginPath();
            ctx.moveTo(width / 2 - 20, height / 2 - 20);
            ctx.lineTo(width / 2 + 20, height / 2 - 20);
            ctx.lineTo(width / 2 + 20, height / 2 + 20);
            ctx.fill();

            ctx.fillStyle = '#00008B';
            ctx.globalAlpha = 0.4;
            ctx.beginPath();
            ctx.arc(width / 2, height / 2, 50, 0, 2 * Math.PI);
            ctx.fill();
            ctx.globalAlpha = 1;
        }

        function initThree() {
            let scene = new THREE.Scene();
            let camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            let renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('main-canvas'),
                antialias: true
            });
            let controls = new THREE.OrbitControls(camera, renderer.domElement);

            let sun = new THREE.Mesh(new THREE.SphereGeometry(10, 20, 20), new THREE.MeshBasicMaterial({ color: 0xFFFF00 }));
            scene.add(sun);

            let earthOrbit = new THREE.EllipseCurve(0, 0, 200, 100, 0, 2 * Math.PI);
            let earthOrbitGeometry = new THREE.BufferGeometry().setFromPoints(earthOrbit.getPoints(100));
            let earthOrbitMaterial = new THREE.LineBasicMaterial({ color: 0xFFFFFF });
            let earthOrbitMesh = new THREE.Line(earthOrbitGeometry, earthOrbitMaterial);
            scene.add(earthOrbitMesh);

            let earth = new THREE.Mesh(new THREE.SphereGeometry(10, 20, 20), new THREE.MeshStandardMaterial({ color: 0x4169E1 }));
            scene.add(earth);

            let earthAxis = new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 10, 0x00FF00, 10);
            earthAxis.parent = earth;
            scene.add(earthAxis);

            let solarWind = new THREE.ArrowHelper(new THREE.Vector3(1, 0, 0), new THREE.Vector3(0, 0, 0), 100, 0xFF0000, 10);
            scene.add(solarWind);

            let magnetosphere = new THREE.Mesh(new THREE.LatheGeometry(50, 100), new THREE.MeshStandardMaterial({ color: 0x0000FF, transparent: true, opacity: 0.4 }));
            magnetosphere.parent = earth;
            scene.add(magnetosphere);

            camera.position.z = 200;

            function animate() {
                requestAnimationFrame(animate);

                if (currentMonth > 0) {
                    let tilt = 23.5 * Math.cos(2 * Math.PI * currentMonth / 12);
                    earth.rotation.x = tilt;
                    earthAxis.updateMatrixWorld();
                }

                let earthPosition = new THREE.Vector3(200 * Math.cos(2 * Math.PI * currentMonth / 12), 0, 200 * Math.sin(2 * Math.PI * currentMonth / 12));
                earth.position.copy(earthPosition);

                let earthAxisPosition = new THREE.Vector3(0, 10, 0);
                earthAxis.position.copy(earthAxisPosition);

                let solarWindPosition = new THREE.Vector3(100 * Math.cos(2 * Math.PI * currentMonth / 12), 0, 100 * Math.sin(2 * Math.PI * currentMonth / 12));
                solarWind.position.copy(solarWindPosition);

                let magnetospherePosition = new THREE.Vector3(earthPosition.x, earthPosition.y, earthPosition.z);
                magnetosphere.position.copy(magnetospherePosition);

                renderer.render(scene, camera);
            }

            animate();
        }
    </script>
</body>
</html>
