    </div>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        function gamma(x) {
            const a = [0.99999999999980993, 676.5203681218851, -1259.1392167224028, 771.32342877765313, -176.61502916214059, 12.507343278686905, -0.13857109526572012, 9.9843695780195716e-6, 1.5056327351493116e-7];
            x = Math.floor(x);
            const t = (x < 0) ? x + 7.5 : x;
            const term = Math.exp(-t * Math.log(t)) / Math.sqrt(2 * Math.PI * t);
            let result = 0;
            for (let i = 0; i < a.length; i++) {
                result += a[i] / (t + i);
            }
            return Math.sign(x) * term * Math.exp((t + 0.5) * Math.log(2 * Math.PI) - result);
        }

        function gammaPDF(x, alpha, beta) {
            return (x ** (alpha - 1) * Math.exp(-x / beta)) / (beta ** alpha * gamma(alpha));
        }

        function gammaCDF(x, alpha, beta) {
            const pdfValues = [];
            const xValues = Array.from({ length: 1000 }, (_, i) => i / 100);
            for (const xValue of xValues) {
                pdfValues.push(gammaPDF(xValue, alpha, beta));
            }
            return pdfValues.reduce((acc, curr, i) => acc + (i === 0 ? curr : (curr + pdfValues[i - 1]) / 2), 0);
        }

        function updatePlot() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const xValues = Array.from({ length: 1000 }, (_, i) => i / 100);
            const pdfValues = xValues.map(x => gammaPDF(x, alpha, beta));
            const cdfValues = pdfValues.reduce((acc, curr, i) => acc + (i === 0 ? curr : (curr + pdfValues[i - 1]) / 2), 0);
            const mean = alpha * beta;
            const yMean = gammaPDF(mean, alpha, beta);
            const plotData = [
                {
                    x: xValues,
                    y: pdfValues,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2.5 }
                },
                {
                    x: xValues,
                    y: cdfValues,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'orange', width: 2.5 }
                },
                {
                    type: 'line',
                    x0: mean,
                    x1: mean,
                    y0: 0,
                    y1: yMean,
                    line: { color: 'red', width: 3 }
                }
            ];
            Plotly.react('plot-container', plotData, {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { t: 50, b: 50, l: 50, r: 50 }
            });
            document.getElementById('mean-value').textContent = (alpha * beta).toFixed(3);
            document.getElementById('variance-value').textContent = (alpha * beta ** 2).toFixed(3);
            document.getElementById('mgf-formula').innerHTML = `$$ (1 - ${beta} t)^{-${alpha}} = \\frac{1}{(1 - ${beta} t)^{${alpha}}} $$`;
        }

        document.getElementById('slider-alpha').addEventListener('input', updatePlot);
        document.getElementById('slider-beta').addEventListener('input', updatePlot);
        updatePlot();
    </script>
</body>
</html>
