        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas" width="650" height="650"></canvas>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let isDragging = false;
        let locator = { x: 0.2, y: 0.22 };
        let azeotropePoints = [
            { x: 0.655, y: 0, label: "A₁" },
            { x: 0, y: 0.795, label: "A₂" },
            { x: 0.605, y: 0.345, label: "A₃" },
            { x: 0.26, y: 0.335, label: "A₄" }
        ];
        let azeotropePolylines = [
            [{ x: 0.655, y: 0 }, { x: 0, y: 0.795 }, { x: 0.605, y: 0.345 }],
            [{ x: 0.605, y: 0.345 }, { x: 0.26, y: 0.335 }, { x: 0.655, y: 0 }]
        ];

        function setup() {
            createCanvas(650, 650);
            background(255);
            stroke(0);
            strokeWeight(2);
            noFill();

            // Draw axes and ticks
            line(60, 60, 60, height - 60);
            line(60, height - 60, width - 60, height - 60);
            for (let i = 0; i <= 1; i += 0.2) {
                stroke(0);
                strokeWeight(1);
                line(60, map(i, 0, 1, height - 60, 60), 60, map(i, 0, 1, height - 60, 60));
                strokeWeight(2);
                text(i, 10, map(i, 0, 1, height - 60, 60));
                strokeWeight(1);
                line(width - 60, map(i, 0, 1, height - 60, 60), width - 60, map(i, 0, 1, height - 60, 60));
                strokeWeight(2);
                text(i, width - 50, map(i, 0, 1, height - 60, 60));
            }

            // Draw hypotenuse
            line(60, 60, width - 60, height - 60);

            // Draw component labels
            fill(255, 0, 0);
            textSize(16);
            text("methanol", 10, 60);
            text("chloroform", width - 50, 60);
            text("acetone", 10, height - 50);

            // Draw azeotrope points
            fill(153, 0, 0);
            for (let point of azeotropePoints) {
                ellipse(point.x * (width - 120) + 60, map(point.y, 0, 1, height - 60, 60), 6);
                fill(0);
                textSize(12);
                text(point.label, point.x * (width - 120) + 60, map(point.y, 0, 1, height - 60, 60) + 10);
            }

            // Draw distillation line
            drawDistillationLine();
        }

        function draw() {
            background(255);
            drawAxesAndTicks();
            drawHypotenuse();
            drawComponentLabels();
            drawAzeotropePoints();
            drawDistillationLine();
            drawLocator();
        }

        function drawAxesAndTicks() {
            stroke(0);
            strokeWeight(2);
            line(60, 60, 60, height - 60);
            line(60, height - 60, width - 60, height - 60);
            for (let i = 0; i <= 1; i += 0.2) {
                stroke(0);
                strokeWeight(1);
                line(60, map(i, 0, 1, height - 60, 60), 60, map(i, 0, 1, height - 60, 60));
                strokeWeight(2);
                text(i, 10, map(i, 0, 1, height - 60, 60));
                strokeWeight(1);
                line(width - 60, map(i, 0, 1, height - 60, 60), width - 60, map(i, 0, 1, height - 60, 60));
                strokeWeight(2);
                text(i, width - 50, map(i, 0, 1, height - 60, 60));
            }
        }

        function drawHypotenuse() {
            stroke(0);
            line(60, 60, width - 60, height - 60);
        }

        function drawComponentLabels() {
            fill(255, 0, 0);
            textSize(16);
            text("methanol", 10, 60);
            text("chloroform", width - 50, 60);
            text("acetone", 10, height - 50);
        }

        function drawAzeotropePoints() {
            fill(153, 0, 0);
            for (let point of azeotropePoints) {
                ellipse(point.x * (width - 120) + 60, map(point.y, 0, 1, height - 60, 60), 6);
                fill(0);
                textSize(12);
                text(point.label, point.x * (width - 120) + 60, map(point.y, 0, 1, height - 60, 60) + 10);
            }
        }

        function drawDistillationLine() {
            stroke(0, 0, 255);
            strokeWeight(2);
            let points = [];
            let stepSize = 0.005;
            let vector = getVector(locator.x, locator.y);
            let currentPoint = locator;
            while (true) {
                points.push({ x: currentPoint.x * (width - 120) + 60, y: map(currentPoint.y, 0, 1, height - 60, 60) });
                currentPoint.x += vector.dx * stepSize;
                currentPoint.y += vector.dy * stepSize;
                if (currentPoint.x < 0 || currentPoint.y < 0 || currentPoint.x + currentPoint.y >= 1) {
                    break;
                }
                vector = getVector(currentPoint.x, currentPoint.y);
            }
            beginShape();
            for (let point of points) {
                vertex(point.x, point.y);
            }
            endShape();
        }

        function drawLocator() {
            fill(255, 255, 255);
            ellipse(locator.x * (width - 120) + 60, map(locator.y, 0, 1, height - 60, 60), 8);
            stroke(0);
            strokeWeight(2);
            ellipse(locator.x * (width - 120) + 60, map(locator.y, 0, 1, height - 60, 60), 8);
        }

        function getVector(x, y) {
            const A3 = { x: 0.605, y: 0.345 };
            const M = { x: 0, y: 0 };

            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function mousePressed() {
            let distance = dist(mouseX, mouseY, locator.x * (width - 120) + 60, map(locator.y, 0, 1, height - 60, 60));
            if (distance < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                locator.x = (mouseX - 60) / (width - 120);
                locator.y = 1 - (mouseY - 60) / (height - 120);
                drawDistillationLine();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function drawDistillationLine() {
            background(255);
            drawAxesAndTicks();
            drawHypotenuse();
            drawComponentLabels();
            drawAzeotropePoints();
            drawDistillationLine();
            drawLocator();
        }

        setup();
        draw();
    </script>
</body>
</html>
