<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Elliptic Paraboloid Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }

        #main-container {
            display: flex;
            flex-direction: column;
            height: 100vh;
        }

        #control-panel {
            background-color: lightgrey;
            border: 1px solid black;
            padding: 10px;
            flex-grow: 0;
            flex-shrink: 0;
            height: 50px;
        }

        #canvas-container {
            flex-grow: 1;
            flex-shrink: 1;
        }

        canvas {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <h2>lengths of semi-axes</h2>
            </div>
            <div id="slider-group-a">
                <label for="slider-a">a</label>
                <input type="range" id="slider-a" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-a" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-b">
                <label for="slider-b">b</label>
                <input type="range" id="slider-b" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-b" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-c">
                <label for="slider-c">c</label>
                <input type="range" id="slider-c" min="0.1" max="5" step="0.1" value="1.0">
                <input type="number" id="input-c" min="0.1" max="5" step="0.1" value="1.0">
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Get the canvas element
        const canvas = document.getElementById('canvas');
        const renderer = new THREE.WebGLRenderer({
            canvas: canvas,
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);

        // Create the scene, camera, and controls
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Set the camera position and look at the origin
        camera.position.set(5, 5, 5);
        camera.lookAt(new THREE.Vector3(0, 0, 0));

        // Add ambient and directional lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(0, 5, 5);
        scene.add(ambientLight);
        scene.add(directionalLight);

        // Add the coordinate axes
        const axisGeometry = new THREE.BufferGeometry();
        const axisMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });
        const xAxis = new THREE.Line(axisGeometry);
        xAxis.material = axisMaterial;
        xAxis.position.set(0, 0, 0);
        xAxis.lookAt(new THREE.Vector3(5, 0, 0));
        scene.add(xAxis);

        const yAxis = new THREE.Line(axisGeometry);
        yAxis.material = axisMaterial;
        yAxis.position.set(0, 0, 0);
        yAxis.lookAt(new THREE.Vector3(0, 5, 0));
        scene.add(yAxis);

        const zAxis = new THREE.Line(axisGeometry);
        zAxis.material = axisMaterial;
        zAxis.position.set(0, 0, 0);
        zAxis.lookAt(new THREE.Vector3(0, 0, 5));
        scene.add(zAxis);

        // Function to generate the elliptic paraboloid mesh
        function generateParaboloid(a, b, c) {
            const range = 2.0;
            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                let x = a * v * range * Math.cos(u * 2 * Math.PI);
                let y = b * v * range * Math.sin(u * 2 * Math.PI);
                let z = c * (v * range) * (v * range);
                target.set(x, y, z);
            }, 50, 50);

            const material = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true
            });

            const solidMesh = new THREE.Mesh(geometry, material);
            solidMesh.position.set(0, 0, 0);
            scene.add(solidMesh);

            // Create a wireframe mesh for visualization
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                wireframe: true,
                color: 0xcccccc
            });
            const wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);
            wireframeMesh.position.set(0, 0, 0);
            scene.add(wireframeMesh);

            // Function to color the vertices based on their height
            function colorVertices(geometry) {
                const vertices = geometry.getAttribute('position');
                const colors = new Float32Array(vertices.count * 3);
                const maxZ = c * (range * range);
                for (let i = 0; i < vertices.count; i++) {
                    const z = vertices.getY(i);
                    const normalizedZ = (z / maxZ) * 0.8 + 0.2;
                    colors[i * 3] = normalizedZ;
                    colors[i * 3 + 1] = normalizedZ;
                    colors[i * 3 + 2] = 1;
                }
                geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));
            }

            colorVertices(geometry);
        }

        // Initialize the scene with default values
        generateParaboloid(1.5, 1.5, 1.0);

        // Animate the scene
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Add event listeners for slider changes
        const sliderA = document.getElementById('slider-a');
        const sliderB = document.getElementById('slider-b');
        const sliderC = document.getElementById('slider-c');
        const inputA = document.getElementById('input-a');
        const inputB = document.getElementById('input-b');
        const inputC = document.getElementById('input-c');

        sliderA.addEventListener('input', () => {
            inputA.value = sliderA.value;
            generateParaboloid(parseFloat(sliderA.value), parseFloat(inputB.value), parseFloat(inputC.value));
        });

        sliderB.addEventListener('input', () => {
            inputB.value = sliderB.value;
            generateParaboloid(parseFloat(sliderA.value), parseFloat(sliderB.value), parseFloat(inputC.value));
        });

        sliderC.addEventListener('input', () => {
            inputC.value = sliderC.value;
            generateParaboloid(parseFloat(sliderA.value), parseFloat(inputB.value), parseFloat(sliderC.value));
        });

        inputA.addEventListener('input', () => {
            sliderA.value = inputA.value;
            generateParaboloid(parseFloat(sliderA.value), parseFloat(inputB.value), parseFloat(inputC.value));
        });

        inputB.addEventListener('input', () => {
            sliderB.value = inputB.value;
            generateParaboloid(parseFloat(sliderA.value), parseFloat(sliderB.value), parseFloat(inputC.value));
        });

        inputC.addEventListener('input', () => {
            sliderC.value = inputC.value;
            generateParaboloid(parseFloat(sliderA.value), parseFloat(inputB.value), parseFloat(sliderC.value));
        });
    </script>
</body>
</html>
