                m2Selector.innerHTML = '';
                for (let m of m2Values) {
                    let button = document.createElement('button');
                    button.value = m;
                    button.textContent = m;
                    m2Selector.appendChild(button);
                }
                jSelector.innerHTML = '';
                for (let j of jValues) {
                    let button = document.createElement('button');
                    button.value = j;
                    button.textContent = j;
                    jSelector.appendChild(button);
                }
                m1Selector.innerHTML = '';
                m1Selector.appendChild(document.createElement('button'));
                let j1Value = parseFloat(j1Selector.children[1].value);
                let jValues1 = [];
                for (let m = -j1Value; m <= j1Value; m++) {
                    jValues1.push(Math.sqrt(j1Value * (j1Value + 1)));
                }
                for (let j of jValues1) {
                    let button = document.createElement('button');
                    button.value = j;
                    button.textContent = j;
                    jSelector.appendChild(button);
                }
                let m2Value = parseFloat(m2Selector.children[1].value);
                let j2Value = parseFloat(j2Selector.children[1].value);
                let j = Math.sqrt(j1Value * (j1Value + 1));
                for (let child of m2Selector.children) {
                    if (child.value == m2Value) {
                        child.style.backgroundColor = '#666';
                    } else {
                        child.style.backgroundColor = '';
                    }
                }
                for (let child of jSelector.children) {
                    if (child.value == j) {
                        child.style.backgroundColor = '#666';
                    } else {
                        child.style.backgroundColor = '';
                    }
                }
                phi_z = 0;
                phi_j = 0;
                isAnimating = false;
                draw();
            });

            m1Selector.addEventListener('click', (e) => {
                let value = parseFloat(e.target.value);
                let m1 = value;
                let j = Math.sqrt(parseFloat(j1Selector.children[1].value) * (parseFloat(j1Selector.children[1].value) + 1));
                for (let child of m1Selector.children) {
                    if (child.value == m1) {
                        child.style.backgroundColor = '#666';
                    } else {
                        child.style.backgroundColor = '';
                    }
                }
                phi_z = 0;
                phi_j = 0;
                isAnimating = false;
                draw();
            });

            m2Selector.addEventListener('click', (e) => {
                let value = parseFloat(e.target.value);
                let m2 = value;
                let j = Math.sqrt(parseFloat(j2Selector.children[1].value) * (parseFloat(j2Selector.children[1].value) + 1));
                for (let child of m2Selector.children) {
                    if (child.value == m2) {
                        child.style.backgroundColor = '#666';
                    } else {
                        child.style.backgroundColor = '';
                    }
                }
                phi_z = 0;
                phi_j = 0;
                isAnimating = false;
                draw();
            });

            jSelector.addEventListener('click', (e) => {
                let value = parseFloat(e.target.value);
                let j = value;
                let m1 = parseFloat(m1Selector.children[1].value);
                let j1 = parseFloat(j1Selector.children[1].value);
                let m2 = parseFloat(m2Selector.children[1].value);
                let j2 = parseFloat(j2Selector.children[1].value);
                for (let child of jSelector.children) {
                    if (child.value == j) {
                        child.style.backgroundColor = '#666';
                    } else {
                        child.style.backgroundColor = '';
                    }
                }
                phi_z = 0;
                phi_j = 0;
                isAnimating = false;
                draw();
            });

            let playPauseButton = document.getElementById('btn-play-pause');
            playPauseButton.addEventListener('click', () => {
                if (!isAnimating) {
                    isAnimating = true;
                    playPauseButton.textContent = '❚❚';
                } else {
                    isAnimating = false;
                    playPauseButton.textContent = '▶';
                }
            });

            let resetButton = document.getElementById('btn-reset');
            resetButton.addEventListener('click', () => {
                phi_z = 0;
                phi_j = 0;
                isAnimating = false;
                playPauseButton.textContent = '▶';
                for (let child of m1Selector.children) {
                    child.style.backgroundColor = '';
                }
                for (let child of m2Selector.children) {
                    child.style.backgroundColor = '';
                }
                for (let child of jSelector.children) {
                    child.style.backgroundColor = '';
                }
                draw();
            });

            function draw() {
                scene.clear();
                let j1 = parseFloat(j1Selector.children[1].value);
                let m1 = parseFloat(m1Selector.children[1].value);
                let j2 = parseFloat(j2Selector.children[1].value);
                let m2 = parseFloat(m2Selector.children[1].value);
                let j = parseFloat(jSelector.children[1].value);
                let m = m1 + m2;
                let L_J = Math.sqrt(j * (j + 1));
                let L_J1 = Math.sqrt(j1 * (j1 + 1));
                let L_J2 = Math.sqrt(j2 * (j2 + 1));
                let angleJ = Math.acos(m / L_J);
                let angleJ1 = Math.acos(m1 / L_J1);
                let angleJ2 = Math.acos(m2 / L_J2);

                let J = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), L_J, 0xffffff);
                scene.add(J);

                if (j >= j1 && j >= j2) {
                    let J1 = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), L_J1, 0xff0000);
                    let J2 = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), L_J2, 0x0000ff);
                    let coneJ = new THREE.ConeGeometry(0.1, L_J, 32);
                    let coneJ1 = new THREE.ConeGeometry(0.1, L_J1, 32);
                    let coneJ2 = new THREE.ConeGeometry(0.1, L_J2, 32);
                    let coneMeshJ = new THREE.Mesh(coneJ, new THREE.MeshBasicMaterial({ color: 0xffffff, opacity: 0.5, transparent: true }));
                    let coneMeshJ1 = new THREE.Mesh(coneJ1, new THREE.MeshBasicMaterial({ color: 0xff0000, opacity: 0.5, transparent: true }));
                    let coneMeshJ2 = new THREE.Mesh(coneJ2, new THREE.MeshBasicMaterial({ color: 0x0000ff, opacity: 0.5, transparent: true }));
                    coneMeshJ.position.set(0, 0, L_J);
                    coneMeshJ1.position.set(0, 0, L_J1);
                    coneMeshJ2.position.set(0, 0, L_J2);
                    scene.add(coneMeshJ);
                    scene.add(coneMeshJ1);
                    scene.add(coneMeshJ2);

                    let rotation = new THREE.Quaternion();
                    rotation.setFromEuler(new THREE.Euler(0, 0, angleJ));
                    J1.quaternion.copy(rotation);
                    J2.quaternion.copy(rotation);

                    J1.position.copy(J.position);
                    J2.position.copy(J.position);

                    let rotationJ = new THREE.Quaternion();
                    rotationJ.setFromEuler(new THREE.Euler(0, phi_j, 0));
                    J1.quaternion.multiply(rotationJ);
                    J2.quaternion.multiply(rotationJ);

                    let rotationZ = new THREE.Quaternion();
                    rotationZ.setFromEuler(new THREE.Euler(0, 0, phi_z));
                    J1.quaternion.multiply(rotationZ);
                    J2.quaternion.multiply(rotationZ);
                } else {
                    let J1 = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), L_J1, 0xff0000);
                    let J2 = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), L_J2, 0x0000ff);
                    let coneJ1 = new THREE.ConeGeometry(0.1, L_J1, 32);
                    let coneJ2 = new THREE.ConeGeometry(0.1, L_J2, 32);
                    let coneMeshJ1 = new THREE.Mesh(coneJ1, new THREE.MeshBasicMaterial({ color: 0xff0000, opacity: 0.5, transparent: true }));
                    let coneMeshJ2 = new THREE.Mesh(coneJ2, new THREE.MeshBasicMaterial({ color: 0x0000ff, opacity: 0.5, transparent: true }));
                    coneMeshJ1.position.set(0, 0, L_J1);
                    coneMeshJ2.position.set(0, 0, L_J2);
                    scene.add(coneMeshJ1);
                    scene.add(coneMeshJ2);

                    J1.position.copy(new THREE.Vector3(Math.cos(angleJ1) * L_J1, Math.sin(angleJ1) * L_J1, 0));
                    J2.position.copy(new THREE.Vector3(Math.cos(angleJ2) * L_J2, Math.sin(angleJ2) * L_J2, 0));

                    let rotationJ1 = new THREE.Quaternion();
                    rotationJ1.setFromEuler(new THREE.Euler(0, phi_z, 0));
                    J1.quaternion.copy(rotationJ1);

                    let rotationJ2 = new THREE.Quaternion();
                    rotationJ2.setFromEuler(new THREE.Euler(0, phi_z, 0));
                    J2.quaternion.copy(rotationJ2);
                }

                renderer.setSize(800, 600);
                renderer.render(scene, camera);
            }

            function animate() {
                requestAnimationFrame(animate);
                if (isAnimating) {
                    phi_z += 0.01;
                    phi_j += 0.01;
                    draw();
                }
            }

            animate();
        }

        init();
    </script>
</body>
</html>
