            display: flex;
            flex-direction: row;
            padding: 20px;
        }

        #recursive-container, #iterative-container {
            flex: 1;
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        #canvas-recursive, #canvas-iterative {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <label for="steps-slider">steps</label>
        <input type="range" id="steps-slider" min="0" max="100" value="61" step="1">
        <span id="steps-value">+ 61</span>
        <div id="box-type-control">
            <label>box</label>
            <button id="btn-none" class="box-button">None</button>
            <button id="btn-box" class="box-button">Box</button>
            <button id="btn-scattered" class="box-button active">Scattered</button>
        </div>
    </div>
    <div id="viz-container">
        <div id="recursive-container">
            <h3>Recursive</h3>
            <div id="canvas-recursive"></div>
        </div>
        <div id="iterative-container">
            <h3>Iterative</h3>
            <div id="canvas-iterative"></div>
        </div>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <script>
        let recursiveHistory = [];
        let iterativeHistory = [];
        let grid = [];
        let currentStep = 61;
        let obstaclePattern = 'scattered';
        let boxButtons = document.querySelectorAll('.box-button');

        function setup() {
            createCanvas(201, 201);
            noStroke();
            rectMode(CORNER);
        }

        function draw() {
            background(255);
            drawGrid();
        }

        function drawGrid() {
            for (let i = 0; i < 10; i++) {
                for (let j = 0; j < 10; j++) {
                    let x = i * 20 + 1;
                    let y = j * 20 + 1;
                    if (grid[i][j] === 'obstacle') {
                        fill(0);
                    } else if (grid[i][j] === 'filled') {
                        fill(0, 0, 255);
                    } else {
                        fill(255);
                    }
                    rect(x, y, 18, 18);
                    if (i > 0 && j > 0 && i < 9 && j < 9) {
                        stroke(0);
                        strokeWeight(1);
                        line(x, y, x + 18, y);
                        line(x, y + 18, x + 18, y + 18);
                    }
                }
            }
        }

        function floodFillRecursive(x, y) {
            if (x < 0 || x >= 10 || y < 0 || y >= 10 || grid[x][y] === 'obstacle' || recursiveHistory.includes(`${x},${y}`)) {
                return;
            }
            recursiveHistory.push(`${x},${y}`);
            grid[x][y] = 'filled';
            floodFillRecursive(x - 1, y);
            floodFillRecursive(x + 1, y);
            floodFillRecursive(x, y - 1);
            floodFillRecursive(x, y + 1);
        }

        function floodFillIterative() {
            let queue = [[4, 4]];
            while (queue.length > 0) {
                let [x, y] = queue.shift();
                if (grid[x][y] !== 'obstacle' && !iterativeHistory.includes(`${x},${y}`)) {
                    iterativeHistory.push(`${x},${y}`);
                    grid[x][y] = 'filled';
                    queue.push([x - 1, y]);
                    queue.push([x + 1, y]);
                    queue.push([x, y - 1]);
                    queue.push([x, y + 1]);
                }
            }
        }

        function generateObstaclePattern() {
            if (obstaclePattern === 'none') {
                for (let i = 0; i < 10; i++) {
                    for (let j = 0; j < 10; j++) {
                        grid[i][j] = 'empty';
                    }
                }
            } else if (obstaclePattern === 'box') {
                for (let i = 0; i < 10; i++) {
                    grid[i][0] = 'obstacle';
                    grid[i][9] = 'obstacle';
                    grid[0][i] = 'obstacle';
                    grid[9][i] = 'obstacle';
                }
            } else if (obstaclePattern === 'scattered') {
                for (let i = 0; i < 10; i++) {
                    for (let j = 0; j < 10; j++) {
                        if (random(1) < 0.25 && (i !== 4 || j !== 4)) {
                            grid[i][j] = 'obstacle';
                        } else {
                            grid[i][j] = 'empty';
                        }
                    }
                }
            }
        }

        function resetSimulation() {
            recursiveHistory = [];
            iterativeHistory = [];
            grid = [];
            currentStep = 0;
            generateObstaclePattern();
            floodFillRecursive(4, 4);
            floodFillIterative();
            document.getElementById('steps-slider').value = 0;
            document.getElementById('steps-value').textContent = '+ 0';
            draw();
        }

        boxButtons.forEach(button => {
            button.addEventListener('click', () => {
                boxButtons.forEach(b => b.classList.remove('active'));
                button.classList.add('active');
                obstaclePattern = button.id;
                resetSimulation();
            });
        });

        document.getElementById('steps-slider').addEventListener('input', () => {
            currentStep = parseInt(document.getElementById('steps-slider').value);
            document.getElementById('steps-value').textContent = `+ ${currentStep}`;
            draw();
        });

        generateObstaclePattern();
        floodFillRecursive(4, 4);
        floodFillIterative();
        draw();
    </script>
</body>
</html>
