        <canvas id="canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let k = 0.9;
        let time = 15;
        let vx = 2.5;
        let dt = 0.15;
        let g = 9.8;
        let y0 = 10;
        let vy0 = 0;
        let x0 = 0;
        let currentTime = 0;
        let currentX = x0;
        let currentY = y0;
        let currentVy = vy0;
        let pathPoints = [];
        let strobePoints = [];
        let maxXTicks = 0;
        let maxYTicks = 0;

        function setup() {
            createCanvas(700, 400);
            background(0);
            noLoop();
            strokeWeight(2);
        }

        function draw() {
            background(0);
            drawAxes();
            drawTrajectory();
            drawStrobePoints();
            drawTicks();
        }

        function drawAxes() {
            stroke(100, 100, 150);
            line(40, 360, 640, 360);
            line(40, 360, 40, 40);
        }

        function drawTicks() {
            stroke(100, 100, 150);
            for (let i = 40; i <= 640; i += 100) {
                line(i, 40, i, 360);
            }
            for (let i = 40; i <= 360; i += 50) {
                line(40, i, 40, 40);
            }
        }

        function drawTrajectory() {
            stroke(255, 0, 0);
            noFill();
            beginShape();
            for (let i = 0; i < pathPoints.length; i++) {
                vertex(pathPoints[i].x, pathPoints[i].y);
            }
            endShape();
        }

        function drawStrobePoints() {
            noStroke();
            fill(255, 255, 0);
            for (let i = 0; i < strobePoints.length; i++) {
                ellipse(strobePoints[i].x, strobePoints[i].y, 8, 8);
            }
        }

        function calculatePathPoints() {
            pathPoints = [];
            strobePoints = [];
            let vx = vx;
            let vy = vy0;
            let t_flight = 0;
            let strobeTime = 0;
            let x = x0;
            let y = y0;
            let vy_impact = 0;
            while (currentTime < time) {
                t_flight = (vy + Math.sqrt(vy * vy + 2 * g * y)) / g;
                for (let t = 0; t <= t_flight; t += 0.01) {
                    let x_new = x + vx * t;
                    let y_new = y + vy * t - 0.5 * g * t * t;
                    pathPoints.push({x: x_new, y: y_new});
                }
                strobeTime = Math.ceil((currentTime + t_flight) / dt) * dt;
                while (strobeTime < currentTime + t_flight) {
                    let x_new = x + vx * (strobeTime - currentTime);
                    let y_new = y + vy * (strobeTime - currentTime) - 0.5 * g * (strobeTime - currentTime) * (strobeTime - currentTime);
                    strobePoints.push({x: x_new, y: y_new});
                    strobeTime += dt;
                }
                x = x + vx * t_flight;
                y = 0;
                vy = Math.sqrt(k) * vy_impact;
                currentTime += t_flight;
            }
        }

        function calculateTicks() {
            maxXTicks = 0;
            maxYTicks = 0;
            for (let i = 0; i < pathPoints.length; i++) {
                if (pathPoints[i].x > maxXTicks) {
                    maxXTicks = pathPoints[i].x;
                }
                if (pathPoints[i].y > maxYTicks) {
                    maxYTicks = pathPoints[i].y;
                }
            }
            maxXTicks = Math.ceil(maxXTicks);
            maxYTicks = Math.ceil(maxYTicks);
        }

        function update() {
            calculatePathPoints();
            calculateTicks();
            redraw();
        }

        function sliderChanged() {
            k = document.getElementById("slider-k").value;
            time = document.getElementById("slider-time").value;
            vx = document.getElementById("slider-vx").value;
            dt = document.getElementById("slider-dt").value;
            update();
        }

        document.getElementById("slider-k").addEventListener("input", sliderChanged);
        document.getElementById("slider-time").addEventListener("input", sliderChanged);
        document.getElementById("slider-vx").addEventListener("input", sliderChanged);
        document.getElementById("slider-dt").addEventListener("input", sliderChanged);

        setup();
        update();
    </script>
</body>
</html>
