</head>
<body>
    <!-- Header Section -->
    <div id="header">
        <h1>Angle Bisectors on the Circumcircle</h1>
        <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    </div>

    <!-- Visualization Section -->
    <div id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </div>

    <!-- p5.js Library -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script>
        let angleA = 7 * Math.PI / 6;
        let angleB = 11 * Math.PI / 6;
        let angleC = Math.PI / 2;
        let dragPoint = null;

        function setup() {
            createCanvas(500, 500);
            background(240);
            noStroke();
            strokeWeight(4);
            stroke(128, 88, 205); // SlateBlue

            // Circumcircle
            fill(240);
            ellipse(width / 2, height / 2, 400);

            // Triangle ABC
            stroke(128, 88, 205); // SlateBlue
            line(0, 0, 200, 0);
            line(200, 0, 200, 200);
            line(200, 200, 0, 0);

            // Points A, B, C
            fill(255, 165, 0); // Orange
            ellipse(200, 0, 24);
            ellipse(200, 200, 24);
            ellipse(0, 200, 24);

            // Labels
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("A", 200, -10);
            text("B", 200, 210);
            text("C", -10, 200);
        }

        function draw() {
            background(240);

            // Circumcircle
            stroke(128, 88, 205); // SlateBlue
            ellipse(width / 2, height / 2, 400);

            // Triangle ABC
            stroke(128, 88, 205); // SlateBlue
            line(0, 0, 200, 0);
            line(200, 0, 200, 200);
            line(200, 200, 0, 0);

            // Points A, B, C
            fill(255, 165, 0); // Orange
            ellipse(200, 0, 24);
            ellipse(200, 200, 24);
            ellipse(0, 200, 24);

            // Labels
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("A", 200, -10);
            text("B", 200, 210);
            text("C", -10, 200);

            // Calculate A', B', C'
            let xA = 200 + 200 * cos(angleA);
            let yA = 200 + 200 * sin(angleA);
            let xB = 200 + 200 * cos(angleB);
            let yB = 200 + 200 * sin(angleB);
            let xC = 200 + 200 * cos(angleC);
            let yC = 200 + 200 * sin(angleC);

            let angleA_ = atan2(sin(angleB) + sin(angleC), cos(angleB) + cos(angleC));
            let angleB_ = atan2(sin(angleA) + sin(angleC), cos(angleA) + cos(angleC));
            let angleC_ = atan2(sin(angleA) + sin(angleB), cos(angleA) + cos(angleB));

            let xA_ = 200 + 200 * cos(angleA_);
            let yA_ = 200 + 200 * sin(angleA_);
            let xB_ = 200 + 200 * cos(angleB_);
            let yB_ = 200 + 200 * sin(angleB_);
            let xC_ = 200 + 200 * cos(angleC_);
            let yC_ = 200 + 200 * sin(angleC_);

            // Draw A', B', C'
            fill(255, 165, 0); // Orange
            ellipse(xA_, yA_, 24);
            ellipse(xB_, yB_, 24);
            ellipse(xC_, yC_, 24);

            // Draw AA', BB', CC'
            stroke(255, 165, 0); // Orange
            line(200, 0, xA_, yA_);
            line(200, 200, xB_, yB_);
            line(0, 200, xC_, yC_);

            // Draw A'B', B'C', C'A'
            stroke(255, 165, 0); // Orange
            line(xA_, yA_, xB_, yB_);
            line(xB_, yB_, xC_, yC_);
            line(xC_, yC_, xA_, yA_);
        }

        function mousePressed() {
            let dx = dist(mouseX, mouseY, 200, 0);
            let dy = dist(mouseX, mouseY, 200, 200);
            let dz = dist(mouseX, mouseY, 0, 200);

            if (dx < 15) {
                dragPoint = "A";
            } else if (dy < 15) {
                dragPoint = "B";
            } else if (dz < 15) {
                dragPoint = "C";
            }
        }

        function mouseDragged() {
            if (dragPoint === "A") {
                angleA = atan2(mouseY - height / 2, mouseX - width / 2);
            } else if (dragPoint === "B") {
                angleB = atan2(mouseY - height / 2, mouseX - width / 2);
            } else if (dragPoint === "C") {
                angleC = atan2(mouseY - height / 2, mouseX - width / 2);
            }
        }

        function mouseReleased() {
            dragPoint = null;
        }
    </script>
</body>
</html>
