            let quarterAngle = angle + PI / 12;
            let tipX = x1 + cos(halfAngle) * arrowSize;
            let tipY = y1 + sin(halfAngle) * arrowSize;
            let baseX = x1 + cos(quarterAngle) * arrowSize;
            let baseY = y1 + sin(quarterAngle) * arrowSize;
            line(x1, y1, x2, y2);
            line(tipX, tipY, baseX, baseY);
