    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let globalState = {
            t: 0.4,
            position: false,
            velocity: false,
            acceleration: true,
            coords: 'rectangular',
            circle: false
        };

        let canvas;
        let ctx;

        function setup() {
            canvas = createCanvas(800, 600);
            ctx = canvas.getContext('2d');
            canvas.parent('canvas-container');
            background(220);
        }

        function draw() {
            background(220);
            drawCoordinateSystem();
            drawCurve();
            drawPoint();
            drawVectors();
            drawCircle();
        }

        function drawCoordinateSystem() {
            stroke(0);
            line(0, 300, 800, 300);
            line(400, 0, 400, 600);
            fill(0);
            text('x(t)', 380, 10);
            text('y(t)', 10, 290);
            for (let i = -2; i <= 2; i += 1) {
                line(i * 100 + 400, 300, i * 100 + 400, 290);
                fill(0);
                text(i, i * 100 + 410, 300);
            }
        }

        function drawCurve() {
            stroke(0, 0, 255);
            beginShape();
            for (let i = 0; i <= 2 * PI; i += 0.01) {
                let x = cos(i);
                let y = sin(2 * i);
                vertex(x * 400 + 400, y * 300 + 300);
            }
            endShape();
        }

        function drawPoint() {
            fill(0);
            ellipse(cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300, 10);
        }

        function drawVectors() {
            if (globalState.position) {
                stroke(0, 0, 0);
                line(400, 300, cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300);
            }
            if (globalState.velocity) {
                stroke(128, 0, 0);
                let vx = -sin(globalState.t);
                let vy = 2 * cos(2 * globalState.t);
                line(cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300, (cos(globalState.t) + vx) * 400 + 400, (sin(2 * globalState.t) + vy) * 300 + 300);
            }
            if (globalState.acceleration) {
                stroke(128, 0, 128);
                let ax = -cos(globalState.t);
                let ay = -4 * sin(2 * globalState.t);
                line(cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300, (cos(globalState.t) + ax) * 400 + 400, (sin(2 * globalState.t) + ay) * 300 + 300);
            }
            if (globalState.coords === 'rectangular') {
                stroke(0);
                line(400, 300, 400, 290);
                fill(0);
                text('î', 410, 290);
                line(400, 300, 410, 300);
                text('ĵ', 420, 300);
            } else if (globalState.coords === 'normal-tangential') {
                stroke(0);
                let vx = -sin(globalState.t);
                let vy = 2 * cos(2 * globalState.t);
                let vn = vx / sqrt(vx * vx + vy * vy);
                let vt = vy / sqrt(vx * vx + vy * vy);
                let nx = -vt;
                let ny = vn;
                line(cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300, (cos(globalState.t) + vx) * 400 + 400, (sin(2 * globalState.t) + vy) * 300 + 300);
                line(cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300, (cos(globalState.t) + vx + vn * 100) * 400 + 400, (sin(2 * globalState.t) + vy + ny * 100) * 300 + 300);
                fill(0);
                text('ê_T', (cos(globalState.t) + vx) * 400 + 410, (sin(2 * globalState.t) + vy) * 300 + 290);
                text('ê_N', (cos(globalState.t) + vx + vn * 100) * 400 + 410, (sin(2 * globalState.t) + vy + ny * 100) * 300 + 290);
            } else if (globalState.coords === 'polar') {
                stroke(0);
                let r = sqrt(cos(globalState.t) * cos(globalState.t) + sin(2 * globalState.t) * sin(2 * globalState.t));
                let theta = atan2(sin(2 * globalState.t), cos(globalState.t));
                let rx = r * cos(theta);
                let ry = r * sin(theta);
                line(cos(globalState.t) * 400 + 400, sin(2 * globalState.t) * 300 + 300, (cos(globalState.t) + rx) * 400 + 400, (sin(2 * globalState.t) + ry) * 300 + 300);
                fill(0);
                text('ê_r', (cos(globalState.t) + rx) * 400 + 410, (sin(2 * globalState.t) + ry) * 300 + 290);
                text('ê_θ', (cos(globalState.t) + rx + 100 * cos(theta)) * 400 + 410, (sin(2 * globalState.t) + ry + 100 * sin(theta)) * 300 + 290);
            }
        }

        function drawCircle() {
            if (globalState.circle) {
                stroke(128, 128, 128);
                let vx = -sin(globalState.t);
                let vy = 2 * cos(2 * globalState.t);
                let r = sqrt(vx * vx + vy * vy);
                let theta = atan2(vy, vx);
                let cx = cos(globalState.t) + r * cos(theta);
                let cy = sin(2 * globalState.t) + r * sin(theta);
                let radius = r / (4 * sqrt(vx * vx + vy * vy));
                ellipse(cx * 400 + 400, cy * 300 + 300, radius * 200, radius * 200);
            }
        }

        function mousePressed() {
            globalState.t = 0.4;
            redraw();
        }

        function keyPressed() {
            if (key === 'r') {
                globalState.t = 0;
                redraw();
            }
        }

        function update() {
            let slider = document.getElementById('slider-time');
            globalState.t = parseFloat(slider.value);
            redraw();
        }

        function togglePosition() {
            globalState.position = !globalState.position;
            redraw();
        }

        function toggleVelocity() {
            globalState.velocity = !globalState.velocity;
            redraw();
        }

        function toggleAcceleration() {
            globalState.acceleration = !globalState.acceleration;
            redraw();
        }

        function selectCoords() {
            globalState.coords = document.getElementById('select-coords').value;
            redraw();
        }

        function toggleCircle() {
            globalState.circle = !globalState.circle;
            redraw();
        }

        setup();
        draw();

        document.getElementById('slider-time').addEventListener('input', update);
        document.getElementById('btn-reset-time').addEventListener('click', () => {
            globalState.t = 0.4;
            redraw();
        });
        document.getElementById('toggle-position').addEventListener('change', togglePosition);
        document.getElementById('toggle-velocity').addEventListener('change', toggleVelocity);
        document.getElementById('toggle-acceleration').addEventListener('change', toggleAcceleration);
        document.getElementById('select-coords').addEventListener('change', selectCoords);
        document.getElementById('toggle-circle').addEventListener('change', toggleCircle);
    </script>
</body>
</html>
