<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Spring Mass System Simulation</title>
    <style>
        body {
            background-color: #f0f0f0;
        }
        #control-panel {
            display: flex;
            justify-content: space-between;
            padding: 10px;
            background-color: #ccc;
            border-bottom: 1px solid #ddd;
        }
        #control-panel > div {
            margin: 0 10px;
        }
        #plot-canvas-container, #animation-canvas-container {
            display: inline-block;
            vertical-align: top;
        }
        #plot-canvas-container {
            width: 80%;
        }
        #animation-canvas-container {
            width: 20%;
        }
        canvas {
            border: 1px solid #ddd;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div>
            <label for="slider-amplitude">Amplitude:</label>
            <input type="range" id="slider-amplitude" min="0.1" max="1.0" step="0.001" value="0.3">
            <span id="amplitude-value">0.3</span>
        </div>
        <div>
            <label for="slider-stiffness">Stiffness:</label>
            <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
            <span id="stiffness-value">1.0</span>
        </div>
        <div>
            <label for="slider-mass">Mass:</label>
            <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
            <span id="mass-value">1.0</span>
        </div>
        <div>
            <label for="time">Time:</label>
            <button id="btn-step-back">◀</button>
            <button id="btn-play-pause">▶</button>
            <button id="btn-step-forward">▶|</button>
        </div>
        <button id="btn-reset">x</button>
    </div>
    <div id="visualization-container">
        <div id="plot-canvas-container"></div>
        <div id="animation-canvas-container"></div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency;
        let positionHistory = [];

        function setup() {
            createCanvas(800, 200);
            angleMode(DEGREES);
            background(255);
            stroke(0);
            noFill();
            rectMode(CENTER);
            textAlign(CENTER, CENTER);
        }

        function draw() {
            if (isPlaying) {
                time += 0.05;
                let y = amplitude * sin(angularFrequency * time);
                positionHistory.push([time, y]);
                if (time > positionHistory[0][0]) {
                    positionHistory.shift();
                }
                drawPlot();
                drawAnimation();
            }
        }

        function drawPlot() {
            background(255);
            stroke(0);
            line(0, height / 2, width, height / 2);
            line(width / 2, 0, width / 2, height);
            for (let i = -1; i <= 1; i += 0.5) {
                line(width / 2, i * height / 2 + height / 2, width / 2, i * height / 2 + height / 2);
                text(i, width / 2, i * height / 2 + height / 2 + 10);
            }
            stroke(153, 99, 178);
            beginShape();
            for (let i = 0; i < positionHistory.length; i++) {
                vertex(positionHistory[i][0] * width / 25 + width / 2, positionHistory[i][1] * height / 2 + height / 2);
            }
            endShape();
        }

        function drawAnimation() {
            background(255);
            stroke(0);
            line(0, height / 2, width, height / 2);
            for (let i = -1; i <= 1; i += 0.5) {
                line(width / 2, i * height / 2 + height / 2, width / 2, i * height / 2 + height / 2);
                text(i, width / 2, i * height / 2 + height / 2 + 10);
            }
            let y = amplitude * sin(angularFrequency * time);
            fill(153, 99, 178);
            rect(width / 2, y * height / 2 + height / 2, 20, 20);
            stroke(0);
            beginShape();
            for (let i = -1.2; i <= 1.2; i += 0.2) {
                let x = width / 2 + (i - y) * 10;
                vertex(x, height / 2 + 10);
                vertex(x, height / 2 - 10);
            }
            endShape();
        }

        function mousePressed() {
            if (isPlaying) {
                isPlaying = false;
                document.getElementById('btn-play-pause').innerHTML = '▶';
            }
        }

        function updateSliderValue(id, value) {
            let slider = document.getElementById(id);
            slider.value = value;
            document.getElementById(id + '-value').innerHTML = value;
            if (id === 'slider-amplitude') {
                amplitude = parseFloat(value);
            } else if (id === 'slider-stiffness') {
                stiffness = parseFloat(value);
                angularFrequency = Math.sqrt(stiffness / mass);
            } else if (id === 'slider-mass') {
                mass = parseFloat(value);
                angularFrequency = Math.sqrt(stiffness / mass);
            }
            time = 0;
            positionHistory = [];
            isPlaying = false;
            document.getElementById('btn-play-pause').innerHTML = '▶';
            drawPlot();
            drawAnimation();
        }

        function playPause() {
            isPlaying = !isPlaying;
            if (isPlaying) {
                document.getElementById('btn-play-pause').innerHTML = '||';
                draw();
            } else {
                document.getElementById('btn-play-pause').innerHTML = '▶';
            }
        }

        function stepBack() {
            if (!isPlaying) {
                time -= 0.1;
                if (time < 0) {
                    time = 0;
                }
                let y = amplitude * sin(angularFrequency * time);
                positionHistory.push([time, y]);
                drawPlot();
                drawAnimation();
            }
        }

        function stepForward() {
            if (!isPlaying) {
                time += 0.1;
                let y = amplitude * sin(angularFrequency * time);
                positionHistory.push([time, y]);
                drawPlot();
                drawAnimation();
            }
        }

        function reset() {
            amplitude = 0.3;
            stiffness = 1.0;
            mass = 1.0;
            time = 0;
            isPlaying = false;
            angularFrequency = Math.sqrt(stiffness / mass);
            positionHistory = [];
            document.getElementById('slider-amplitude').value = '0.3';
            document.getElementById('slider-stiffness').value = '1.0';
            document.getElementById('slider-mass').value = '1.0';
            document.getElementById('amplitude-value').innerHTML = '0.3';
            document.getElementById('stiffness-value').innerHTML = '1.0';
            document.getElementById('mass-value').innerHTML = '1.0';
            document.getElementById('btn-play-pause').innerHTML = '▶';
            drawPlot();
            drawAnimation();
        }

        document.getElementById('slider-amplitude').addEventListener('input', function() {
            updateSliderValue('slider-amplitude', this.value);
        });
        document.getElementById('slider-stiffness').addEventListener('input', function() {
            updateSliderValue('slider-stiffness', this.value);
        });
        document.getElementById('slider-mass').addEventListener('input', function() {
            updateSliderValue('slider-mass', this.value);
        });
        document.getElementById('btn-play-pause').addEventListener('click', playPause);
        document.getElementById('btn-step-back').addEventListener('click', stepBack);
        document.getElementById('btn-step-forward').addEventListener('click', stepForward);
        document.getElementById('btn-reset').addEventListener('click', reset);
    </script>
</body>
</html>
