<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Distance Transform Demo</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
        }

        #control-panel {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }

        #distance-function {
            margin-right: 10px;
        }

        button {
            padding: 10px 20px;
            border: none;
            border-radius: 5px;
            cursor: pointer;
        }

        button:hover {
            background-color: #ccc;
        }

        button.active {
            background-color: #aaa;
        }

        #grid-container {
            display: grid;
            grid-template-columns: repeat(7, 1fr);
            grid-template-rows: repeat(9, 1fr);
            gap: 2px;
        }

        .cell {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 40px;
            height: 40px;
            border: 1px solid #ccc;
            cursor: pointer;
        }

        .cell-on {
            background-color: #FFD700;
        }

        .cell-off {
            background-color: #D3D3D3;
        }

        .distance-value {
            font-size: 12px;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <span id="distance-function">distance function</span>
        <button id="btn-manhattan" class="active">Manhattan</button>
        <button id="btn-euclidean">Squared Euclidean</button>
        <button id="btn-chebyshev">Chebyshev</button>
    </div>
    <div id="grid-container"></div>

    <script>
        // Initialize state variables
        let currentDistanceFunction = 'manhattan';
        let gridState = Array(9).fill(0).map(() => Array(7).fill(false));
        gridState[2][1] = true;
        gridState[6][4] = true;

        // Get references to the grid container and buttons
        const gridContainer = document.getElementById('grid-container');
        const manhattanButton = document.getElementById('btn-manhattan');
        const euclideanButton = document.getElementById('btn-euclidean');
        const chebyshevButton = document.getElementById('btn-chebyshev');

        // Function to calculate distance transform
        function calculateDistanceTransform() {
            const distances = Array(9).fill(0).map(() => Array(7).fill(Infinity));
            const sourcePoints = gridState.reduce((acc, row, r) => {
                row.forEach((val, c) => {
                    if (val) {
                        acc.push([r, c]);
                    }
                });
                return acc;
            }, []);

            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    for (const [sourceR, sourceC] of sourcePoints) {
                        let distance = 0;
                        switch (currentDistanceFunction) {
                            case 'manhattan':
                                distance = Math.abs(r - sourceR) + Math.abs(c - sourceC);
                                break;
                            case 'euclidean':
                                distance = Math.sqrt((r - sourceR) ** 2 + (c - sourceC) ** 2);
                                break;
                            case 'chebyshev':
                                distance = Math.max(Math.abs(r - sourceR), Math.abs(c - sourceC));
                                break;
                        }
                        distances[r][c] = Math.min(distances[r][c], distance);
                    }
                }
            }

            return distances;
        }

        // Function to update grid display
        function updateGridDisplay(distances) {
            gridContainer.innerHTML = '';
            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    const cell = document.createElement('div');
                    cell.classList.add('cell');
                    if (gridState[r][c]) {
                        cell.classList.add('cell-on');
                    } else {
                        cell.classList.add('cell-off');
                    }
                    cell.addEventListener('click', () => {
                        gridState[r][c] = !gridState[r][c];
                        const newDistances = calculateDistanceTransform();
                        updateGridDisplay(newDistances);
                    });
                    const distanceValue = document.createElement('span');
                    distanceValue.classList.add('distance-value');
                    distanceValue.textContent = distances[r][c].toFixed(2);
                    cell.appendChild(distanceValue);
                    gridContainer.appendChild(cell);
                }
            }
        }

        // Function to handle button clicks
        function handleButtonClick(event) {
            const button = event.target;
            if (button === manhattanButton) {
                currentDistanceFunction = 'manhattan';
                manhattanButton.classList.add('active');
                euclideanButton.classList.remove('active');
                chebyshevButton.classList.remove('active');
            } else if (button === euclideanButton) {
                currentDistanceFunction = 'euclidean';
                manhattanButton.classList.remove('active');
                euclideanButton.classList.add('active');
                chebyshevButton.classList.remove('active');
            } else if (button === chebyshevButton) {
                currentDistanceFunction = 'chebyshev';
                manhattanButton.classList.remove('active');
                euclideanButton.classList.remove('active');
                chebyshevButton.classList.add('active');
            }
            const newDistances = calculateDistanceTransform();
            updateGridDisplay(newDistances);
        }

        // Add event listeners to buttons
        manhattanButton.addEventListener('click', handleButtonClick);
        euclideanButton.addEventListener('click', handleButtonClick);
        chebyshevButton.addEventListener('click', handleButtonClick);

        // Initialize grid display
        updateGridDisplay(calculateDistanceTransform());
    </script>
</body>
</html>
