    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        // Initial setup
        const btnModelEp = document.getElementById('btn-model-ep');
        const btnModelAr = document.getElementById('btn-model-ar');
        const btnTmaxLow = document.getElementById('btn-tmax-low');
        const btnTmaxHigh = document.getElementById('btn-tmax-high');
        const sliderN = document.getElementById('slider-n');
        const sliderTmin = document.getElementById('slider-tmin');
        const sliderTmax = document.getElementById('slider-tmax');
        const sliderTref = document.getElementById('slider-tref');
        const sliderParam = document.getElementById('slider-param');
        const labelN = document.getElementById('label-n');
        const labelTmin = document.getElementById('label-tmin');
        const labelTmax = document.getElementById('label-tmax');
        const labelTref = document.getElementById('label-tref');
        const labelParamValue = document.getElementById('label-param-value');
        const fitResults = document.getElementById('fit-results');
        const plotContainer = document.getElementById('plot-container');

        // Initial state
        update();

        // Event listeners
        sliderN.addEventListener('input', update);
        sliderTmin.addEventListener('input', update);
        sliderTmax.addEventListener('input', update);
        sliderTref.addEventListener('input', update);
        sliderParam.addEventListener('input', update);
        btnModelEp.addEventListener('click', update);
        btnModelAr.addEventListener('click', update);
        btnTmaxLow.addEventListener('click', update);
        btnTmaxHigh.addEventListener('click', update);

        // Update function
        function update() {
            // Button logic
            btnModelEp.classList.toggle('active', btnModelEp === document.activeElement);
            btnModelAr.classList.toggle('active', btnModelAr === document.activeElement);
            btnTmaxLow.classList.toggle('active', btnTmaxLow === document.activeElement);
            btnTmaxHigh.classList.toggle('active', btnTmaxHigh === document.activeElement);

            // Slider constraint logic
            if (btnTmaxLow.classList.contains('active')) {
                sliderTmax.max = 1200;
            } else {
                sliderTmax.max = 6000;
            }

            // Ensure Tmin <= Tmax
            if (sliderTmin.value > sliderTmax.value) {
                sliderTmin.value = sliderTmax.value;
            }

            // Ensure Tmin <= Tref <= Tmax
            if (sliderTmin.value > sliderTref.value) {
                sliderTref.value = sliderTmin.value;
            }
            if (sliderTref.value > sliderTmax.value) {
                sliderTref.value = sliderTmax.value;
            }

            // Dynamic label logic
            const labelParamName = document.getElementById('label-param-name');
            if (btnModelEp.classList.contains('active')) {
                labelParamName.textContent = '\(a_{EP}\)';
            } else {
                labelParamName.textContent = '\(a_{Ar}\)';
            }
            MathJax.typeset();

            // Update value displays
            labelN.textContent = sliderN.value;
            labelTmin.textContent = sliderTmin.value;
            labelTmax.textContent = sliderTmax.value;
            labelTref.textContent = sliderTref.value;
            labelParamValue.textContent = sliderParam.value;

            // Data generation
            const n = parseInt(sliderN.value);
            const Tmin = parseInt(sliderTmin.value);
            const Tmax = parseInt(sliderTmax.value);
            const Tref = parseInt(sliderTref.value);
            const param = parseInt(sliderParam.value);
            const T = [];
            const y = [];
            for (let i = 0; i < n; i++) {
                const t = Tmin + (Tmax - Tmin) * i / (n - 1);
                T.push(t);
                if (btnModelEp.classList.contains('active')) {
                    y.push((t + 273.15) / Tref + Math.exp(param * (1 / Tref - 1 / (t + 273.15))));
                } else {
                    y.push(Math.exp(param * (1 / Tref - 1 / (t + 273.15))));
                }
            }

            // Data fitting
            let aFit;
            if (btnModelEp.classList.contains('active')) {
                aFit = fitEP(T, y);
            } else {
                aFit = fitAr(T, y);
            }

            // Display update
            const results = `\(a_{Ar} = ${aFit.aFit}.\) \(r^2 = ${aFit.r2.toFixed(3)}\>`;
            fitResults.innerHTML = results;
            MathJax.typeset();

            // Plot redraw
            drawPlot(T, y, aFit);
        }

        // Fit functions
        function fitEP(T, y) {
            let aFit;
            let r2;
            // Perform grid search to find best-fit parameter
            for (let i = 0; i <= 10000; i++) {
                const param = i / 100;
                const yFit = [];
                for (let j = 0; j < T.length; j++) {
                    yFit.push((T[j] + 273.15) / Tref + Math.exp(param * (1 / Tref - 1 / (T[j] + 273.15))));
                }
                const rss = y.reduce((acc, val, index) => acc + Math.pow(val - yFit[index], 2), 0);
                if (!aFit || rss < aFit.rss) {
                    aFit = { param, rss, r2: 1 - (rss / y.reduce((acc, val) => acc + Math.pow(val, 2), 0)) };
                }
            }
            return aFit;
        }

        function fitAr(T, y) {
            let aFit;
            let r2;
            // Perform grid search to find best-fit parameter
            for (let i = 0; i <= 10000; i++) {
                const param = i / 100;
                const yFit = [];
                for (let j = 0; j < T.length; j++) {
                    yFit.push(Math.exp(param * (1 / Tref - 1 / (T[j] + 273.15))));
                }
                const rss = y.reduce((acc, val, index) => acc + Math.pow(val - yFit[index], 2), 0);
                if (!aFit || rss < aFit.rss) {
                    aFit = { param, rss, r2: 1 - (rss / y.reduce((acc, val) => acc + Math.pow(val, 2), 0)) };
                }
            }
            return aFit;
        }

        // Plot function
        function drawPlot(T, y, aFit) {
            const layout = {
                title: `Arrhenius model fit to Eyring-Polanyi data`,
                xaxis: { title: 'T °C' },
                yaxis: { title: 'k(T) / k(T_{ref})' },
            };
            const trace1 = {
                x: T,
                y: y,
                mode: 'markers',
                marker: { color: 'blue', opacity: 0.5, symbol: 'circle', line: { color: 'blue', width: 1 } },
                type: 'scatter',
            };
            const trace2 = {
                x: T,
                y: aFit.yFit,
                mode: 'lines',
                line: { color: 'green', width: 2 },
                type: 'scatter',
            };
            Plotly.newPlot('plot-container', [trace1, trace2], layout);
        }
    </script>
</body>
</html>
