<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Polygon Dissection Visualization</title>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
        }

        #controls-container {
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 10px;
            padding: 10px;
            border: 1px solid #ccc;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        #controls-container button {
            margin: 5px;
            padding: 10px 20px;
            border: none;
            border-radius: 5px;
            background-color: #f0f0f0;
            cursor: pointer;
        }

        #controls-container button:hover {
            background-color: #e0e0e0;
        }

        #controls-container button.active {
            background-color: #e0e0e0;
        }

        #canvas-container {
            width: 80%;
            height: 600px;
            margin: 20px auto;
            border: 1px solid #ccc;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        #n-display {
            font-size: 24px;
            font-weight: bold;
            margin-bottom: 10px;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <span id="n-display">6</span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>
    <script>
        let n = 6;
        let colors = ['#fefd00', '#ee1f24', '#0048a1'];

        function drawScene() {
            background(255);
            noStroke();

            // Draw polygon (left side)
            let R = 150;
            let cx = width / 4;
            let cy = height / 2;
            let angleStep = PI / n;
            for (let j = 1; j <= 2 * n; j++) {
                let colorIndex = (j - 1) % 3;
                let color = colors[colorIndex];
                let p1 = {
                    x: cx + R * cos(j * angleStep - PI / 2),
                    y: cy + R * sin(j * angleStep - PI / 2)
                };
                let p2 = {
                    x: cx + R * cos((j - 1) * angleStep - PI / 2),
                    y: cy + R * sin((j - 1) * angleStep - PI / 2)
                };
                let p3 = {
                    x: cx,
                    y: cy
                };
                fill(color);
                triangle(p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);
            }

            // Draw rectangles (right side)
            let startX = width / 2;
            let startY = height / 2;
            let s = 2 * R * sin(angleStep / 2);
            let a = R * cos(angleStep / 2);
            for (let k = 1; k <= floor(n / 2); k++) {
                let x = startX + (k - 1) * (s + 20);
                let y = startY;
                let w = s;
                let h = 2 * a;
                fill('#fefd00', '#ee1f24', '#0048a1', '#fefd00');
                rect(x, y, w, h);
                fill(0);
                rect(x + 5, y + 5, w - 10, h - 10);
            }

            // Draw half-rectangle (if n is odd)
            if (n % 2 !== 0) {
                let x = startX + floor(n / 2) * (s + 20);
                let y = startY;
                let w = s;
                let h = a * 2;
                fill('#0048a1', '#ee1f24');
                rect(x, y, w, h);
                fill(0);
                rect(x + 5, y + 5, w - 10, h - 10);
            }
        }

        function setup() {
            createCanvas(800, 600);
            background(255);
            drawScene();
        }

        function draw() {
            drawScene();
        }

        function mousePressed() {
            drawScene();
        }

        function changeN(newN) {
            n = newN;
            document.getElementById('n-display').textContent = n;
            let buttons = document.querySelectorAll('#controls-container button');
            buttons.forEach(button => button.classList.remove('active'));
            document.getElementById(`btn-n-${n}`).classList.add('active');
            drawScene();
        }

        document.querySelectorAll('#controls-container button').forEach(button => {
            button.addEventListener('click', () => {
                let newN = parseInt(button.id.split('-')[1]);
                changeN(newN);
            });
        });

        changeN(6);
        setup();
    </script>
</body>
</html>
