<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Robot Workspace and Phase Space Visualizer</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-color: #f0f0f0;
        }
        #controls-container {
            display: flex;
            justify-content: space-between;
            padding: 10px;
            background-color: #fff;
            border: 1px solid #ddd;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }
        .slider-group {
            display: flex;
            align-items: center;
            margin: 10px;
        }
        label {
            margin-right: 10px;
        }
        input[type="range"] {
            width: 80%;
        }
        #btn-reset {
            position: absolute;
            top: 10px;
            right: 10px;
            background-color: #f00;
            color: #fff;
            border: none;
            padding: 5px 10px;
            font-size: 16px;
            cursor: pointer;
        }
        #main-container {
            display: flex;
            justify-content: space-between;
            padding: 20px;
        }
        #workspace-panel {
            width: 40%;
            height: 400px;
            background-color: #f0f0f0;
            border: 1px solid #ddd;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }
        #phase-space-panel {
            width: 40%;
            height: 400px;
            background-color: #f0f0f0;
            border: 1px solid #ddd;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }
        #canvas-workspace, #canvas-phase-space {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <div id="controls-container">
        <div class="slider-group">
            <label for="slider-q">Q</label>
            <input type="range" id="slider-q" min="1" max="7" step="1" value="2">
            <span id="value-q"></span>
        </div>
        <div class="slider-group">
            <label for="slider-p">P</label>
            <input type="range" id="slider-p" min="0" max="1" step="0.01" value="1">
            <span id="value-p"></span>
        </div>
        <button id="btn-reset">x</button>
    </div>
    <div id="main-container">
        <div id="workspace-panel">
            <h3>robot workspace</h3>
            <div id="canvas-workspace"></div>
        </div>
        <div id="phase-space-panel">
            <h3 id="phase-space-title">robot phase space, path length = 0.00 rads</h3>
            <div id="canvas-phase-space"></div>
        </div>
    </div>

    <script>
        let Q = 2;
        let P = 1;
        let isDraggingObstacle1 = false;
        let isDraggingObstacle2 = false;
        let isDraggingStart = false;
        let isDraggingGoal = false;

        let startConfig = { q1: -1.5, q2: 0.5 };
        let goalConfig = { q1: 1.5, q2: 1.0 };
        let obstacles = [
            { x: 200, y: 100, radius: 15 },
            { x: 200, y: 300, radius: 15 }
        ];
        let grid = [];
        let distanceField = [];
        let path = [];

        let robotArm = {
            baseX: 200,
            baseY: 200,
            link1Length: 75,
            link2Length: 50,
            linkWidth: 10
        };

        let canvasWorkspace;
        let canvasPhaseSpace;

        function setup() {
            canvasWorkspace = createCanvas(400, 400);
            canvasWorkspace.parent('canvas-workspace');
            canvasPhaseSpace = createCanvas(400, 400);
            canvasPhaseSpace.parent('canvas-phase-space');

            noStroke();
            rectMode(CENTER);
            ellipseMode(CENTER);
            textSize(16);
            textAlign(CENTER, CENTER);

            generateGrid();
            calculateCObstacles();
            runBFS();
            findShortestPath();
            renderCanvases();
        }

        function draw() {
            background(220);
            renderCanvases();
        }

        function generateGrid() {
            let N = 10 * 2 ** Q;
            let cellSize = width / N;
            for (let i = 0; i < N; i++) {
                let row = [];
                for (let j = 0; j < N; j++) {
                    row.push(false);
                }
                grid.push(row);
            }
            for (let i = 0; i < obstacles.length; i++) {
                let x = Math.floor(obstacles[i].x / cellSize);
                let y = Math.floor(obstacles[i].y / cellSize);
                grid[x][y] = true;
            }
        }

        function calculateCObstacles() {
            let obstacles = [];
            for (let i = 0; i < Q; i++) {
                let x = 0;
                let y = 0;
                for (let j = 0; j < 2 ** Q; j++) {
                    let q1 = (j >> i) & 1;
                    let q2 = (j >> (i + 1)) & 1;
                    let distance = calculateDistance(q1, q2);
                    let x1 = x + distance * Math.cos(q1);
                    let y1 = y + distance * Math.sin(q1);
                    let x2 = x1 + distance * Math.cos(q2);
                    let y2 = y1 + distance * Math.sin(q2);
                    obstacles.push({ x: x2, y: y2 });
                    x = x2;
                    y = y2;
                }
            }
            for (let i = 0; i < obstacles.length; i++) {
                let x = Math.floor(obstacles[i].x / (width / (2 ** Q)));
                let y = Math.floor(obstacles[i].y / (height / (2 ** Q)));
                grid[x][y] = true;
            }
        }

        function calculateDistance(q1, q2) {
            return sqrt(pow(robotArm.link1Length, 2) + pow(robotArm.link2Length, 2) - 2 * robotArm.link1Length * robotArm.link2Length * cos(q1 - q2));
        }

        function runBFS() {
            let queue = [];
            queue.push(goalConfig);
            let visited = [];
            while (queue.length > 0) {
                let node = queue.shift();
                if (node.q1 === startConfig.q1 && node.q2 === startConfig.q2) {
                    break;
                }
                visited.push(node);
                for (let i = 0; i < 2 ** Q; i++) {
                    let q1 = (i >> 0) & 1;
                    let q2 = (i >> 1) & 1;
                    let neighbor = { q1: node.q1 + q1 * 0.1, q2: node.q2 + q2 * 0.1 };
                    let x = Math.floor(neighbor.q1 / 0.1);
                    let y = Math.floor(neighbor.q2 / 0.1);
                    if (!visited.includes(neighbor) && !grid[x][y]) {
                        queue.push(neighbor);
                    }
                }
            }
            for (let i = 0; i < queue.length; i++) {
                let node = queue[i];
                distanceField[node.q1 * (2 ** Q) + node.q2] = i;
            }
        }

        function findShortestPath() {
            let queue = [];
            queue.push(goalConfig);
            let visited = [];
            while (queue.length > 0) {
                let node = queue.shift();
                if (node.q1 === startConfig.q1 && node.q2 === startConfig.q2) {
                    break;
                }
                visited.push(node);
                for (let i = 0; i < 2 ** Q; i++) {
                    let q1 = (i >> 0) & 1;
                    let q2 = (i >> 1) & 1;
                    let neighbor = { q1: node.q1 + q1 * 0.1, q2: node.q2 + q2 * 0.1 };
                    let x = Math.floor(neighbor.q1 / 0.1);
                    let y = Math.floor(neighbor.q2 / 0.1);
                    if (!visited.includes(neighbor) && !grid[x][y]) {
                        queue.push(neighbor);
                    }
                }
            }
            path = [];
            let currentNode = queue[0];
            while (currentNode.q1 !== startConfig.q1 || currentNode.q2 !== startConfig.q2) {
                path.unshift(currentNode);
                let index = distanceField[currentNode.q1 * (2 ** Q) + currentNode.q2];
                currentNode = queue[index];
            }
        }

        function renderCanvases() {
            background(220);
            canvasWorkspace.parent('canvas-workspace');
            canvasPhaseSpace.parent('canvas-phase-space');
            renderWorkspace();
            renderPhaseSpace();
        }

        function renderWorkspace() {
            background(220);
            fill(128);
            rect(200, 200, 400, 400);
            fill(0);
            for (let i = 0; i < obstacles.length; i++) {
                ellipse(obstacles[i].x, obstacles[i].y, obstacles[i].radius * 2);
            }
            let theta1 = startConfig.q1 + (P - 0) * (goalConfig.q1 - startConfig.q1);
            let theta2 = startConfig.q2 + (P - 0) * (goalConfig.q2 - startConfig.q2);
            push();
            translate(200, 200);
            rotate(theta1);
            rect(0, 0, robotArm.link1Length, robotArm.link1Length);
            pop();
            push();
            translate(200 + robotArm.link1Length, 200);
            rotate(theta2);
            rect(0, 0, robotArm.link2Length, robotArm.link2Length);
            pop();
            fill(255);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('Robot Arm', 200 + robotArm.link1Length + robotArm.link2Length, 200);
        }

        function renderPhaseSpace() {
            background(220);
            let cellSize = width / (2 ** Q);
            for (let i = 0; i < grid.length; i++) {
                for (let j = 0; j < grid[i].length; j++) {
                    if (grid[i][j]) {
                        fill(255, 0, 0);
                    } else {
                        let distance = distanceField[i * (2 ** Q) + j];
                        if (distance !== undefined) {
                            let gray = map(distance, 0, queue.length - 1, 0, 255);
                            fill(gray, gray, gray);
                        } else {
                            fill(128);
                        }
                    }
                    rect(i * cellSize + cellSize / 2, j * cellSize + cellSize / 2, cellSize, cellSize);
                }
            }
            for (let i = 0; i < path.length; i++) {
                let x = Math.floor(path[i].q1 / 0.1);
                let y = Math.floor(path[i].q2 / 0.1);
                let index = i;
                if (i === path.length - 1) {
                    index = queue.indexOf(path[i]);
                }
                fill(0, 255, 0);
                rect(x * cellSize + cellSize / 2, y * cellSize + cellSize / 2, cellSize, cellSize);
                if (i > 0) {
                    let x1 = Math.floor(path[i - 1].q1 / 0.1);
                    let y1 = Math.floor(path[i - 1].q2 / 0.1);
                    let x2 = Math.floor(path[i].q1 / 0.1);
                    let y2 = Math.floor(path[i].q2 / 0.1);
                    stroke(0, 255, 0);
                    line(x1 * cellSize + cellSize / 2, y1 * cellSize + cellSize / 2, x2 * cellSize + cellSize / 2, y2 * cellSize + cellSize / 2);
                }
            }
            let x = Math.floor(startConfig.q1 / 0.1);
            let y = Math.floor(startConfig.q2 / 0.1);
            fill(0, 255, 0);
            ellipse(x * cellSize + cellSize / 2, y * cellSize + cellSize / 2, cellSize / 2);
            x = Math.floor(goalConfig.q1 / 0.1);
            y = Math.floor(goalConfig.q2 / 0.1);
            fill(0, 255, 0);
            ellipse(x * cellSize + cellSize / 2, y * cellSize + cellSize / 2, cellSize / 2);
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('robot phase space, path length = ' + path.length * 0.1 + ' rads', width / 2, 20);
        }

        canvasWorkspace.mousePressed(() => {
            for (let i = 0; i < obstacles.length; i++) {
                let x = Math.floor(obstacles[i].x / (width / (2 ** Q)));
                let y = Math.floor(obstacles[i].y / (height / (2 ** Q)));
                if (dist(mouseX, mouseY, x * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, y * (height / (2 ** Q)) + (height / (2 ** Q)) / 2) < (width / (2 ** Q)) / 2) {
                    isDraggingObstacle1 = true;
                    isDraggingObstacle2 = false;
                    isDraggingStart = false;
                    isDraggingGoal = false;
                    return;
                }
            }
            let x = Math.floor(startConfig.q1 / 0.1);
            let y = Math.floor(startConfig.q2 / 0.1);
            if (dist(mouseX, mouseY, x * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, y * (height / (2 ** Q)) + (height / (2 ** Q)) / 2) < (width / (2 ** Q)) / 2) {
                isDraggingStart = true;
                isDraggingObstacle1 = false;
                isDraggingObstacle2 = false;
                isDraggingGoal = false;
                return;
            }
            let x = Math.floor(goalConfig.q1 / 0.1);
            let y = Math.floor(goalConfig.q2 / 0.1);
            if (dist(mouseX, mouseY, x * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, y * (height / (2 ** Q)) + (height / (2 ** Q)) / 2) < (width / (2 ** Q)) / 2) {
                isDraggingGoal = true;
                isDraggingObstacle1 = false;
                isDraggingObstacle2 = false;
                isDraggingStart = false;
                return;
            }
        });

        canvasWorkspace.mouseDragged(() => {
            if (isDraggingObstacle1) {
                obstacles[0].x = mouseX;
                obstacles[0].y = mouseY;
                generateGrid();
                calculateCObstacles();
                runBFS();
                findShortestPath();
                renderCanvases();
            } else if (isDraggingObstacle2) {
                obstacles[1].x = mouseX;
                obstacles[1].y = mouseY;
                generateGrid();
                calculateCObstacles();
                runBFS();
                findShortestPath();
                renderCanvases();
            } else if (isDraggingStart) {
                startConfig.q1 = (mouseX - (width / (2 ** Q)) / 2) / (width / (2 ** Q));
                startConfig.q2 = (mouseY - (height / (2 ** Q)) / 2) / (height / (2 ** Q));
                generateGrid();
                calculateCObstacles();
                runBFS();
                findShortestPath();
                renderCanvases();
            } else if (isDraggingGoal) {
                goalConfig.q1 = (mouseX - (width / (2 ** Q)) / 2) / (width / (2 ** Q));
                goalConfig.q2 = (mouseY - (height / (2 ** Q)) / 2) / (height / (2 ** Q));
                generateGrid();
                calculateCObstacles();
                runBFS();
                findShortestPath();
                renderCanvases();
            }
        });

        canvasWorkspace.mouseReleased(() => {
            isDraggingObstacle1 = false;
            isDraggingObstacle2 = false;
            isDraggingStart = false;
            isDraggingGoal = false;
        });

        canvasPhaseSpace.mousePressed(() => {
            let x = Math.floor(startConfig.q1 / 0.1);
            let y = Math.floor(startConfig.q2 / 0.1);
            if (dist(mouseX, mouseY, x * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, y * (height / (2 ** Q)) + (height / (2 ** Q)) / 2) < (width / (2 ** Q)) / 2) {
                isDraggingStart = true;
                isDraggingObstacle1 = false;
                isDraggingObstacle2 = false;
                isDraggingGoal = false;
                return;
            }
            let x = Math.floor(goalConfig.q1 / 0.1);
            let y = Math.floor(goalConfig.q2 / 0.1);
            if (dist(mouseX, mouseY, x * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, y * (height / (2 ** Q)) + (height / (2 ** Q)) / 2) < (width / (2 ** Q)) / 2) {
                isDraggingGoal = true;
                isDraggingObstacle1 = false;
                isDraggingObstacle2 = false;
                isDraggingStart = false;
                return;
            }
        });

        canvasPhaseSpace.mouseDragged(() => {
            if (isDraggingStart) {
                startConfig.q1 = (mouseX - (width / (2 ** Q)) / 2) / (width / (2 ** Q));
                startConfig.q2 = (mouseY - (height / (2 ** Q)) / 2) / (height / (2 ** Q));
                if (grid[Math.floor(startConfig.q1 / 0.1)][Math.floor(startConfig.q2 / 0.1)]) {
                    fill(255, 0, 0);
                    rect(Math.floor(startConfig.q1 / 0.1) * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, Math.floor(startConfig.q2 / 0.1) * (height / (2 ** Q)) + (height / (2 ** Q)) / 2, (width / (2 ** Q)) / 2, (height / (2 ** Q)) / 2);
                } else {
                    fill(0, 255, 0);
                    rect(Math.floor(startConfig.q1 / 0.1) * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, Math.floor(startConfig.q2 / 0.1) * (height / (2 ** Q)) + (height / (2 ** Q)) / 2, (width / (2 ** Q)) / 2, (height / (2 ** Q)) / 2);
                }
                generateGrid();
                calculateCObstacles();
                runBFS();
                findShortestPath();
                renderCanvases();
            } else if (isDraggingGoal) {
                goalConfig.q1 = (mouseX - (width / (2 ** Q)) / 2) / (width / (2 ** Q));
                goalConfig.q2 = (mouseY - (height / (2 ** Q)) / 2) / (height / (2 ** Q));
                if (grid[Math.floor(goalConfig.q1 / 0.1)][Math.floor(goalConfig.q2 / 0.1)]) {
                    fill(255, 0, 0);
                    rect(Math.floor(goalConfig.q1 / 0.1) * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, Math.floor(goalConfig.q2 / 0.1) * (height / (2 ** Q)) + (height / (2 ** Q)) / 2, (width / (2 ** Q)) / 2, (height / (2 ** Q)) / 2);
                } else {
                    fill(0, 255, 0);
                    rect(Math.floor(goalConfig.q1 / 0.1) * (width / (2 ** Q)) + (width / (2 ** Q)) / 2, Math.floor(goalConfig.q2 / 0.1) * (height / (2 ** Q)) + (height / (2 ** Q)) / 2, (width / (2 ** Q)) / 2, (height / (2 ** Q)) / 2);
                }
                generateGrid();
                calculateCObstacles();
                runBFS();
                findShortestPath();
                renderCanvases();
            }
        });

        canvasPhaseSpace.mouseReleased(() => {
            isDraggingStart = false;
            isDraggingGoal = false;
        });

        document.getElementById('slider-q').addEventListener('input', () => {
            Q = parseInt(document.getElementById('slider-q').value);
            generateGrid();
            calculateCObstacles();
            runBFS();
            findShortestPath();
            renderCanvases();
            document.getElementById('value-q').innerHTML = 'Q = ' + Q;
        });

        document.getElementById('slider-p').addEventListener('input', () => {
            P = parseFloat(document.getElementById('slider-p').value);
            renderCanvases();
            document.getElementById('value-p').innerHTML = 'P = ' + P.toFixed(2);
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            Q = 2;
            P = 1;
            isDraggingObstacle1 = false;
            isDraggingObstacle2 = false;
            isDraggingStart = false;
            isDraggingGoal = false;
            startConfig = { q1: -1.5, q2: 0.5 };
            goalConfig = { q1: 1.5, q2: 1.0 };
            obstacles = [
                { x: 200, y: 100, radius: 15 },
                { x: 200, y: 300, radius: 15 }
            ];
            generateGrid();
            calculateCObstacles();
            runBFS();
            findShortestPath();
            renderCanvases();
        });

        setup();
    </script>
</body>
</html>
