<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Minimum Spanning Tree Visualization</title>
    <style>
        #main-container {
            display: flex;
            flex-direction: row;
            height: 100vh;
            width: 100vw;
        }

        #control-panel {
            width: 30%;
            height: 100vh;
            padding: 20px;
            border-right: 1px solid #ccc;
        }

        #canvas-container {
            width: 70%;
            height: 100vh;
            padding: 20px;
        }

        #problem-group {
            margin-bottom: 20px;
        }

        #method-group {
            margin-bottom: 20px;
        }

        #process-group {
            margin-bottom: 20px;
        }

        .button {
            padding: 10px 20px;
            border: none;
            border-radius: 5px;
            cursor: pointer;
        }

        .button.active {
            background-color: #4CAF50;
            color: #fff;
        }

        .button.inactive {
            background-color: #ccc;
            color: #666;
        }

        #slider-seed, #slider-number {
            width: 80%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="problem-group">
                <h4>Problem</h4>
                <div>
                    <label>Dimension:</label>
                    <button id="btn-2d" class="button active">2D</button>
                    <button id="btn-3d" class="button inactive">3D</button>
                </div>
                <div>
                    <label>Random Seed:</label>
                    <input type="range" id="slider-seed" min="0" max="1000" step="1" value="525">
                    <span id="seed-value">525</span>
                </div>
                <div>
                    <label>Number:</label>
                    <input type="range" id="slider-number" min="5" max="150" step="1" value="40">
                    <span id="number-value">40</span>
                </div>
            </div>
            <div id="method-group">
                <h4>Method</h4>
                <div>
                    <button id="btn-prim" class="button active">Prim</button>
                    <button id="btn-kruskal" class="button inactive">Kruskal</button>
                </div>
            </div>
            <div id="process-group">
                <h4>Process</h4>
                <div>
                    <button id="btn-play" class="button active">►</button>
                    <button id="btn-pause" class="button inactive">||</button>
                    <button id="btn-reset" class="button active">|◄</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <div id="canvas"></div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/dist/p5.js"></script>
    <script>
        let dimension = '2D';
        let algorithm = 'prim';
        let animationState = 'stopped';
        let seed = 525;
        let points = [];
        let mstEdges = [];
        let animationCounter = 0;
        let intervalId = null;

        p5.disableFriendlyErrorMessages;

        function setup() {
            createCanvas(800, 600);
            background(220);
            noStroke();
            frameRate(30);

            // Generate points on reset
            generatePoints();
        }

        function draw() {
            if (animationState === 'playing') {
                animationCounter++;
                if (animationCounter % 10 === 0) {
                    if (algorithm === 'prim') {
                        addEdgePrim();
                    } else if (algorithm === 'kruskal') {
                        addEdgeKruskal();
                    }
                    updateCanvas();
                }
            } else if (animationState === 'paused') {
                noLoop();
            } else if (animationState === 'stopped') {
                loop();
            }
        }

        function mousePressed() {
            if (dimension === '3D') {
                orbitControl();
            }
        }

        function generatePoints() {
            points = [];
            for (let i = 0; i < Number(document.getElementById('slider-number').value); i++) {
                let x, y;
                if (dimension === '2D') {
                    x = random(width);
                    y = random(height);
                } else {
                    x = random(-200, 200);
                    y = random(-200, 200);
                }
                points.push(createVector(x, y));
            }
        }

        function addEdgePrim() {
            let minEdge = null;
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    let edge = {
                        'a': i,
                        'b': j,
                        'weight': dist(points[i].x, points[i].y, points[j].x, points[j].y)
                    };
                    if (minEdge === null || edge.weight < minEdge.weight) {
                        minEdge = edge;
                    }
                }
            }
            mstEdges.push(minEdge);
            points[minEdge.a].z = points[minEdge.b].z = 1;
        }

        function addEdgeKruskal() {
            let edges = [];
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    let edge = {
                        'a': i,
                        'b': j,
                        'weight': dist(points[i].x, points[i].y, points[j].x, points[j].y)
                    };
                    edges.push(edge);
                }
            }
            edges.sort((a, b) => a.weight - b.weight);
            let mstEdge = edges[animationCounter];
            mstEdges.push(mstEdge);
            points[mstEdge.a].z = points[mstEdge.b].z = 1;
        }

        function updateCanvas() {
            background(220);
            for (let i = 0; i < points.length; i++) {
                if (points[i].z === 1) {
                    fill('blue');
                    ellipse(points[i].x, points[i].y, 10);
                } else {
                    fill('red');
                    ellipse(points[i].x, points[i].y, 5);
                }
            }
            for (let i = 0; i < mstEdges.length; i++) {
                stroke('red');
                strokeWeight(2);
                line(points[mstEdges[i].a].x, points[mstEdges[i].a].y, points[mstEdges[i].b].x, points[mstEdges[i].b].y);
            }
        }

        function startAnimation() {
            animationState = 'playing';
            intervalId = setInterval(() => {
                if (animationCounter >= 100) {
                    clearInterval(intervalId);
                    animationState = 'stopped';
                    animationCounter = 0;
                    mstEdges = [];
                    points.forEach(point => point.z = 0);
                }
            }, 100);
        }

        function pauseAnimation() {
            animationState = 'paused';
            clearInterval(intervalId);
        }

        function resetAnimation() {
            animationState = 'stopped';
            animationCounter = 0;
            mstEdges = [];
            points.forEach(point => point.z = 0);
            updateCanvas();
        }

        document.getElementById('btn-play').addEventListener('click', () => {
            startAnimation();
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            pauseAnimation();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetAnimation();
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            dimension = '2D';
            resetAnimation();
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            dimension = '3D';
            resetAnimation();
        });

        document.getElementById('slider-seed').addEventListener('input', () => {
            seed = Number(document.getElementById('slider-seed').value);
            generatePoints();
            resetAnimation();
        });

        document.getElementById('slider-number').addEventListener('input', () => {
            points = [];
            generatePoints();
            resetAnimation();
        });

        document.getElementById('btn-prim').addEventListener('click', () => {
            algorithm = 'prim';
            resetAnimation();
        });

        document.getElementById('btn-kruskal').addEventListener('click', () => {
            algorithm = 'kruskal';
            resetAnimation();
        });

        setup();
    </script>
</body>
</html>
