    <script>
        function updateDisplay() {
            const selectMethod = document.getElementById('select-method');
            const sliderNum1 = document.getElementById('slider-num1');
            const btnIntYes = document.getElementById('btn-int-yes');
            const btnIntNo = document.getElementById('btn-int-no');
            const sliderNum2 = document.getElementById('slider-num2');
            const num1Display = document.getElementById('num1');
            const num2Display = document.getElementById('num2');
            const estimationMethodDisplay = document.getElementById('estimation-method');
            const placeValuesMethodDisplay = document.getElementById('place-values-method');
            const resultDisplay = document.getElementById('result');

            const num1Value = parseFloat(sliderNum1.value);
            const num2Value = parseFloat(sliderNum2.value);

            const isInteger = btnIntYes.classList.contains('active');
            const step = isInteger ? 1 : 0.1;

            sliderNum2.step = step;

            if (isInteger) {
                sliderNum2.value = Math.round(num2Value);
                sliderNum2.dispatchEvent(new Event('input'));
            }

            btnIntYes.classList.toggle('active', isInteger);
            btnIntNo.classList.toggle('active', !isInteger);

            const num1AsInt = Math.floor(num1Value);
            const num2AsInt = Math.floor(num2Value);
            const num1Factor = Math.pow(10, num1Value.toString().split('.')[1].length);
            const num2Factor = Math.pow(10, num2Value.toString().split('.')[1].length);

            let estimationMethod = '';
            let placeValuesMethod = '';

            if (selectMethod.value === 'estimation') {
                estimationMethod = `start with: ${num1Value} × ${num2Value}\nconvert to: ${num1AsInt}. × ${num2AsInt}. = ${num1AsInt * num2AsInt}.\nestimate as: ${Math.round(num1Value)} × ${Math.round(num2Value)} = ${Math.round(num1Value * num2Value)}\nresult is: ${num1Value * num2Value}`;
            } else {
                placeValuesMethod = `start with: ${num1Value} × ${num2Value}\nrewrite as: (${num1AsInt}. ÷ ${num1Factor}) × (${num2AsInt}. ÷ ${num2Factor})\n= (${num1AsInt}. × ${num2AsInt}.) ÷ (${num1Factor} × ${num2Factor})\n= ${num1AsInt * num2AsInt} ÷ ${num1Factor * num2Factor}\n= ${num1Value * num2Value}`;
            }

            num1Display.textContent = num1Value;
            num2Display.textContent = num2Value;
            estimationMethodDisplay.textContent = estimationMethod;
            placeValuesMethodDisplay.textContent = placeValuesMethod;
            resultDisplay.textContent = num1Value * num2Value;
        }

        const selectMethod = document.getElementById('select-method');
        selectMethod.addEventListener('change', updateDisplay);

        const sliderNum1 = document.getElementById('slider-num1');
        sliderNum1.addEventListener('input', updateDisplay);

        const sliderNum2 = document.getElementById('slider-num2');
        sliderNum2.addEventListener('input', updateDisplay);

        const btnIntYes = document.getElementById('btn-int-yes');
        btnIntYes.addEventListener('click', () => {
            sliderNum2.step = 1;
            sliderNum2.value = Math.round(parseFloat(sliderNum2.value));
            sliderNum2.dispatchEvent(new Event('input'));
            btnIntYes.classList.add('active');
            btnIntNo.classList.remove('active');
            updateDisplay();
        });

        const btnIntNo = document.getElementById('btn-int-no');
        btnIntNo.addEventListener('click', () => {
            sliderNum2.step = 0.1;
            btnIntYes.classList.remove('active');
            btnIntNo.classList.add('active');
            updateDisplay();
        });

        updateDisplay();
    </script>
</body>
</html>
