    <script>
        // Get the canvas element
        const canvas = document.getElementById('canvas-material-bar');
        const ctx = canvas.getContext('2d');

        // Get the slider and button elements
        const slider = document.getElementById('slider-time');
        const btnReset = document.getElementById('btn-reset');
        const spanTimeValue = document.getElementById('span-time-value');

        // Set the initial time value
        let t = 9.8;

        // Function to draw the material bar
        function drawMaterialBar() {
            // Clear the canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw the glass block
            const glassGradient = ctx.createLinearGradient(0, 0, 120);
            glassGradient.addColorStop(0, '#FF0000');
            glassGradient.addColorStop(1, '#FFFFFF');
            ctx.fillStyle = glassGradient;
            ctx.fillRect(0, 0, 120, 80);

            // Draw the melted water
            ctx.fillStyle = '#00BFFF';
            ctx.fillRect(120, 0, 120 * t / 10, 80);

            // Draw the ice block
            ctx.fillStyle = '#003366';
            ctx.fillRect(120 + 120 * t / 10, 0, 480 - 120 * t / 10, 80);

            // Draw the axis
            ctx.strokeStyle = '#000';
            ctx.lineWidth = 1;
            ctx.beginPath();
            ctx.moveTo(120, 80);
            ctx.lineTo(120, 0);
            ctx.stroke();
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            ctx.textBaseline = 'top';
            ctx.fillStyle = '#000';
            ctx.fillText('0', 60, 10);
            ctx.fillText('5', 180, 10);
            ctx.fillText('10', 300, 10);
            ctx.fillText('15', 420, 10);
        }

        // Function to update the plot
        function updatePlot() {
            // Get the new time value
            t = parseFloat(slider.value);

            // Update the span time value
            spanTimeValue.textContent = t.toFixed(1);

            // Recalculate the Stefan distance
            const s = 0.155 * Math.sqrt(t);

            // Update the plotly chart
            const plotData = [
                {
                    x: Array.from({ length: 101 }, (_, i) => i),
                    y: Array.from({ length: 101 }, (_, i) => 0.155 * Math.sqrt(i)),
