                yaxis: { title: 'distance (mm)', range: [0, 1.6] }
            };
            Plotly.relayout('div-plot', {
                xaxis: { range: [0, t] },
                yaxis: { range: [0, s] }
            });
            Plotly.update('div-plot', { data: plotData, layout: layout });

            // Redraw the material bar
