            // Manually trigger the input event
            slider.dispatchEvent(new Event('input'));
        }

        // Initial update
        updatePlot();

        // Event listeners
        slider.addEventListener('input', updatePlot);
        btnReset.addEventListener('click', resetDemo);
    </script>
