<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Time Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-color: #f0f0f0;
        }
        #app-container {
            max-width: 800px;
            margin: 40px auto;
            padding: 20px;
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        #control-panel, #visualization-panel {
            background: white;
            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
            padding: 15px;
        }
        #clocks-container {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 20px;
        }
        #local-clock-group, #solar-clock-group {
            background: white;
            border: 1px solid black;
            padding: 2px 5px;
        }
        #local-time-digital, #solar-time-digital, #offset-display {
            background: white;
            border: 1px solid black;
            padding: 2px 5px;
        }
        #local-clock-canvas, #solar-clock-canvas {
            border: 1px solid black;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input id="date-slider" type="range" min="0" max="364" value="340" step="1">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>
        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper">
                        <canvas id="local-clock-canvas" width="200" height="200"></canvas>
                        <div id="local-time-digital">HH:MM:SS</div>
                    </div>
                </div>
                <div id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper">
                        <canvas id="solar-clock-canvas" width="200" height="200"></canvas>
                        <div id="solar-time-digital">HH:MM:SS</div>
                    </div>
                </div>
            </div>
        </div>
        <div id="offset-display">offset of solar time from local time: ... minutes</div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script>
        let longitude = 0;
        let latitude = 51.5;

        let localTime;
        let solarTime;

        let localTimeDigital;
        let solarTimeDigital;

        let offsetDisplay;

        let dateSlider;
        let todayButton;

        let localClockCanvas;
        let solarClockCanvas;

        function setup() {
            localTime = new Date();
            solarTime = new Date();

            localTimeDigital = document.getElementById('local-time-digital');
            solarTimeDigital = document.getElementById('solar-time-digital');
            offsetDisplay = document.getElementById('offset-display');

            dateSlider = document.getElementById('date-slider');
            todayButton = document.getElementById('today-button');

            localClockCanvas = document.getElementById('local-clock-canvas');
            solarClockCanvas = document.getElementById('solar-clock-canvas');

            updateDateDisplay();

            todayButton.addEventListener('click', todayButtonClicked);
            dateSlider.addEventListener('input', dateSliderChanged);

            setInterval(update, 1000);
        }

        function update() {
            localTime = new Date();
            solarTime = calculateSolarTime(localTime);
            updateClocks(localTime, solarTime);
            updateDigitalDisplays(localTime, solarTime);
            updateOffsetDisplay(localTime, solarTime);
        }

        function calculateSolarTime(localTime) {
            let d = dateSlider.value;
            let EoT = 9.87 * Math.sin(2 * (360 / 365.24) * (d - 81)) - 7.53 * Math.cos((360 / 365.24) * (d - 81)) - 1.5 * Math.sin((360 / 365.24) * (d - 81));
            let totalMinutesOffset = EoT + 4 * longitude - 60 * (localTime.getTimezoneOffset() / -60);
            let solarTimeMilliseconds = localTime.getTime() + totalMinutesOffset * 60000;
            return new Date(solarTimeMilliseconds);
        }

        function updateDateDisplay() {
            let date = new Date(dateSlider.value * 86400000 + localTime.getTime());
            let day = date.toLocaleString('default', { weekday: 'long' });
            let month = date.toLocaleString('default', { month: 'long' });
            let dayOfMonth = date.getDate();
            let dateDisplay = document.getElementById('date-display');
            dateDisplay.textContent = `${day}, ${month} ${dayOfMonth}`;
        }

        function todayButtonClicked() {
            let today = new Date();
            let day = today.toLocaleString('default', { weekday: 'long' });
            let month = today.toLocaleString('default', { month: 'long' });
            let dayOfMonth = today.getDate();
            let date = today.getTime();
            dateSlider.value = Math.floor(date / 86400000);
            updateDateDisplay();
        }

        function dateSliderChanged() {
            updateDateDisplay();
        }

        function updateClocks(localTime, solarTime) {
            let localAngle = localTime.getHours() * 30 + localTime.getMinutes() * 0.5 + localTime.getSeconds() * 0.008333;
            let solarAngle = solarTime.getHours() * 30 + solarTime.getMinutes() * 0.5 + solarTime.getSeconds() * 0.008333;
            let p5 = new p5((sketch) => {
                sketch.setup = function() {
                    sketch.createCanvas(localClockCanvas.width, localClockCanvas.height);
                    sketch.angleMode(sketch.DEGREES);
                };
                sketch.draw = function() {
                    sketch.background(255);
                    sketch.fill(0);
                    sketch.stroke(0);
                    sketch.strokeWeight(2);
                    sketch.noFill();
                    sketch.ellipse(localClockCanvas.width / 2, localClockCanvas.height / 2, 180, 180);
                    sketch.fill(128);
                    sketch.ellipse(localClockCanvas.width / 2, localClockCanvas.height / 2, 160, 160);
                    sketch.strokeWeight(1);
                    sketch.line(localClockCanvas.width / 2, localClockCanvas.height / 2, localClockCanvas.width / 2 + 100 * Math.sin(localAngle), localClockCanvas.height / 2 + 100 * Math.cos(localAngle));
                    sketch.line(localClockCanvas.width / 2, localClockCanvas.height / 2, localClockCanvas.width / 2 + 150 * Math.sin(solarAngle), localClockCanvas.height / 2 + 150 * Math.cos(solarAngle));
                    sketch.fill(255, 0, 0);
                    sketch.stroke(255, 0, 0);
                    sketch.strokeWeight(1);
                    sketch.line(localClockCanvas.width / 2, localClockCanvas.height / 2, localClockCanvas.width / 2 + 80 * Math.sin(localAngle), localClockCanvas.height / 2 + 80 * Math.cos(localAngle));
                };
            }, localClockCanvas);
            let p5Solar = new p5((sketch) => {
                sketch.setup = function() {
                    sketch.createCanvas(solarClockCanvas.width, solarClockCanvas.height);
                    sketch.angleMode(sketch.DEGREES);
                };
                sketch.draw = function() {
                    sketch.background(255);
                    sketch.fill(0);
                    sketch.stroke(0);
                    sketch.strokeWeight(2);
                    sketch.noFill();
                    sketch.ellipse(solarClockCanvas.width / 2, solarClockCanvas.height / 2, 180, 180);
                    sketch.fill(240, 255, 210);
                    sketch.ellipse(solarClockCanvas.width / 2, solarClockCanvas.height / 2, 160, 160);
                    sketch.strokeWeight(1);
                    sketch.line(solarClockCanvas.width / 2, solarClockCanvas.height / 2, solarClockCanvas.width / 2 + 100 * Math.sin(solarAngle), solarClockCanvas.height / 2 + 100 * Math.cos(solarAngle));
                    sketch.fill(0);
                    sketch.stroke(0);
                    sketch.strokeWeight(1);
                    sketch.line(solarClockCanvas.width / 2, solarClockCanvas.height / 2, solarClockCanvas.width / 2 + 150 * Math.sin(localAngle), solarClockCanvas.height / 2 + 150 * Math.cos(localAngle));
                    sketch.fill(255, 0, 0);
                    sketch.stroke(255, 0, 0);
                    sketch.strokeWeight(1);
                    sketch.line(solarClockCanvas.width / 2, solarClockCanvas.height / 2, solarClockCanvas.width / 2 + 80 * Math.sin(solarAngle), solarClockCanvas.height / 2 + 80 * Math.cos(solarAngle));
                };
            }, solarClockCanvas);
        }

        function updateDigitalDisplays(localTime, solarTime) {
            localTimeDigital.textContent = `${localTime.getHours().toString().padStart(2, '0')}:${localTime.getMinutes().toString().padStart(2, '0')}:${localTime.getSeconds().toString().padStart(2, '0')}`;
            solarTimeDigital.textContent = `${solarTime.getHours().toString().padStart(2, '0')}:${solarTime.getMinutes().toString().padStart(2, '0')}:${solarTime.getSeconds().toString().padStart(2, '0')}`;
        }

        function updateOffsetDisplay(localTime, solarTime) {
            let offset = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
            offsetDisplay.textContent = `offset of solar time from local time: ${offset} minutes`;
        }

        setup();
    </script>
