    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" value="2">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>
    <script>
        let palette = {
            "DarkRainbow": ['#1E4363', '#2E6341'],
            "SiennaTones": ['#A0522D', '#CD853F', '#D2B48C'],
            "RustTones": ['#B7410E', '#000033', '#FFFFFF'],
            "FuchsiaTones": ['#C71585', '#000000', '#FFFFFF'],
            "Grays": ['#404040', '#808080', '#C0C0C0']
        };

        let zoom = false;
        let steps = 2;

        function drawTiling(level, x, y, size, angle) {
            if (level == 0) {
                let colorIndex = Math.floor(angle / 120) % palette[document.getElementById('select-color').value].length;
                let c = palette[document.getElementById('select-color').value][colorIndex];
                fill(c);
                noStroke();
                drawRhombus(x, y, size, angle);
                stroke(255);
                strokeWeight(1);
                line(x + size * cos(angle + 60), y + size * sin(angle + 60), x + size * cos(angle - 60), y + size * sin(angle - 60));
            } else {
                let scaleFactor = 1 / sqrt(7);
                let angleOffset = atan(sqrt(3) / 5);
                for (let i = 0; i < 7; i++) {
                    let childAngle = angle + i * 60;
                    let childSize = size * scaleFactor;
                    let childX = x + size * cos(angle + 30) * scaleFactor;
                    let childY = y + size * sin(angle + 30) * scaleFactor;
                    push();
                    translate(childX, childY);
                    rotate(childAngle);
                    drawTiling(level - 1, 0, 0, childSize, 0);
                    pop();
                }
            }
        }

        function drawRhombus(x, y, size, angle) {
            push();
            translate(x, y);
            rotate(angle);
            beginShape();
            vertex(-size, size);
            vertex(size, size);
            vertex(size, -size);
            vertex(-size, -size);
            endShape(CLOSE);
            pop();
        }

        function setup() {
            createCanvas(800, 600);
            angleMode(DEGREES);
            background(255);
            noLoop();
            let controls = document.querySelectorAll('#control-panel input, #control-panel select');
            controls.forEach(control => {
                control.addEventListener('input', redraw);
            });
            redraw();
        }

        function redraw() {
            background(255);
            let zoomScale = zoom ? pow(sqrt(7), steps) : 1;
            let centerX = width / 2;
            let centerY = height / 2;
            translate(centerX, centerY);
            scale(zoomScale);
            if (steps > 0) {
                drawTiling(steps, 0, 0, 100, 0);
            } else {
                let colorIndex = 0;
                let c = palette[document.getElementById('select-color').value][colorIndex];
                fill(c);
                noStroke();
                drawRhombus(0, 0, 100, 0);
                stroke(255);
                strokeWeight(1);
                line(100 * cos(0 + 60), 100 * sin(0 + 60), 100 * cos(0 - 60), 100 * sin(0 - 60));
            }
        }

        function draw() {
            redraw();
        }

        setup();
    </script>
</body>
</html>
