<body>
    <div id="demo-container">
        <div id="control-panel">
            <div id="orientation-controls-container">
                <span>Magnet Orientation:</span>
                <div id="magnet-control-0">
                    <span id="magnet-orientation-display-0">↓</span>
                    <button id="magnet-orientation-up-0">↑</button>
                    <button id="magnet-orientation-down-0">↓</button>
                </div>
                <div id="magnet-control-1">
                    <span id="magnet-orientation-display-1">↑</span>
                    <button id="magnet-orientation-up-1">↑</button>
                    <button id="magnet-orientation-down-1">↓</button>
                </div>
                <div id="magnet-control-2">
                    <span id="magnet-orientation-display-2">⊙</span>
                    <button id="magnet-orientation-up-2">↑</button>
                    <button id="magnet-orientation-down-2">↓</button>
                </div>
                <div id="magnet-control-3">
                    <span id="magnet-orientation-display-3">⊗</span>
                    <button id="magnet-orientation-up-3">↑</button>
                    <button id="magnet-orientation-down-3">↓</button>
                </div>
                <div id="magnet-control-4">
                    <span id="magnet-orientation-display-4">→</span>
                    <button id="magnet-orientation-up-4">↑</button>
                    <button id="magnet-orientation-down-4">↓</button>
                </div>
            </div>
            <div id="strength-control-container">
                <label for="slider-strength">Magnet Strength B (tesla):</label>
                <input type="range" id="slider-strength" min="1" max="10" value="5" step="1">
                <span id="slider-strength-value">5</span>
            </div>
            <div id="plot-type-controls-container">
                <button id="btn-stream-plot" class="active">Stream Plot</button>
                <button id="btn-density-plot">Density Plot</button>
            </div>
        </div>
