    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let magnetOrientations = [3, 2, 4, 5, 0];
        let plotType = 'stream';
        let sliderStrength = 5;

        function setup() {
            createCanvas(800, 600);
            background(220);
            frameRate(30);
        }

        function draw() {
            background(220);
            drawMagneticField();
            drawMagnetArray();
        }

        function drawMagneticField() {
            if (plotType === 'stream') {
                drawStreamPlot();
            } else if (plotType === 'density') {
                drawDensityPlot();
            }
        }

        function drawStreamPlot() {
            let gridX = 10;
            let gridY = 10;
            let step = 0.1;
            let maxSteps = 100;
            let fieldLines = [];

            for (let x = -4; x <= 4; x += gridX) {
                for (let y = -4; y <= 4; y += gridY) {
                    let fieldLine = [];
                    let direction = p5.Vector.fromAngle(radians(0));
                    let position = createVector(x, y);
                    let stepCount = 0;

                    while (stepCount < maxSteps) {
                        let field = calculateField(position.x, position.y);
                        fieldLine.push(position.copy());
                        direction.mult(0.1);
                        direction.add(field);
                        direction.normalize();
                        position.add(direction);
                        stepCount++;
                    }

                    fieldLines.push(fieldLine);
                }
            }

            for (let i = 0; i < fieldLines.length; i++) {
                beginShape();
                for (let j = 0; j < fieldLines[i].length; j++) {
                    vertex(fieldLines[i][j].x, fieldLines[i][j].y);
                }
                endShape();
            }

            for (let i = 0; i < fieldLines.length; i++) {
                let angle = atan2(fieldLines[i][fieldLines[i].length - 1].y - fieldLines[i][0].y, fieldLines[i][fieldLines[i].length - 1].x - fieldLines[i][0].x);
                let length = dist(fieldLines[i][fieldLines[i].length - 1].x, fieldLines[i][fieldLines[i].length - 1].y, fieldLines[i][0].x, fieldLines[i][0].y);
                let arrowSize = length / 10;
                let arrowTip = createVector(fieldLines[i][fieldLines[i].length - 1].x + cos(angle) * arrowSize, fieldLines[i][fieldLines[i].length - 1].y + sin(angle) * arrowSize);
                let arrowBase = createVector(fieldLines[i][fieldLines[i].length - 1].x + cos(angle) * arrowSize / 2, fieldLines[i][fieldLines[i].length - 1].y + sin(angle) * arrowSize / 2);
                line(fieldLines[i][fieldLines[i].length - 1].x, fieldLines[i][fieldLines[i].length - 1].y, arrowTip.x, arrowTip.y);
                line(arrowTip.x, arrowTip.y, arrowBase.x, arrowBase.y);
            }
        }

        function drawDensityPlot() {
            let gridX = 10;
            let gridY = 10;
            let step = 0.1;
            let maxSteps = 100;
            let fieldMagnitudes = [];

            for (let x = -4; x <= 4; x += gridX) {
                for (let y = -4; y <= 4; y += gridY) {
                    let magnitude = calculateFieldMagnitude(x, y);
                    fieldMagnitudes.push(magnitude);
                }
            }

            for (let i = 0; i < fieldMagnitudes.length; i++) {
                let x = i % gridX * gridX - 4;
                let y = Math.floor(i / gridX) * gridY - 4;
                let color = lerpColor(color(255, 255, 0), color(0, 0, 255), fieldMagnitudes[i] / 10);
                fill(color);
                noStroke();
                rect(x, y, gridX, gridY);
            }
        }

        function drawMagnetArray() {
            let magnetSize = 1;
            let magnetSpacing = 1;

            for (let i = 0; i < 5; i++) {
                let x = (i - 2) * magnetSpacing;
                let y = 0;

                fill(128);
                noStroke();
                rect(x - magnetSize / 2, y - magnetSize / 2, magnetSize, magnetSize);

                let orientation = magnetOrientations[i];
                let symbol = '';

                if (orientation === 0) {
                    symbol = '→';
                } else if (orientation === 1) {
                    symbol = '←';
                } else if (orientation === 2) {
                    symbol = '↑';
                } else if (orientation === 3) {
                    symbol = '↓';
                } else if (orientation === 4) {
                    symbol = '⊙';
                } else if (orientation === 5) {
                    symbol = '⊗';
                }

                fill(0);
                textSize(24);
                textAlign(CENTER, CENTER);
                text(symbol, x, y);
            }
        }

        function calculateField(x, y) {
            let field = createVector(0, 0);
            let L = 1;
            let strength = sliderStrength;

            for (let i = 0; i < 5; i++) {
                let r_i = createVector((i - 2) * L, 0, 0);
                let m_i = getMagneticMoment(magnetOrientations[i]);
                m_i.mult(strength);

                let r = createVector(x - r_i.x, y - r_i.y, 0);
                let r_magnitude = r.mag();
                let unit_r = r.normalize();

                let B = (3 * (m_i.dot(unit_r) * unit_r) / r_magnitude ** 5) - m_i / r_magnitude ** 3;
                field.add(B);
            }

            return field;
        }

        function calculateFieldMagnitude(x, y) {
            let field = calculateField(x, y);
            return field.mag();
        }

        function getMagneticMoment(orientation) {
            let moment = createVector(0, 0, 0);

            if (orientation === 0) {
                moment = createVector(1, 0, 0);
            } else if (orientation === 1) {
                moment = createVector(-1, 0, 0);
            } else if (orientation === 2) {
                moment = createVector(0, 1, 0);
            } else if (orientation === 3) {
                moment = createVector(0, -1, 0);
            } else if (orientation === 4) {
                moment = createVector(0, 0, 1);
            } else if (orientation === 5) {
                moment = createVector(0, 0, -1);
            }

            return moment;
        }

        function updateSliderValue() {
            sliderStrength = document.getElementById('slider-strength').value;
            document.getElementById('slider-strength-value').textContent = sliderStrength;
        }

        function updatePlotType() {
            if (document.getElementById('btn-stream-plot').classList.contains('active')) {
                plotType = 'stream';
            } else if (document.getElementById('btn-density-plot').classList.contains('active')) {
                plotType = 'density';
            }
            document.getElementById('btn-stream-plot').classList.toggle('active');
            document.getElementById('btn-density-plot').classList.toggle('active');
        }

        document.getElementById('slider-strength').addEventListener('input', updateSliderValue);
        document.getElementById('slider-strength').addEventListener('change', updateSliderValue);

        document.getElementById('btn-stream-plot').addEventListener('click', updatePlotType);
        document.getElementById('btn-density-plot').addEventListener('click', updatePlotType);

        for (let i = 0; i < 5; i++) {
            document.getElementById(`magnet-orientation-up-${i}`).addEventListener('click', () => {
                magnetOrientations[i] = (magnetOrientations[i] + 1) % 6;
                document.getElementById(`magnet-orientation-display-${i}`).textContent = getOrientationSymbol(magnetOrientations[i]);
            });

            document.getElementById(`magnet-orientation-down-${i}`).addEventListener('click', () => {
                magnetOrientations[i] = (magnetOrientations[i] - 1 + 6) % 6;
                document.getElementById(`magnet-orientation-display-${i}`).textContent = getOrientationSymbol(magnetOrientations[i]);
            });
        }

        function getOrientationSymbol(orientation) {
            let symbol = '';

            if (orientation === 0) {
                symbol = '→';
            } else if (orientation === 1) {
                symbol = '←';
            } else if (orientation === 2) {
                symbol = '↑';
            } else if (orientation === 3) {
                symbol = '↓';
            } else if (orientation === 4) {
                symbol = '⊙';
            } else if (orientation === 5) {
                symbol = '⊗';
            }

            return symbol;
        }

        setup();
        draw();
    </script>
</body>
</html>
