                line(x, y, x, y + 300);
                fill(c);
                text(i, x, y + 320);
            }

            for (let i = 0; i <= 9; i++) {
                let x = 100;
                let y = 100 + (i * 30);
                let c = color(0);
                if (i % 3 === 0) c = color(255, 0, 0); // Red
                if (i % 3 === 1) c = color(0, 255, 0); // Green
                if (i % 3 === 2) c = color(0, 0, 255); // Blue
                stroke(c);
                line(x, y, x - 300, y);
                fill(c);
                text(i, x - 320, y);
            }

            // Draw phase envelope
            if (currentView === 'diagram') {
                stroke(255, 165, 0); // Orange
                fill(255, 165, 0);
                beginShape();
                for (let i = 0; i < phaseEnvelopePoints.length; i++) {
                    let p = phaseEnvelopePoints[i];
                    vertex(p.xA * 300 + 100, p.xB * 300 + 100);
                }
                endShape(CLOSE);

                stroke(255, 0, 255); // Magenta
                fill(255, 0, 255);
                beginShape();
                for (let i = 0; i < phaseEnvelopePoints.length; i++) {
                    let p = phaseEnvelopePoints[i];
                    vertex(p.xC * 300 + 100, p.xB * 300 + 100);
                }
                endShape(CLOSE);
            } else {
                fill(173, 230, 252); // Light Blue
                rect(100, 100, 300, 300);
                fill(255, 229, 180); // Light Red
                rect(100, 100, 300, 300);
            }

            // Draw tie lines
            if (showTieLines) {
                stroke(128);
                for (let i = 0; i < tieLines.length; i++) {
                    let tl = tieLines[i];
                    let alphaPoint = tl.alpha;
                    let betaPoint = tl.beta;
                    stroke(128);
                    line(alphaPoint.xA * 300 + 100, alphaPoint.xB * 300 + 100, betaPoint.xA * 300 + 100, betaPoint.xB * 300 + 100);
                }
            }

            // Draw user point
            let userPointX = userPoint.xA * 300 + 100;
            let userPointY = userPoint.xB * 300 + 100;
            fill(0);
            ellipse(userPointX, userPointY, 10);

            // Draw projection lines
            stroke(0);
            line(userPointX, userPointY, 100, 100);
            line(userPointX, userPointY, 500, 100);
            line(userPointX, userPointY, 300, 500);

            // Draw alpha and beta phase points
            if (currentView === 'diagram') {
                if (showAlphaPhase) {
                    fill(255, 165, 0); // Orange
                    ellipse(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 10);
                    stroke(0);
                    line(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 100, 100);
                    line(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 500, 100);
                    line(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 300, 500);
                }

                if (showBetaPhase) {
                    fill(255, 0, 255); // Magenta
                    ellipse(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 10);
                    stroke(0);
                    line(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 100, 100);
                    line(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 500, 100);
                    line(userPoint.xA * 300 + 100, userPoint.xB * 300 + 100, 300, 500);
                }
            }

            // Draw plait point
            if (currentView === 'phases') {
                fill(0);
                ellipse(300, 300, 10);
                text('Plait Point', 300, 310);
            }
        }

        function mousePressed() {
            let userPointX = mouseX - 100;
            let userPointY = mouseY - 100;
            let trianglePoints = [
                { x: 100, y: 100 },
                { x: 400, y: 100 },
                { x: 300, y: 400 },
            ];
            let barycentricCoordinates = getBarycentricCoordinates(mouseX, mouseY, trianglePoints[0].x, trianglePoints[0].y, trianglePoints[1].x, trianglePoints[1].y, trianglePoints[2].x, trianglePoints[2].y);
            userPoint.xA = barycentricCoordinates.xA;
            userPoint.xB = barycentricCoordinates.xB;
            userPoint.xC = barycentricCoordinates.xC;
            redraw();
        }

        function getBarycentricCoordinates(x, y, v1x, v1y, v2x, v2y, v3x, v3y) {
            let area = (v2y - v1y) * (v3x - v2x) - (v2x - v1x) * (v3y - v2y);
            let a = ((v2y - v1y) * (x - v2x) - (v2x - v1x) * (y - v2y)) / area;
            let b = ((v3y - v2y) * (x - v2x) - (v3x - v2x) * (y - v2y)) / area;
            let c = 1 - a - b;
            return { xA: a, xB: b, xC: c };
        }

        function toggleView() {
            if (currentView === 'diagram') {
                currentView = 'phases';
                showGridLines = false;
                showTieLines = false;
                showAlphaPhase = false;
                showBetaPhase = false;
            } else {
                currentView = 'diagram';
                showGridLines = true;
                showTieLines = true;
                showAlphaPhase = true;
                showBetaPhase = true;
            }
            redraw();
        }

        function toggleGridLines() {
            showGridLines = !showGridLines;
            redraw();
        }

        function toggleTieLines() {
            showTieLines = !showTieLines;
            redraw();
        }

        function toggleAlphaPhase() {
            showAlphaPhase = !showAlphaPhase;
            redraw();
        }

        function toggleBetaPhase() {
            showBetaPhase = !showBetaPhase;
            redraw();
        }

        document.getElementById('btn-diagram').addEventListener('click', toggleView);
        document.getElementById('btn-phases').addEventListener('click', toggleView);
        document.getElementById('check-grid-lines').addEventListener('change', toggleGridLines);
        document.getElementById('check-tie-lines').addEventListener('change', toggleTieLines);
        document.getElementById('check-alpha-phase').addEventListener('change', toggleAlphaPhase);
        document.getElementById('check-beta-phase').addEventListener('change', toggleBetaPhase);

        setup();
        draw();
    </script>
</body>
</html>
