                let x = n & 255;
                let y = (n >> 8) & 255;
                vertex(x * 2 + width / 2, y * 2 + height / 2);
            }
            endShape();
            stroke(255, 0, 0);
            fill(255, 0, 0);
            ellipse(initialization & 255 * 2 + width / 2, (initialization >> 8) & 255 * 2 + height / 2, 5, 5);
            stroke(0, 0, 255);
            fill(0, 0, 255);
            ellipse((initialization + maxSteps) % 65536 & 255 * 2 + width / 2, ((initialization + maxSteps) % 65536 >> 8) & 255 * 2 + height / 2, 5, 5);
            if (cycle1) {
