                    if ((n + BitReverse15(Math.floor(n / 2))) % 65536 == n) {
                        let x = n & 255;
                        let y = (n >> 8) & 255;
                        stroke(0, 255, 255);
                        fill(0, 255, 255);
                        ellipse(x * 2 + width / 2, y * 2 + height / 2, 3, 3);
                    }
                }
            }
            if (cycle2) {
                let cyclePairs = [[10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678], [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355], [43690, 54613]];
                for (let pair of cyclePairs) {
                    let x1 = pair[0] & 255;
                    let y1 = (pair[0] >> 8) & 255;
                    let x2 = pair[1] & 255;
                    let y2 = (pair[1] >> 8) & 255;
