                    line(x1 * 2 + width / 2, y1 * 2 + height / 2, x2 * 2 + width / 2, y2 * 2 + height / 2);
                }
            }
            if (cycle4) {
                let cyclePoints = [43690, 54612, 32768, 49152];
                for (let i = 0; i < 4; i++) {
                    let x = cyclePoints[i] & 255;
                    let y = (cyclePoints[i] >> 8) & 255;
                    stroke(0, 128, 0);
                    line(x * 2 + width / 2, y * 2 + height / 2, cyclePoints[(i + 1) % 4] & 255 * 2 + width / 2, (cyclePoints[(i + 1) % 4] >> 8) & 255 * 2 + height / 2);
                }
                let minX = Math.min(...cyclePoints.map(n => n & 255));
                let maxX = Math.max(...cyclePoints.map(n => n & 255));
                let minY = Math.min(...cyclePoints.map(n => (n >> 8) & 255));
                let maxY = Math.max(...cyclePoints.map(n => (n >> 8) & 255));
                stroke(255, 165, 0);
                noFill();
                rect(minX * 2 + width / 2 - 5, minY * 2 + height / 2 - 5, (maxX - minX + 1) * 2 + 10, (maxY - minY + 1) * 2 + 10);
                for (let i = 0; i < 4; i++) {
                    let x1 = cyclePoints[i] & 255;
                    let y1 = (cyclePoints[i] >> 8) & 255;
                    let x2 = cyclePoints[(i + 1) % 4] & 255;
                    let y2 = (cyclePoints[(i + 1) % 4] >> 8) & 255;
                    let midX = (x1 + x2) / 2;
                    let midY = (y1 + y2) / 2;
                    stroke(0, 128, 0);
                    line(midX * 2 + width / 2, midY * 2 + height / 2, (midX + 1) * 2 + width / 2, (midY + 1) * 2 + height / 2);
                }
            }
        }

        function BitReverse15(n) {
            let result = 0;
            for (let i = 0; i < 15; i++) {
                result = (result << 1) | (n & 1);
                n >>= 1;
            }
            return result;
        }

        function updateInfoText() {
            let infoText = document.getElementById("info-text");
            let n = (initialization + maxSteps) % 65536;
            let x = n & 255;
            let y = (n >> 8) & 255;
            let xNext = (initialization & 255);
            let yNext = (initialization >> 8) & 255;
            let foundCycle = false;
            for (let i = 1; i <= maxSteps; i++) {
                n = (n + BitReverse15(Math.floor(n / 2))) % 65536;
                let x2 = n & 255;
                let y2 = (n >> 8) & 255;
                if (x == x2 && y == y2) {
                    foundCycle = true;
                    break;
                }
            }
            if (foundCycle) {
                infoText.textContent = `result of initial condition ${initialization} repeats in ${maxSteps + 1} steps`;
            } else {
                infoText.textContent = `result of initial condition ${initialization} does not repeat in ${maxSteps + 1} steps`;
            }
        }

        function updateCanvas() {
            redraw();
        }

        document.getElementById("slider-initialization").addEventListener("input", function() {
            initialization = parseInt(this.value);
            document.getElementById("value-initialization").textContent = `+ ${initialization}`;
            updateInfoText();
            updateCanvas();
        });

        document.getElementById("slider-max-steps").addEventListener("input", function() {
            maxSteps = parseInt(this.value);
            document.getElementById("value-max-steps").textContent = `+ ${maxSteps}`;
            updateInfoText();
            updateCanvas();
        });

        document.getElementById("checkbox-cycle4").addEventListener("change", function() {
            cycle4 = this.checked;
            updateCanvas();
        });

        document.getElementById("checkbox-cycle2").addEventListener("change", function() {
            cycle2 = this.checked;
            updateCanvas();
        });

        document.getElementById("checkbox-cycle1").addEventListener("change", function() {
            cycle1 = this.checked;
            updateCanvas();
        });

        document.getElementById("checkbox-all-points").addEventListener("change", function() {
            allPoints = this.checked;
            updateCanvas();
        });

        document.getElementById("checkbox-modify").addEventListener("change", function() {
            modify = this.checked;
            updateCanvas();
        });

        setup();
        draw();
    </script>
</body>
</html>
