                    composition: {
                        benzene: {
                            'Cond': { initial: 0.98, Kp: -0.1, tau: 1.2 },
                            '5':    { initial: 0.8872, Kp: 0.02, tau: 0.5 },
                            'Reb':  { initial: 0.02, Kp: 0.05, tau: 1.8 }
                        },
                        toluene: { /* ... parameters ... */ },
                        pxylene: { /* ... parameters ... */ }
                    },
                    temperature: { /* ... */ },
                    molar_hold_up: { /* ... */ }
                },
                reboil_ratio: {
                    // ... similar nested structure for reboil ratio step
                }
            };

            // Define event listeners for controls
            document.addEventListener('DOMContentLoaded', function() {
                // Initialize plot
                const plotContainer = document.getElementById('plot-container');
                const stepTypeRadio = document.getElementsByName('step-type');
                const stageRadio = document.getElementsByName('stage');
                const plotTypeButton = document.querySelectorAll('.plot-type-btn');
                const componentRadio = document.getElementsByName('component');

                // Set default plot type
                const initialPlotType = 'composition';
                const initialComponent = 'benzene';
                const initialStage = '5';
                const initialStepType = 'reflux_ratio';
                const initialStepPercent = -0.3;

                // Create initial plot data
                const initialPlotData = createPlotData(initialPlotType, initialComponent, initialStage, initialStepType, initialStepPercent);

                // Create initial plot layout
                const initialPlotLayout = {
                    xaxis: { title: 'time in minutes', gridcolor: '#CCCCCC', range: [0, 20] },
                    yaxis: { title: '', gridcolor: '#CCCCCC' },
                    margin: { l: 80, r: 20, t: 20, b: 50 },
                    paper_bgcolor: 'rgba(0,0,0,0)',
                    plot_bgcolor: 'rgba(0,0,0,0)'
                };

                // Initialize plot
                Plotly.newPlot(plotContainer, initialPlotData, initialPlotLayout);

                // Add event listeners for controls
                stepTypeRadio.forEach(radio => radio.addEventListener('change', updatePlot));
                stageRadio.forEach(radio => radio.addEventListener('change', updatePlot));
                plotTypeButton.forEach(button => button.addEventListener('click', updatePlot));
                componentRadio.forEach(radio => radio.addEventListener('change', updatePlot));

                // Update plot on initial state
                updatePlot();
            });

            // Function to update plot on control changes
            function updatePlot() {
                // Get current control values
                const stepTypeValue = document.querySelector('input[name="step-type"]:checked').value;
                const stageValue = document.querySelector('input[name="stage"]:checked').value;
                const plotTypeValue = document.querySelector('.plot-type-btn.active').id;
                const componentValue = document.querySelector('input[name="component"]:checked').value;
                const stepPercentValue = parseFloat(document.getElementById('step-percent').value);

                // Create new plot data
                const newPlotData = createPlotData(plotTypeValue, componentValue, stageValue, stepTypeValue, stepPercentValue);

                // Create new plot layout
                const newPlotLayout = {
                    xaxis: { title: 'time in minutes', gridcolor: '#CCCCCC', range: [0, 20] },
                    yaxis: { title: '', gridcolor: '#CCCCCC' },
                    margin: { l: 80, r: 20, t: 20, b: 50 },
                    paper_bgcolor: 'rgba(0,0,0,0)',
                    plot_bgcolor: 'rgba(0,0,0,0)'
                };

                // Update plot
                Plotly.react('plot-container', newPlotData, newPlotLayout);
            }

            // Function to create plot data
            function createPlotData(plotType, component, stage, stepType, stepPercent) {
                // Get simulation parameters
                const params = simulationParams[stepType][plotType][component][stage];

                // Create time array
                const timeArray = Array.from({ length: 201 }, (_, i) => i * 0.1);

                // Calculate y-values using FOPDT formula
                const yValues = timeArray.map(t => {
                    if (t < 10) return params.initial;
                    return params.initial + params.Kp * stepPercent * (1 - Math.exp(-(t - 10) / params.tau));
                });

                // Create plot data
                const plotData = [
                    {
                        x: timeArray,
                        y: yValues,
                        type: 'scatter',
                        mode: 'lines',
                        line: {
                            color: getLineColor(component)
                        }
                    }
                ];

                return plotData;
            }

            // Function to get line color based on component
            function getLineColor(component) {
                switch (component) {
                    case 'benzene':
                        return '#FF0000';
                    case 'toluene':
                        return '#0000FF';
                    case 'pxylene':
                        return '#008B8B';
                    default:
                        return '#000000';
                }
            }
        </script>
    </div>
</body>
</html>
