    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/renderers/CSS2DRenderer.js"></script>

    <script>
        // Define the celestial sphere
        const sphereGeometry = new THREE.SphereGeometry(1, 60, 60);
        const sphereMaterial = new THREE.MeshBasicMaterial({
            transparent: true,
            opacity: 0.4,
            vertexColors: true,
            side: THREE.DoubleSide
        });
        const sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);

        // Define the celestial equator and ecliptic
        const equatorGeometry = new THREE.BufferGeometry();
        const equatorPoints = new Float32Array(360 * 3);
        for (let i = 0; i < 360; i++) {
            equatorPoints[i * 3] = Math.cos(i * Math.PI / 180) * 1;
            equatorPoints[i * 3 + 1] = 0;
            equatorPoints[i * 3 + 2] = Math.sin(i * Math.PI / 180) * 1;
        }
        equatorGeometry.setAttribute('position', new THREE.BufferAttribute(equatorPoints, 3));
        const equatorMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff });
        const equatorLine = new THREE.LineLoop(equatorGeometry, equatorMaterial);

        const eclipticGeometry = new THREE.BufferGeometry();
        const eclipticPoints = new Float32Array(360 * 3);
        for (let i = 0; i < 360; i++) {
            eclipticPoints[i * 3] = Math.cos(i * Math.PI / 180) * 1 * Math.cos(Math.PI / 180 * 23.5);
            eclipticPoints[i * 3 + 1] = Math.sin(i * Math.PI / 180) * 1 * Math.cos(Math.PI / 180 * 23.5);
            eclipticPoints[i * 3 + 2] = Math.sin(i * Math.PI / 180 * 23.5);
        }
        eclipticGeometry.setAttribute('position', new THREE.BufferAttribute(eclipticPoints, 3));
        const eclipticMaterial = new THREE.LineBasicMaterial({ color: 0xff00ff });
        const eclipticLine = new THREE.LineLoop(eclipticGeometry, eclipticMaterial);

        // Define the brightest stars
        const starsGeometry = new THREE.BufferGeometry();
        const starsPoints = new Float32Array(1000 * 3);
        for (let i = 0; i < 1000; i++) {
            const ra = Math.random() * 2 * Math.PI;
            const dec = Math.random() * Math.PI;
            starsPoints[i * 3] = Math.cos(ra) * Math.cos(dec);
            starsPoints[i * 3 + 1] = Math.sin(ra) * Math.cos(dec);
            starsPoints[i * 3 + 2] = Math.sin(dec);
        }
        starsGeometry.setAttribute('position', new THREE.BufferAttribute(starsPoints, 3));
        const starsMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 0.01 });
        const starsPointsObject = new THREE.Points(starsGeometry, starsMaterial);

        // Define the constellations
        const constellations = {
            'Zodiac': [
                { vertices: [0, 0, 0], color: 0xff0000 },
                { vertices: [1, 0, 0], color: 0xff0000 },
                { vertices: [1, 1, 0], color: 0xff0000 },
                { vertices: [0, 1, 0], color: 0xff0000 },
                { vertices: [-1, 1, 0], color: 0xff0000 },
                { vertices: [-1, 0, 0], color: 0xff0000 },
                { vertices: [-1, -1, 0], color: 0xff0000 },
                { vertices: [0, -1, 0], color: 0xff0000 },
                { vertices: [1, -1, 0], color: 0xff0000 }
            ],
            'Ursa Major Family': [
                { vertices: [0, 0, 0], color: 0x00ff00 },
                { vertices: [1, 0, 0], color: 0x00ff00 },
                { vertices: [1, 1, 0], color: 0x00ff00 },
                { vertices: [0, 1, 0], color: 0x00ff00 },
                { vertices: [-1, 1, 0], color: 0x00ff00 },
                { vertices: [-1, 0, 0], color: 0x00ff00 },
                { vertices: [-1, -1, 0], color: 0x00ff00 },
                { vertices: [0, -1, 0], color: 0x00ff00 },
                { vertices: [1, -1, 0], color: 0x00ff00 }
            ],
            'Perseus Family': [
                { vertices: [0, 0, 0], color: 0x0000ff },
                { vertices: [1, 0, 0], color: 0x0000ff },
                { vertices: [1, 1, 0], color: 0x0000ff },
                { vertices: [0, 1, 0], color: 0x0000ff },
                { vertices: [-1, 1, 0], color: 0x0000ff },
                { vertices: [-1, 0, 0], color: 0x0000ff },
                { vertices: [-1, -1, 0], color: 0x0000ff },
                { vertices: [0, -1, 0], color: 0x0000ff },
                { vertices: [1, -1, 0], color: 0x0000ff }
            ]
        };

        // Define the first point of Aries
        const ariesGeometry = new THREE.SphereGeometry(0.01, 60, 60);
        const ariesMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const ariesMesh = new THREE.Mesh(ariesGeometry, ariesMaterial);
        ariesMesh.position.set(1, 0, 0);

        // Define the zenith
        const zenithGeometry = new THREE.Group();
        const zenithSphereGeometry = new THREE.SphereGeometry(0.01, 60, 60);
        const zenithSphereMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
        const zenithSphereMesh = new THREE.Mesh(zenithSphereGeometry, zenithSphereMaterial);
        zenithSphereMesh.position.set(0, 1, 0);
        zenithGeometry.add(zenithSphereMesh);

        const zenithLineGeometry = new THREE.BufferGeometry();
        const zenithLinePoints = new Float32Array(2 * 3);
        zenithLinePoints[0] = 0;
        zenithLinePoints[1] = 0;
        zenithLinePoints[2] = 0;
        zenithLinePoints[3] = 0;
        zenithLinePoints[4] = 1;
        zenithLinePoints[5] = 0;
        zenithLineGeometry.setAttribute('position', new THREE.BufferAttribute(zenithLinePoints, 3));
        const zenithLineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
        const zenithLineMesh = new THREE.Line(zenithLineGeometry, zenithLineMaterial);
        zenithGeometry.add(zenithLineMesh);
        zenithGeometry.position.set(0, 1, 0);

        // Define the pole labels
        const poleLabels = [];
        const poleLabelGeometry = new THREE.BufferGeometry();
        const poleLabelPoints = new Float32Array(2 * 3);
        poleLabelPoints[0] = 0;
        poleLabelPoints[1] = 1;
        poleLabelPoints[2] = 0;
        poleLabelPoints[3] = 0;
        poleLabelPoints[4] = 1;
        poleLabelPoints[5] = 0;
        poleLabelGeometry.setAttribute('position', new THREE.BufferAttribute(poleLabelPoints, 3));
        const poleLabelMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
        const poleLabelMesh = new THREE.Line(poleLabelGeometry, poleLabelMaterial);
        poleLabelMesh.position.set(0, 1, 0);
        poleLabels.push(poleLabelMesh);

        const poleLabelTextGeometry = new THREE.BufferGeometry();
        const poleLabelTextPoints = new Float32Array(2 * 3);
        poleLabelTextPoints[0] = 0;
        poleLabelTextPoints[1] = 1.1;
        poleLabelTextPoints[2] = 0;
        poleLabelTextPoints[3] = 0;
        poleLabelTextPoints[4] = 1.1;
        poleLabelTextPoints[5] = 0;
        poleLabelTextGeometry.setAttribute('position', new THREE.BufferAttribute(poleLabelTextPoints, 3));
        const poleLabelTextColor = 0x000000;
        const poleLabelTextMaterial = new THREE.CSS2DMaterial({ color: poleLabelTextColor });
        const poleLabelTextMesh = new THREE.CSS2DObject();
        poleLabelTextMesh.position.set(0, 1.1, 0);
        poleLabelTextMesh.text = 'N';
        poleLabels.push(poleLabelTextMesh);

        const poleLabelGeometrySouth = new THREE.BufferGeometry();
        const poleLabelPointsSouth = new Float32Array(2 * 3);
        poleLabelPointsSouth[0] = 0;
        poleLabelPointsSouth[1] = -1;
        poleLabelPointsSouth[2] = 0;
        poleLabelPointsSouth[3] = 0;
        poleLabelPointsSouth[4] = -1;
        poleLabelPointsSouth[5] = 0;
        poleLabelGeometrySouth.setAttribute('position', new THREE.BufferAttribute(poleLabelPointsSouth, 3));
        const poleLabelMeshSouth = new THREE.Line(poleLabelGeometrySouth, poleLabelMaterial);
        poleLabelMeshSouth.position.set(0, -1, 0);
        poleLabels.push(poleLabelMeshSouth);

        const poleLabelTextGeometrySouth = new THREE.BufferGeometry();
        const poleLabelTextPointsSouth = new Float32Array(2 * 3);
        poleLabelTextPointsSouth[0] = 0;
        poleLabelTextPointsSouth[1] = -1.1;
        poleLabelTextPointsSouth[2] = 0;
        poleLabelTextPointsSouth[3] = 0;
        poleLabelTextPointsSouth[4] = -1.1;
