        scene.add(zenithGeometry);
        scene.add(...poleLabels);

        // Define the camera
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);

        // Define the renderer
        const renderer = new THREE.WebGLRenderer({
            canvas: document.querySelector('.three-canvas'),
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setPixelRatio(window.devicePixelRatio);

        // Define the orbit controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Define the animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Start the animation loop
        animate();

        // Add event listeners for the controls
        document.getElementById('select-family').addEventListener('change', (e) => {
            const family = e.target.value;
            const constellations = constellations[family];
            const selectConstellation = document.getElementById('select-constellation');
            selectConstellation.innerHTML = '';
            const noneOption = document.createElement('option');
            noneOption.value = 'None';
            noneOption.text = 'None';
            selectConstellation.add(noneOption);
            if (family === 'all') {
                const options = Object.keys(constellations).map((constellation) => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.text = constellation;
                    return option;
                });
                selectConstellation.add(...options);
                selectConstellation.value = 'None';
            } else {
                const options = constellations.map((constellation) => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.text = constellation;
                    return option;
                });
                selectConstellation.add(...options);
                selectConstellation.value = constellations[0].name;
            }
        });

        document.getElementById('select-constellation').addEventListener('change', (e) => {
            const constellation = e.target.value;
            const selectConstellation = document.getElementById('select-constellation');
            const noneOption = selectConstellation.querySelector('option[value="None"]');
            if (constellation === 'None') {
                noneOption.selected = true;
            } else {
                const options = selectConstellation.querySelectorAll('option');
                for (let i = 0; i < options.length; i++) {
                    if (options[i].value === constellation) {
                        options[i].selected = true;
                        break;
                    }
                }
            }
            const selectFamily = document.getElementById('select-family');
            const family = selectFamily.value;
            const constellations = constellations[family];
            const lines = scene.children.filter((child) => child.type === 'Line');
            lines.forEach((line) => {
                if (line.name === constellation) {
                    line.visible = true;
                } else {
                    line.visible = false;
                }
            });
            const points = scene.children.filter((child) => child.type === 'Points');
            points.forEach((point) => {
                if (point.name === constellation) {
                    point.visible = true;
                } else {
                    point.visible = false;
                }
            });
        });

        document.getElementById('check-sphere').addEventListener('change', (e) => {
            const sphereMesh = scene.children.find((child) => child.type === 'Mesh' && child.name === 'sphere');
            sphereMesh.visible = e.target.checked;
        });

        document.getElementById('check-stars').addEventListener('change', (e) => {
            const starsPointsObject = scene.children.find((child) => child.type === 'Points' && child.name === 'stars');
            starsPointsObject.visible = e.target.checked;
        });

        document.getElementById('check-equator').addEventListener('change', (e) => {
            const equatorLine = scene.children.find((child) => child.type === 'Line' && child.name === 'equator');
            equatorLine.visible = e.target.checked;
        });

        document.getElementById('check-ecliptic').addEventListener('change', (e) => {
            const eclipticLine = scene.children.find((child) => child.type === 'Line' && child.name === 'ecliptic');
            eclipticLine.visible = e.target.checked;
        });

        document.getElementById('check-zenith').addEventListener('change', (e) => {
            const zenithGeometry = scene.children.find((child) => child.type === 'Group' && child.name === 'zenith');
            zenithGeometry.visible = e.target.checked;
        });

        document.getElementById('check-aries').addEventListener('change', (e) => {
            const ariesMesh = scene.children.find((child) => child.type === 'Mesh' && child.name === 'aries');
            ariesMesh.visible = e.target.checked;
        });

        document.getElementById('slider-ra').addEventListener('input', (e) => {
            const zenithGeometry = scene.children.find((child) => child.type === 'Group' && child.name === 'zenith');
            const ra = parseFloat(e.target.value);
            zenithGeometry.position.set(Math.cos(ra * Math.PI / 180) * 1, 1, Math.sin(ra * Math.PI / 180) * 1);
        });

        document.getElementById('slider-dec').addEventListener('input', (e) => {
            const zenithGeometry = scene.children.find((child) => child.type === 'Group' && child.name === 'zenith');
            const dec = parseFloat(e.target.value);
            zenithGeometry.position.set(Math.cos(dec * Math.PI / 180) * 1 * Math.cos(Math.PI / 180 * 23.5), 1, Math.sin(dec * Math.PI / 180) * 1 * Math.cos(Math.PI / 180 * 23.5));
        });

        // Initialize the controls
        controls.target.set(0, 0, 0);
        controls.update();
    </script>
</body>
</html>
