    <!-- Fast Fourier Transform (FFT) function -->
    <script>
        function fft(real, imag) {
            var n = real.length;
            for (var k = 0; k < n; k++) {
                var m = 0;
                var r = 0;
                var i = 0;
                for (var j = 0; j < n; j++) {
                    m = (m + real[j]) * Math.cos(2 * Math.PI * k * j / n) - (imag[j]) * Math.sin(2 * Math.PI * k * j / n);
                    r = (real[j] * Math.cos(2 * Math.PI * k * j / n) + imag[j] * Math.sin(2 * Math.PI * k * j / n));
                    real[j] = r;
                    imag[j] = m;
                }
            }
        }
    </script>

    <!-- Main Script -->
    <script>
        // Initialize control values
        var c1 = 0;
        var c2 = 0;
        var clipping = false;
        var level = 0.7;
        var scale = "linear";

        // Initialize plot data
        var signalPlotData = [];
        var spectrumPlotData = [];

        // Initialize plot layouts
        var signalPlotLayout = {
            title: 'signal',
            xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false },
            yaxis: { title: '', range: [-2, 2], autorange: false },
            margin: { l: 30, r: 20, b: 40, t: 40 }
        };

        var spectrumPlotLayout = {
            title: 'magnitude spectrum (DFT)',
            xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false },
            yaxis: { title: '', autorange: true },
            margin: { l: 30, r: 20, b: 40, t: 40 }
        };

        // Initialize plot figures
        var signalPlot = Plotly.newPlot('plot-signal', signalPlotData, signalPlotLayout);
        var spectrumPlot = Plotly.newPlot('plot-spectrum', spectrumPlotData, spectrumPlotLayout);

        // Update simulation function
        function updateSimulation() {
            // Read control values
            c1 = parseFloat(document.getElementById('slider-c1').value);
            c2 = parseFloat(document.getElementById('slider-c2').value);
            clipping = document.getElementById('checkbox-clipping').checked;
            level = parseFloat(document.getElementById('slider-level').value);
            scale = document.querySelector('button.active').id;

            // Generate time vector
            var N = 2048;
            var fs = 32000;
            var t = [];
            for (var i = 0; i < N; i++) {
                t.push(i / fs);
            }

            // Generate input signal
            var A = 1;
            var f = 1000;
            var x = [];
            for (var i = 0; i < N; i++) {
                x.push(A * Math.sin(2 * Math.PI * f * t[i]));
            }

            // Calculate output signal
            var y = [];
            for (var i = 0; i < N; i++) {
                y.push(x[i] + c1 * x[i] * x[i] + c2 * x[i] * x[i] * x[i]);
            }

            // Apply clipping
            if (clipping) {
                for (var i = 0; i < N; i++) {
                    y[i] = Math.max(-level, Math.min(level, y[i]));
                }
            }

            // Update signal plot
            signalPlotData = [{ x: t, y: y }];
            signalPlot.update({ data: signalPlotData });

            // Perform DFT
            var real = y.slice();
            var imag = new Array(N).fill(0);
            fft(real, imag);
            var mag = [];
            for (var k = 0; k < N; k++) {
                mag.push(Math.sqrt(real[k] * real[k] + imag[k] * imag[k]));
            }

            // Normalize spectrum
            var normalizedMag = [];
            for (var k = 0; k < N; k++) {
                if (k === 0) {
                    normalizedMag.push(mag[k] / N);
                } else {
                    normalizedMag.push(2 * mag[k] / N);
                }
            }

            // Store spectrum data
            spectrumPlotData = [{ x: Array.from(new Array(N / 2), (_, k) => k * fs / N), y: normalizedMag.slice(0, N / 2) }];

            // Update spectrum plot
            updateSpectrumScale();

            // Update value displays
            document.getElementById('c1-value-display').textContent = c1.toFixed(1);
            document.getElementById('c2-value-display').textContent = c2.toFixed(1);
            document.getElementById('level-value-display').textContent = level.toFixed(2);
        }

        // Update spectrum scale function
        function updateSpectrumScale() {
            // Determine active scale
            var xData = spectrumPlotData[0].x;
            var yData = spectrumPlotData[0].y;

            // Create frequency vector
            var freq = [];
            for (var k = 0; k < xData.length; k++) {
                freq.push(xData[k]);
            }

            // Process Y-data
            if (scale === 'linear') {
                // No processing needed for linear scale
                var yDataLinear = yData;
            } else if (scale === 'decibel') {
                // Calculate dB values
                var yDataDb = [];
                for (var k = 0; k < yData.length; k++) {
                    var dB = 20 * Math.log10(yData[k]);
                    if (dB === -Infinity) {
                        dB = -100;
                    }
                    yDataDb.push(dB);
                }
                var yDataLinear = yDataDb;
            }

            // Update spectrum plot
            spectrumPlot.update({ data: [{ x: freq, y: yDataLinear }] });
            var layout = spectrumPlotLayout;
            if (scale === 'linear') {
                layout.yaxis.range = [0, 1.1];
                layout.yaxis.title = 'magnitude';
            } else if (scale === 'decibel') {
                layout.yaxis.range = [-100, 0];
                layout.yaxis.title = 'magnitude (dB)';
            }
            spectrumPlot.update({ layout: layout });
        }

        // Add event listeners
        document.getElementById('slider-c1').addEventListener('input', updateSimulation);
        document.getElementById('slider-c2').addEventListener('input', updateSimulation);
        document.getElementById('checkbox-clipping').addEventListener('change', updateSimulation);
        document.getElementById('slider-level').addEventListener('input', updateSimulation);
        document.getElementById('btn-linear').addEventListener('click', function() {
            document.querySelector('button.active').classList.remove('active');
            this.classList.add('active');
            scale = 'linear';
            updateSpectrumScale();
        });
        document.getElementById('btn-decibel').addEventListener('click', function() {
            document.querySelector('button.active').classList.remove('active');
            this.classList.add('active');
            scale = 'decibel';
            updateSpectrumScale();
        });

        // Initialize simulation
        updateSimulation();
    </script>
</body>
</html>
