                <label for="checkbox-intersections">intersections</label>
                <input type="checkbox" id="checkbox-intersections">
            </div>
            <div>
                <label for="checkbox-beam-top">beam top half</label>
                <input type="checkbox" id="checkbox-beam-top" checked>
            </div>
            <div>
                <label for="checkbox-beam-bottom">beam bottom half</label>
                <input type="checkbox" id="checkbox-beam-bottom" checked>
            </div>
