    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let rayCount = 15;
        let beamWidth = 1.25;
        let incidentAngle = -45;
        let refractionRatio = 2.0;
        let injected = true;
        let refracted = true;
        let reflected = true;
        let ejected = true;
        let normals = false;
        let intersections = false;
        let beamTop = true;
        let beamBottom = true;

        let sliderRayCount;
        let sliderBeamWidth;
        let sliderIncidentAngle;
        let sliderRefractionRatio;

        let checkboxInjected;
        let checkboxRefracted;
        let checkboxReflected;
        let checkboxEjected;
        let checkboxNormals;
        let checkboxIntersections;
        let checkboxBeamTop;
        let checkboxBeamBottom;

        function setup() {
            createCanvas(800, 600);
            angleMode(DEGREES);
            noStroke();
            frameRate(30);

            sliderRayCount = createSlider(1, 50, 15, 1);
            sliderBeamWidth = createSlider(0.1, 2.0, 1.25, 0.01);
            sliderIncidentAngle = createSlider(-90, 90, -45, 1);
            sliderRefractionRatio = createSlider(1.0, 3.0, 2.0, 0.01);

            checkboxInjected = createCheckbox('injected', true);
            checkboxRefracted = createCheckbox('refracted', true);
            checkboxReflected = createCheckbox('reflected', true);
            checkboxEjected = createCheckbox('ejected', true);
            checkboxNormals = createCheckbox('normals', false);
            checkboxIntersections = createCheckbox('intersections', false);
            checkboxBeamTop = createCheckbox('beam top half', true);
            checkboxBeamBottom = createCheckbox('beam bottom half', true);

            sliderRayCount.position(20, 20);
            sliderBeamWidth.position(20, 70);
            sliderIncidentAngle.position(20, 120);
            sliderRefractionRatio.position(20, 170);

            checkboxInjected.position(20, 220);
            checkboxRefracted.position(20, 240);
            checkboxReflected.position(20, 260);
            checkboxEjected.position(20, 280);
            checkboxNormals.position(20, 300);
            checkboxIntersections.position(20, 320);
            checkboxBeamTop.position(20, 340);
            checkboxBeamBottom.position(20, 360);
        }

        function draw() {
            background(220);

            let w = sliderBeamWidth.value();
            let N = sliderRayCount.value();
            let theta = sliderIncidentAngle.value();
            let n_sphere = sliderRefractionRatio.value();

            translate(width / 2, height / 2);
            scale(1, -1);

            stroke(128);
            line(-250, 0, 250, 0);
            line(0, -250, 0, 250);
            for (let i = -2; i <= 2; i++) {
                line(i * 50, -5, i * 50, 5);
                line(-5, i * 50, 5, i * 50);
            }

            fill(0);
            ellipse(0, 0, 200);

            if (injected) {
                stroke(255, 0, 0);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    line(-200, 0, x, y);
                }
            }

            if (refracted) {
                stroke(0, 255, 0);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    let x1 = x;
                    let y1 = y;
                    let x2 = 0;
                    let y2 = 0;
                    let n1 = 1;
                    let n2 = n_sphere;
                    let theta1 = atan2(y, x);
                    let theta2 = acos(n1 / n2 * sin(theta1));
                    x2 = x1 * cos(theta2) - y1 * sin(theta2);
                    y2 = x1 * sin(theta2) + y1 * cos(theta2);
                    line(x1, y1, x2, y2);
                }
            }

            if (reflected) {
                stroke(0, 0, 255);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    let x1 = x;
                    let y1 = y;
                    let x2 = 0;
                    let y2 = 0;
                    let n1 = n_sphere;
                    let n2 = 1;
                    let theta1 = atan2(y, x);
                    let theta2 = acos(n1 / n2 * sin(theta1));
                    x2 = x1 * cos(theta2) - y1 * sin(theta2);
                    y2 = x1 * sin(theta2) + y1 * cos(theta2);
                    line(x1, y1, x2, y2);
                }
            }

            if (ejected) {
                stroke(0);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    let x1 = x;
                    let y1 = y;
                    let x2 = 0;
                    let y2 = 0;
                    let n1 = n_sphere;
                    let n2 = 1;
                    let theta1 = atan2(y, x);
                    let theta2 = acos(n1 / n2 * sin(theta1));
                    x2 = x1 * cos(theta2) - y1 * sin(theta2);
                    y2 = x1 * sin(theta2) + y1 * cos(theta2);
                    line(x1, y1, x2, y2);
                }
            }

            if (normals) {
                stroke(128);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    let x1 = x;
                    let y1 = y;
                    let x2 = 0;
                    let y2 = 0;
                    let n1 = 1;
                    let n2 = n_sphere;
                    let theta1 = atan2(y, x);
                    let theta2 = acos(n1 / n2 * sin(theta1));
                    x2 = x1 * cos(theta2) - y1 * sin(theta2);
                    y2 = x1 * sin(theta2) + y1 * cos(theta2);
                    line(x1, y1, x2, y2);
                    line(0, 0, x1, y1);
                }
            }

            if (intersections) {
                fill(255, 0, 0);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    let x1 = x;
                    let y1 = y;
                    let x2 = 0;
                    let y2 = 0;
                    let n1 = 1;
                    let n2 = n_sphere;
                    let theta1 = atan2(y, x);
                    let theta2 = acos(n1 / n2 * sin(theta1));
                    x2 = x1 * cos(theta2) - y1 * sin(theta2);
                    y2 = x1 * sin(theta2) + y1 * cos(theta2);
                    ellipse(x1, y1, 5);
                }
            }

            if (beamTop) {
                stroke(255, 0, 0);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = w * cos(a + theta);
                    let y = w * sin(a + theta);
                    line(-200, 0, x, y);
                }
            }

            if (beamBottom) {
                stroke(255, 0, 0);
                for (let i = 0; i < N; i++) {
                    let a = (i - N / 2) * 360 / N;
                    let x = -w * cos(a + theta);
                    let y = w * sin(a + theta);
                    line(-200, 0, x, y);
                }
            }
        }

        function mousePressed() {
            rayCount = sliderRayCount.value();
            beamWidth = sliderBeamWidth.value();
            incidentAngle = sliderIncidentAngle.value();
            refractionRatio = sliderRefractionRatio.value();
            injected = checkboxInjected.checked();
            refracted = checkboxRefracted.checked();
            reflected = checkboxReflected.checked();
            ejected = checkboxEjected.checked();
            normals = checkboxNormals.checked();
            intersections = checkboxIntersections.checked();
            beamTop = checkboxBeamTop.checked();
