<body>
    <div id="controls-container">
        <div>
            <span>size of bipartite graph</span>
            <div id="size-controls">
                <button id="size-6">6</button>
                <button id="size-10">10</button>
                <button id="size-14">14</button>
                <button id="size-18">18</button>
                <button id="size-22">22</button>
                <button id="size-26">26</button>
                <button id="size-30">30</button>
            </div>
        </div>
        <div>
            <span>maximum degree of lower part</span>
            <div id="degree-controls">
                <button id="degree-2">2</button>
                <button id="degree-3">3</button>
                <button id="degree-4">4</button>
                <button id="degree-5">5</button>
                <button id="degree-6">6</button>
            </div>
        </div>
        <div>
            <span>algorithm step</span>
            <div id="step-controls"></div>
        </div>
        <button id="reset-button" title="Generate new graph">(+)</button>
    </div>
    <div id="visualization-container">
        <p id="status-text"></p>
        <div id="canvas-wrapper">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let size = 18;
        let degree = 3;
        let currentStep = 1;
        let history = [];

        function setup() {
            createCanvas(800, 600);
            background(220);
            generateGraph();
        }

        function draw() {
            background(220);
            drawGraph();
        }

        function generateGraph() {
            let graph = [];
            for (let i = 0; i < size; i++) {
                graph.push([]);
                for (let j = 0; j < size; j++) {
                    if (i !== j && Math.floor(Math.random() * 2) === 1) {
                        graph[i].push(j);
                    }
                }
            }

            let matching = [];
            let cover = [];
            let bfsTree = {};
            let highlightedPath = [];

            for (let i = 0; i < size; i++) {
                if (graph[i].length > 0) {
                    let queue = [i];
                    let visited = new Set();
                    while (queue.length > 0) {
                        let vertex = queue.shift();
                        if (!visited.has(vertex)) {
                            visited.add(vertex);
                            bfsTree[vertex] = [];
                            for (let neighbor of graph[vertex]) {
                                bfsTree[vertex].push(neighbor);
                                if (!visited.has(neighbor)) {
                                    queue.push(neighbor);
                                }
                            }
                        }
                    }

                    let path = findAugmentingPath(graph, bfsTree, i);
                    if (path.length > 0) {
                        for (let i = 0; i < path.length - 1; i++) {
                            matching.push([path[i], path[i + 1]]);
                        }
                        cover.push(path[0]);
                    }
                }
            }

            history.push({ matching, cover, bfsTree, highlightedPath, statusText: `Edges: ${matching.length}, Vertices: ${cover.length}` });
            currentStep = history.length;
            updateStepControls();
        }

        function findAugmentingPath(graph, bfsTree, startVertex) {
            let queue = [[startVertex]];
            let visited = new Set();
            while (queue.length > 0) {
                let path = queue.shift();
                let vertex = path[path.length - 1];
                if (!visited.has(vertex)) {
                    visited.add(vertex);
                    for (let neighbor of bfsTree[vertex]) {
                        let newPath = [...path, neighbor];
                        if (graph[neighbor].includes(vertex)) {
                            return newPath;
                        }
                        queue.push(newPath);
                    }
                }
            }
            return [];
        }

        function updateStepControls() {
            let stepControls = document.getElementById('step-controls');
            stepControls.innerHTML = '';
            for (let i = 1; i <= history.length; i++) {
                let button = document.createElement('button');
                button.id = `step-${i}`;
                button.textContent = i;
                button.onclick = () => {
                    currentStep = i;
                    drawGraph();
                };
                stepControls.appendChild(button);
            }
        }

        function drawGraph() {
            let matching = history[currentStep - 1].matching;
            let cover = history[currentStep - 1].cover;
            let bfsTree = history[currentStep - 1].bfsTree;
            let statusText = history[currentStep - 1].statusText;

            background(220);
            drawBipartiteGraph(matching, cover, bfsTree);
            drawAugmentingPathTree(bfsTree);
            fill(0);
            textSize(24);
            text(statusText, 10, 30);
        }

        function drawBipartiteGraph(matching, cover, bfsTree) {
            let topRow = [];
            let bottomRow = [];
            let vertices = [];

            for (let i = 0; i < size; i++) {
                if (i < size / 2) {
                    topRow.push(i);
                } else {
                    bottomRow.push(i);
                }
            }

            for (let i = 0; i < topRow.length; i++) {
                let x = 50 + i * 50;
                let y = 50;
                vertices.push({ x, y, label: topRow[i] + 1 });
            }

            for (let i = 0; i < bottomRow.length; i++) {
                let x = 50 + i * 50;
                let y = 300;
                vertices.push({ x, y, label: bottomRow[i] + 1 });
            }

            for (let vertex of vertices) {
                fill(0);
                ellipse(vertex.x, vertex.y, 20, 20);
                fill(255);
                textSize(16);
                text(vertex.label, vertex.x - 10, vertex.y + 5);
            }

            for (let edge of matching) {
                stroke(128);
                line(vertices[edge[0]].x, vertices[edge[0]].y, vertices[edge[1]].x, vertices[edge[1]].y);
                stroke(0);
                strokeWeight(4);
                line(vertices[edge[0]].x, vertices[edge[0]].y, vertices[edge[1]].x, vertices[edge[1]].y);
            }

            for (let vertex of cover) {
                fill(0, 128, 0);
                ellipse(vertices[vertex].x, vertices[vertex].y, 20, 20);
            }

            for (let vertex in bfsTree) {
                fill(255, 0, 0);
                ellipse(vertices[vertex].x, vertices[vertex].y, 20, 20);
            }
        }

        function drawAugmentingPathTree(bfsTree) {
            let nodes = [];
            let edges = [];

            for (let vertex in bfsTree) {
                nodes.push({ x: 10, y: 10 + nodes.length * 20 });
            }

            for (let vertex in bfsTree) {
                for (let neighbor of bfsTree[vertex]) {
                    edges.push({ x1: nodes[vertex].x + 20, y1: nodes[vertex].y, x2: nodes[neighbor].x + 20, y2: nodes[neighbor].y });
                }
            }

            for (let node of nodes) {
                fill(255, 165, 0);
                rect(node.x, node.y, 20, 20);
                fill(0);
                textSize(16);
                text(nodes.indexOf(node) + 1, node.x + 10, node.y + 20);
            }

            for (let edge of edges) {
                stroke(255, 0, 0);
                line(edge.x1, edge.y1, edge.x2, edge.y2);
            }
        }

        generateGraph();
        updateStepControls();
    </script>
</body>
</html>
