            const bubble = x.map((n) => n * n);
            const selection = x.map((n) => 0.25 * n * n);
            const insertion = x.map((n) => n * factorial(n));
            const merge = x.map((n) => n * Math.log2(n));
            const quick = x.map((n) => 1.2 * n * Math.log2(n));
            const heap = x.map((n) => 1.5 * n * Math.log2(n));
            const bogo = x.map((n) => n * factorial(n));

            const data = [
                {
                    x,
                    y: bubble,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'bubble',
                    visible: document.getElementById('check-bubble').checked,
                    line: { color: 'green', width: 5 }
                },
                {
