        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let lastMouseX, lastMouseY;

        function draw() {
            background(255);
            for (let i = sceneObjects.length - 1; i >= 0; i--) {
                let obj = sceneObjects[i];
                push();
                translate(obj.x, obj.y);
                rotate(obj.rotation);
                drawObject(obj);
                pop();
            }
            if (selectedTool) {
                let x = mouseX;
                let y = mouseY;
                push();
                translate(x, y);
                drawGhost();
                pop();
            }
        }

        function drawObject(obj) {
            if (obj.type === 'atom' || obj.type === 'charge') {
                textSize(16);
                textAlign(CENTER, CENTER);
                text(obj.value, 0, 0);
            } else if (obj.type === 'structure') {
                if (obj.value === 'water') {
                    drawWater(obj);
                } else if (obj.value === 'cyclopropyl_ethane') {
                    drawCyclopropylEthane(obj);
                } else if (obj.value === 'borazine') {
                    drawBorazine(obj);
                } else if (obj.value === 'isopropylamine') {
                    drawIsopropylamine(obj);
                }
            }
        }

        function drawGhost() {
            fill(255, 0, 0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text(selectedTool.value, 0, 0);
        }

        function drawWater(obj) {
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('O', 0, 0);
            fill(255);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('H', -20, -20);
            text('H', 20, -20);
            line(-20, -20, 0, 0);
            line(20, -20, 0, 0);
        }

        function drawCyclopropylEthane(obj) {
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('C', -20, 20);
            text('C', 20, 20);
            text('C', 0, -20);
            text('C', 0, -40);
            line(-20, 20, 0, 0);
            line(20, 20, 0, 0);
            line(0, -20, 0, -40);
            line(-20, -20, 0, -40);
            line(20, -20, 0, -40);
        }

        function drawBorazine(obj) {
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('B', -20, 20);
            text('B', 20, 20);
            text('B', 0, -20);
            text('B', 0, -40);
            text('N', -20, 0);
            text('N', 20, 0);
            line(-20, 20, 0, 0);
            line(20, 20, 0, 0);
            line(0, -20, 0, -40);
            line(-20, 0, 0, -20);
            line(20, 0, 0, -20);
        }

        function drawIsopropylamine(obj) {
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('C', -20, 20);
            text('C', 20, 20);
            text('C', 0, -20);
            text('C', 0, -40);
            text('N', 0, -60);
            text('H', -20, -80);
            text('H', 20, -80);
            text('H', 0, -100);
            line(-20, 20, 0, 0);
            line(20, 20, 0, 0);
            line(0, -20, 0, -40);
            line(0, -20, 0, -60);
            line(0, -40, 0, -60);
            line(-20, -80, 0, -100);
            line(20, -80, 0, -100);
        }

        function setup() {
            createCanvas(800, 600);
            frameRate(30);
        }

        function mousePressed() {
            if (selectedTool) {
                let x = mouseX;
                let y = mouseY;
                sceneObjects.push({
                    type: selectedTool.type,
                    value: selectedTool.value,
                    x: x,
                    y: y,
                    rotation: 0
                });
                selectedTool = null;
            }
        }

        function mouseDragged() {
            if (activeObject) {
                if (interactionMode === 'translate') {
                    activeObject.x = mouseX - lastMouseX + activeObject.x;
                    activeObject.y = mouseY - lastMouseY + activeObject.y;
                } else if (interactionMode === 'rotate') {
                    let angle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
                    activeObject.rotation = angle;
                }
                lastMouseX = mouseX;
                lastMouseY = mouseY;
                redraw();
            }
        }

        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        function selectTool(tool) {
            selectedTool = tool;
            for (let i = 0; i < document.getElementById('toolbar').children.length; i++) {
                let btn = document.getElementById('toolbar').children[i];
                if (btn.id === tool.id) {
                    btn.classList.add('selected');
                } else {
                    btn.classList.remove('selected');
                }
            }
        }

        function addStructure() {
            let selectedValue = document.getElementById('select-structure').value;
            sceneObjects.push({
                type: 'structure',
                value: selectedValue,
                x: width / 2,
                y: height / 2,
                rotation: 0
            });
            document.getElementById('select-structure').value = 'bond';
        }

        function undo() {
            sceneObjects.pop();
            redraw();
        }

        setup();
        draw();
    </script>
</body>
</html>
