            border: 1px solid #ddd;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <div>
            <label for="slider-amplitude">Amplitude a</label>
            <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
            <span id="value-amplitude">1.50</span>
        </div>
        <div>
            <label for="slider-frequency">Frequency f</label>
            <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
            <span id="value-frequency">0.30</span>
        </div>
        <div>
            <label for="slider-wavelength">Wavelength λ</label>
            <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
            <span id="value-wavelength">4.50</span>
        </div>
        <div>
            <label for="slider-time">Time t</label>
            <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
            <span id="value-time">2.50</span>
        </div>
        <div>
            <label for="select-phase">Initial phase ϕ₀</label>
            <select id="select-phase">
                <option value="-${Math.PI}">-π</option>
                <option value="-${(2*Math.PI)/3}">-2π/3</option>
                <option value="-${Math.PI/2}">-π/2</option>
                <option value="-${Math.PI/3}">-π/3</option>
                <option value="0">0</option>
                <option value="${Math.PI/3}">π/3</option>
                <option value="${Math.PI/2}">π/2</option>
                <option value="${(2*Math.PI)/3}">2π/3</option>
                <option value="${Math.PI}">π</option>
            </select>
        </div>
    </div>
    <div id="formula-display"></div>
    <div id="canvas-container">
        <canvas id="canvas" width="800" height="600"></canvas>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/dist/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3.2.2/es5/tex-mml-chtml.js"></script>
    <script>
        let amplitude = 1.5;
        let frequency = 0.3;
        let wavelength = 4.5;
        let time = 2.5;
        let phase = -Math.PI/2;

        function updateControls() {
            document.getElementById('value-amplitude').textContent = parseFloat(amplitude).toFixed(2);
            document.getElementById('value-frequency').textContent = parseFloat(frequency).toFixed(2);
            document.getElementById('value-wavelength').textContent = parseFloat(wavelength).toFixed(2);
            document.getElementById('value-time').textContent = parseFloat(time).toFixed(2);
        }

        function updateFormulaDisplay() {
            let formula = '';
            let k = 2 * Math.PI / wavelength;
            let omega = 2 * Math.PI * frequency;

            if (phase === -Math.PI/2) {
                formula = 'A sin(kx - ωt)';
            } else if (phase === -Math.PI) {
                formula = '-A cos(kx - ωt)';
            } else if (phase === Math.PI/2) {
                formula = 'A sin(kx - ωt)';
            } else if (phase === Math.PI) {
                formula = '-A cos(kx - ωt)';
            } else if (phase === 0) {
                formula = 'A cos(kx - ωt)';
            } else {
                formula = 'A cos(ωt - kx + ϕ₀)';
            }

            formula = formula.replace('A', parseFloat(amplitude).toFixed(2));
            formula = formula.replace('k', parseFloat(k).toFixed(2));
            formula = formula.replace('ω', parseFloat(omega).toFixed(2));
            formula = formula.replace('ϕ₀', parseFloat(phase).toFixed(2));

            document.getElementById('formula-display').innerHTML = '$$' + formula + '$$';
            MathJax.typeset();
        }

        function draw() {
            background(255);

            stroke(0);
            strokeWeight(1);
            line(0, height/2, width, height/2);
            line(width/2, 0, width/2, height);

            stroke(128);
            strokeWeight(1);
            for (let i = 1; i <= 16; i++) {
                line(i * 50, 0, i * 50, height);
                fill(0);
                text(i, i * 50, 20);
            }

            stroke(0, 0, 255);
            strokeWeight(2);
            beginShape();
            for (let x = 0; x < width; x++) {
                let k = 2 * Math.PI / wavelength;
                let omega = 2 * Math.PI * frequency;
                let y = amplitude * cos(omega * time - k * x + phase);
                vertex(x, height/2 - y * 30);
            }
            endShape();

            fill(0);
            stroke(0);
            strokeWeight(1);
            for (let x = 1; x <= 16; x++) {
                let k = 2 * Math.PI / wavelength;
                let omega = 2 * Math.PI * frequency;
                let y = amplitude * cos(omega * time - k * x * 50 + phase);
                let v = -amplitude * omega * sin(omega * time - k * x * 50 + phase);
                let a = -amplitude * omega**2 * cos(omega * time - k * x * 50 + phase);

                fill(255, 0, 0);
                drawArrow(x * 50, height/2 - y * 30, v * 15, 'red', '\\(\\vec{v}\\)');

                fill(0);
                drawArrow(x * 50, height/2 - y * 30, a * 5, 'black', '\\(\\vec{a}\\)');

                fill(0);
                ellipse(x * 50, height/2 - y * 30, 5, 5);
            }
        }

        function drawArrow(baseX, baseY, vectorY, color, label) {
            push();
            translate(baseX, baseY);
            rotate(atan2(vectorY, 0));
            stroke(color);
            strokeWeight(2);
            line(0, 0, 0, -vectorY * 15);
            strokeWeight(1);
            fill(color);
            text(label, 0, -vectorY * 20);
            pop();
        }

        function setup() {
            createCanvas(800, 600);
            background(255);
            frameRate(30);
        }

        function mouseDragged() {
            draw();
        }

        function keyPressed() {
            if (key === ' ') {
                draw();
            }
        }

        function windowResized() {
            resizeCanvas(width, height);
            draw();
        }

        function drawWave() {
            draw();
        }

        function updateValues() {
            amplitude = parseFloat(document.getElementById('slider-amplitude').value);
            frequency = parseFloat(document.getElementById('slider-frequency').value);
            wavelength = parseFloat(document.getElementById('slider-wavelength').value);
            time = parseFloat(document.getElementById('slider-time').value);
            phase = parseFloat(document.getElementById('select-phase').value);
            updateControls();
            updateFormulaDisplay();
            drawWave();
        }

        document.getElementById('slider-amplitude').addEventListener('input', updateValues);
        document.getElementById('slider-frequency').addEventListener('input', updateValues);
        document.getElementById('slider-wavelength').addEventListener('input', updateValues);
        document.getElementById('slider-time').addEventListener('input', updateValues);
        document.getElementById('select-phase').addEventListener('change', updateValues);

        drawWave();
    </script>
</body>
</html>
