            margin-top: 0;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="control-panel">
            <label for="slider-eccentricity">Eccentricity ε</label>
            <input type="range" id="slider-eccentricity" min="0" max="1.5" step="0.01" value="0">
            <span id="value-eccentricity">0</span>
        </div>
        <div class="visualization-area">
            <h3>Kepler Orbits</h3>
            <div id="canvas-container"></div>
            <div id="energy-display">
                <span id="energy-value">\\(-\\frac{1}{4}\\)</span>
            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3.2.0/es5/mj-core.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3.2.0/es5/mj-svg2svg.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3.2.0/es5/mj-init.js"></script>

    <script>
        let ε = 0;
        let energy = -0.25 * (1 - ε²);
        let p = 2;

        function setup() {
            createCanvas(700, 500);
            background(220);
            ellipseMode(CENTER);
            rectMode(CORNER);
            stroke(0);
            fill(0);
        }

        function draw() {
            background(220);
            translate(width * 0.7, height / 2);
            scale(30);

            // Draw axes
            strokeWeight(1);
            line(-15, 0, 5, 0);
            line(0, -7, 0, 7);
            strokeWeight(0.5);
            for (let x = -14; x <= 5; x += 1) {
                line(x, -0.1, x, 0.1);
            }
            for (let y = -6; y <= 6; y += 1) {
                line(-0.1, y, 0.1, y);
            }

            // Draw central body
            fill(0);
            ellipse(0, 0, 6);

            // Draw orbit path
            stroke(0, 128, 181, 255);
            strokeWeight(2);
            noFill();

            if (ε == 0) {
                // Special case for ε = 0 (Circle)
                for (let θ = 0; θ <= 2 * PI; θ += 0.01) {
                    let r = 2;
                    let x = 2 * cos(θ) - 2;
                    let y = 2 * sin(θ);
                    point(x, y);
                }
            } else {
                // 0 < ε < 1 (Ellipse)
                for (let θ = 0; θ <= 2 * PI; θ += 0.01) {
                    let r = p / (1 + ε * cos(θ));
                    let x = r * cos(θ);
                    let y = r * sin(θ);
                    point(x, y);
                }
            }

            // Update energy display
            energy = -0.25 * (1 - ε²);
            if (ε == 0) {
                MathJax.typeset();
                document.getElementById("energy-value").innerHTML = "\\(-\\frac{1}{4}\\)";
            } else {
                document.getElementById("energy-value").innerHTML = energy.toFixed(2);
            }
        }

        function update() {
            ε = document.getElementById("slider-eccentricity").value;
            document.getElementById("value-eccentricity").innerHTML = ε.toFixed(1);
            draw();
        }

        document.getElementById("slider-eccentricity").addEventListener("input", update);
    </script>
</body>
</html>
