            yaxis: { title: 'methyl mercury index (MI)', range: [0, 2.0] },
            plot_bgcolor: 'white',
            grid_color: '#ccc',
            showlegend: false,
            hovermode: 'x',
            responsive: true,
            displayModeBar: false,
        });

        Plotly.newPlot('plot2', [], {
            title: 'MI at river, estuarine and marine POC',
            xaxis: { title: 'SRB (dSrB)', range: [0, 100] },
            yaxis: { title: 'methyl mercury index (MI)', range: [0, 1.0] },
            plot_bgcolor: 'white',
            grid_color: '#ccc',
            showlegend: true,
            hovermode: 'x',
            responsive: true,
            displayModeBar: false,
        });

        Plotly.newPlot('plot3', [], {
            title: 'methyl mercury availability',
            xaxis: { title: '(µM/L) HgT', range: [0, 2.0] },
            yaxis: { title: '[MeHg] (µM/L)', range: [0, 0.20] },
            plot_bgcolor: 'white',
            grid_color: '#ccc',
            showlegend: false,
            hovermode: 'x',
            responsive: true,
            displayModeBar: false,
        });

        // Update function
        function update() {
            // Get slider values
            var poc = document.getElementById('slider-poc').value;
            var eh = document.getElementById('slider-eh').value;
            var hg_m = document.getElementById('slider-hg_m').value;
            var k = document.getElementById('slider-k').value;
            var a = document.getElementById('slider-a').value;

            // Update plot 1
            var plot1_data = [
                {
                    x: Array(101).fill(0).map((_, i) => i),
                    y: Array(101).fill(0).map((_, i) => 125 * (-eh + 200) * poc * hg_m / i),
                    mode: 'lines',
                    line: { color: 'orange', dash: 'dash' },
                },
            ];
