            border: 1px solid #ccc;
            border-radius: 10px;
        }

        .plot-container {
            display: flex;
            gap: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="algorithm-selector">
                <button id="btn-unstructured" class="algorithm-button active">Unstructured</button>
                <button id="btn-adiabatic" class="algorithm-button">Adiabatic</button>
                <button id="btn-discrete-adiabatic" class="algorithm-button">Discrete Adiabatic</button>
                <button id="btn-heuristic" class="algorithm-button">Heuristic</button>
            </div>
            <div>
                <label for="slider-total-steps">Number of steps to complete the search:</label>
                <input type="range" id="slider-total-steps" min="1" max="200" step="1" value="10">
                <span id="display-total-steps">10</span>
            </div>
            <div>
                <label for="slider-current-step">Current step (0 to 10):</label>
                <input type="range" id="slider-current-step" min="0" max="10" step="1" value="0">
                <span id="display-current-step">0</span>
            </div>
            <div id="probability-plot-mode">
                <button id="btn-conflicts" class="probability-plot-button active">Conflicts</button>
                <button id="btn-eigenvalues" class="probability-plot-button">Eigenvalues</button>
            </div>
        </div>
        <div id="visualization-area">
            <div class="plot-container">
                <div id="plot-amplitudes">
                    <h4>Amplitudes</h4>
                    <div id="plot-amplitudes-container"></div>
                </div>
                <div id="plot-probability">
                    <h4>Probability</h4>
                    <div id="plot-probability-container"></div>
                </div>
            </div>
        </div>
    </div>
    <script src="https://cdn.plotly.com/plotly-2.14.0.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjs@9.5.0/dist/math.min.js"></script>
    <script>
        // Initialize the application with the default settings
        const defaultAlgorithm = 'heuristic';
        const defaultTotalSteps = 10;
        const defaultCurrentStep = 0;
        const defaultPlotMode = 'conflicts';

        // Initialize the controls
        document.getElementById('slider-total-steps').value = defaultTotalSteps;
        document.getElementById('slider-current-step').value = defaultCurrentStep;
        document.getElementById('display-total-steps').textContent = defaultTotalSteps;
        document.getElementById('display-current-step').textContent = defaultCurrentStep;

        // Initialize the plot mode
        document.getElementById('btn-' + defaultPlotMode).classList.add('active');

        // Function to update the plots
        function update() {
            // Get the current state of all controls
            const algorithm = document.querySelector('.algorithm-button.active').id;
            const totalSteps = parseInt(document.getElementById('slider-total-steps').value);
            const currentStep = parseInt(document.getElementById('slider-current-step').value);
            const plotMode = document.querySelector('.probability-plot-button.active').id;

            // Calculate the quantum state vector
            const N = 64;
            const solutions = [Math.floor(Math.random() * N), Math.floor(Math.random() * N)];
            const stateVector = math.zeros(N);
            for (let i = 0; i < N; i++) {
                if (solutions.includes(i)) {
                    stateVector.set(math.complex(1 / math.sqrt(N)), i);
                } else {
                    const conflictNumber = getConflictNumber(i, solutions);
                    stateVector.set(math.complex(1 / math.sqrt(N) * math.exp(-1 * conflictNumber)), i);
                }
            }

            // Apply the algorithm
            let stateVectorFinal;
            switch (algorithm) {
                case 'unstructured':
                    stateVectorFinal = groverOperator(stateVector, currentStep);
                    break;
                case 'adiabatic':
                    stateVectorFinal = adiabaticAlgorithm(stateVector, totalSteps, currentStep);
                    break;
                case 'discrete-adiabatic':
                    stateVectorFinal = discreteAdiabaticAlgorithm(stateVector, totalSteps, currentStep);
                    break;
                case 'heuristic':
                    stateVectorFinal = heuristicAlgorithm(stateVector, totalSteps, currentStep);
                    break;
            }

            // Generate data for both plots
            const amplitudesData = [];
            for (let i = 0; i < N; i++) {
                amplitudesData.push([math.re(stateVectorFinal.get(i)), math.im(stateVectorFinal.get(i))]);
            }
            const probabilitiesData = [];
            if (plotMode === 'conflicts') {
                for (let i = 0; i < N; i++) {
                    const conflictNumber = getConflictNumber(i, solutions);
                    probabilitiesData.push([conflictNumber, math.abs(stateVectorFinal.get(i)) ** 2]);
                }
            } else if (plotMode === 'eigenvalues') {
                switch (algorithm) {
                    case 'adiabatic':
                    case 'discrete-adiabatic':
                    case 'heuristic':
                        for (let i = 0; i < N; i++) {
                            const conflictNumber = getConflictNumber(i, solutions);
                            probabilitiesData.push([conflictNumber, math.abs(stateVectorFinal.get(i)) ** 2]);
                        }
                        break;
                    case 'unstructured':
                        const eigenvalues = getEigenvalues(stateVectorFinal);
                        for (let i = 0; i < eigenvalues.length; i++) {
                            probabilitiesData.push([i, math.abs(eigenvalues[i]) ** 2]);
                        }
                        break;
                }
            }

            // Update the plots
            Plotly.newPlot('plot-amplitudes-container', [{
                x: amplitudesData.map(x => x[0]),
                y: amplitudesData.map(x => x[1]),
                mode: 'markers',
                marker: {
                    color: amplitudesData.map((x, i) => getMarkerColor(i, solutions, algorithm)),
                    size: 10,
                    symbol: 'circle'
                }
            }], {
                title: 'Amplitudes',
                xaxis: {
                    title: 'Re'
                },
                yaxis: {
                    title: 'Im'
                }
            });

            Plotly.newPlot('plot-probability-container', [{
                x: probabilitiesData.map(x => x[0]),
                y: probabilitiesData.map(x => x[1]),
                type: 'bar',
                marker: {
                    color: probabilitiesData.map((x, i) => getMarkerColor(i, solutions, algorithm))
                }
            }], {
                title: 'Probability',
                xaxis: {
                    title: plotMode === 'conflicts' ? 'Number of conflicts' : 'Eigenvalue'
                },
                yaxis: {
                    title: 'Probability'
                }
            });
        }

        // Function to get the conflict number
        function getConflictNumber(i, solutions) {
            let minDistance = Infinity;
            for (const solution of solutions) {
                const distance = hammingDistance(i, solution);
                if (distance < minDistance) {
                    minDistance = distance;
                }
            }
            return minDistance;
        }

        // Function to get the eigenvalues
        function getEigenvalues(stateVector) {
            const N = 64;
            const H = math.zeros(N);
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i === j) {
                        H.set(-1, i, j);
                    } else if (solutions.includes(i) && solutions.includes(j)) {
                        H.set(-1, i, j);
                    } else {
                        H.set(1, i, j);
                    }
                }
            }
            const eigenvalues = math.eigs(H, 2);
            return eigenvalues.map(x => x[0]);
        }

        // Function to apply the Grover operator
        function groverOperator(stateVector, steps) {
            const N = 64;
            const G = math.zeros(N);
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i === j) {
                        G.set(-1, i, j);
                    } else if (solutions.includes(i) && solutions.includes(j)) {
                        G.set(-1, i, j);
                    } else {
                        G.set(1, i, j);
                    }
                }
            }
            const P0 = math.zeros(N);
            for (let i = 0; i < N; i++) {
                P0.set(1 / N, i, i);
            }
            const Uw = math.multiply(G, P0);
            const Us = 2 * P0 - math.eye(N);
            const stateVectorFinal = math.multiply(math.pow(math.expm(math.multiply(Us, steps)), -1), math.multiply(math.pow(math.expm(math.multiply(Uw, steps)), 1), stateVector));
            return stateVectorFinal;
        }

        // Function to apply the adiabatic algorithm
        function adiabaticAlgorithm(stateVector, totalSteps, currentStep) {
            const N = 64;
            const H0 = math.zeros(N);
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i === j) {
                        H0.set(-1, i, j);
                    } else if (solutions.includes(i) && solutions.includes(j)) {
                        H0.set(-1, i, j);
                    } else {
                        H0.set(1, i, j);
                    }
                }
            }
            const H = math.zeros(N);
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i === j) {
                        H.set(-1 + (currentStep / totalSteps), i, j);
                    } else if (solutions.includes(i) && solutions.includes(j)) {
                        H.set(-1 + (currentStep / totalSteps), i, j);
                    } else {
                        H.set(1 + (currentStep / totalSteps), i, j);
                    }
                }
            }
            const stateVectorFinal = math.eigs(H, 1);
            return stateVectorFinal[0];
        }

        // Function to apply the discrete adiabatic algorithm
        function discreteAdiabaticAlgorithm(stateVector, totalSteps, currentStep) {
            const N = 64;
            const H = math.zeros(N);
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i === j) {
                        H.set(-1, i, j);
                    } else if (solutions.includes(i) && solutions.includes(j)) {
                        H.set(-1, i, j);
                    } else {
                        H.set(1, i, j);
                    }
                }
            }
            const stateVectorFinal = math.multiply(math.expm(-1 * math.multiply(H, currentStep / totalSteps)), stateVector);
            return stateVectorFinal;
        }

        // Function to apply the heuristic algorithm
        function heuristicAlgorithm(stateVector, totalSteps, currentStep) {
            const N = 64;
            const H = math.zeros(N);
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i === j) {
                        H.set(-1, i, j);
                    } else if (solutions.includes(i) && solutions.includes(j)) {
                        H.set(-1, i, j);
                    } else {
                        H.set(1, i, j);
                    }
                }
            }
            const stateVectorFinal = math.multiply(math.expm(-1 * math.multiply(H, currentStep / totalSteps)), stateVector);
            return stateVectorFinal;
        }

        // Function to get the marker color
        function getMarkerColor(i, solutions, algorithm) {
            if (solutions.includes(i)) {
                return 'black';
            } else if (algorithm === 'unstructured' && currentStep === 0) {
                return 'red';
            } else {
                const conflictNumber = getConflictNumber(i, solutions);
                return conflictColor(conflictNumber);
            }
        }

        // Function to get the conflict color
        function conflictColor(conflictNumber) {
            const maxConflictNumber = Math.max(...solutions.map(i => getConflictNumber(i, solutions)));
            const colorScale = ['blue', 'orange'];
            const colorIndex = Math.floor((conflictNumber / maxConflictNumber) * (colorScale.length - 1));
            return colorScale[colorIndex];
        }

        // Function to get the Hamming distance
        function hammingDistance(i, j) {
            let distance = 0;
            for (let k = 0; k < 6; k++) {
                if ((i >> k) & 1 !== (j >> k) & 1) {
                    distance++;
                }
            }
            return distance;
        }

        // Add event listeners to the controls
        document.getElementById('btn-unstructured').addEventListener('click', () => {
            document.querySelector('.algorithm-button.active').classList.remove('active');
            document.getElementById('btn-unstructured').classList.add('active');
            document.getElementById('slider-total-steps').value = 5;
            document.getElementById('slider-current-step').max = 5;
            document.getElementById('display-total-steps').textContent = 5;
            document.getElementById('display-current-step').textContent = 0;
            update();
        });

        document.getElementById('btn-adiabatic').addEventListener('click', () => {
            document.querySelector('.algorithm-button.active').classList.remove('active');
            document.getElementById('btn-adiabatic').classList.add('active');
            document.getElementById('slider-total-steps').value = 200;
            document.getElementById('slider-current-step').max = 200;
            document.getElementById('display-total-steps').textContent = 200;
            document.getElementById('display-current-step').textContent = 0;
            update();
        });

        document.getElementById('btn-discrete-adiabatic').addEventListener('click', () => {
            document.querySelector('.algorithm-button.active').classList.remove('active');
            document.getElementById('btn-discrete-adiabatic').classList.add('active');
            document.getElementById('slider-total-steps').value = 200;
            document.getElementById('slider-current-step').max = 200;
            document.getElementById('display-total-steps').textContent = 200;
            document.getElementById('display-current-step').textContent = 0;
            update();
        });

        document.getElementById('btn-heuristic').addEventListener('click', () => {
            document.querySelector('.algorithm-button.active').classList.remove('active');
            document.getElementById('btn-heuristic').classList.add('active');
            document.getElementById('slider-total-steps').value = 10;
            document.getElementById('slider-current-step').max = 10;
            document.getElementById('display-total-steps').textContent = 10;
            document.getElementById('display-current-step').textContent = 0;
            update();
        });

        document.getElementById('slider-total-steps').addEventListener('input', () => {
            document.getElementById('display-total-steps').textContent = document.getElementById('slider-total-steps').value;
            document.getElementById('slider-current-step').max = document.getElementById('slider-total-steps').value;
            update();
        });

        document.getElementById('slider-current-step').addEventListener('input', () => {
            document.getElementById('display-current-step').textContent = document.getElementById('slider-current-step').value;
            update();
        });

        document.getElementById('btn-conflicts').addEventListener('click', () => {
            document.querySelector('.probability-plot-button.active').classList.remove('active');
            document.getElementById('btn-conflicts').classList.add('active');
            update();
        });

        document.getElementById('btn-eigenvalues').addEventListener('click', () => {
            document.querySelector('.probability-plot-button.active').classList.remove('active');
            document.getElementById('btn-eigenvalues').classList.add('active');
